    "contracts/token/hpl/HPLBase.sol": {
      "content": "pragma solidity ^0.8.0;

import "./__HPL_ERC20Burnable.sol";
import "../../interfaces/ITokenHook.sol";
import "../../lib/BlackholePrevention.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/proxy/utils/Initializable.sol";
import './UsingLiquidityProtectionService.sol';

contract HPLBase is
    Ownable,
    Initializable,
    __HPL_ERC20Burnable,
    BlackholePrevention,
    UsingLiquidityProtectionService(0xaB5b38273a4a5Efd5b1cF9FaE1Cb4804B5Bd7c4B)
{
    ITokenHook public tokenHook;
    address public stakingRewardTreasury;
    mapping(address => bool) public pancakePairs;

    event SetTokenHook(address caller, address tokenHook);
    event SetPancakePair(address caller, address pancakePair);
    event SetStaking(address caller, address stakingAddress);

    constructor() __HPL_ERC20("HappyLand.Finance", "HPL") {}

    function initialize(
        address _tokenReceiver,
        address _stakingRewardTreasury,
        address _tokenHook
    ) external initializer {
        //supply 400M
        _mint(_tokenReceiver, 400 * 1000000 * 10**decimals());
        stakingRewardTreasury = _stakingRewardTreasury;
        tokenHook = ITokenHook(_tokenHook);
    }

    function token_transfer(address _from, address _to, uint _amount) internal override {
        _transfer(_from, _to, _amount); // Expose low-level token transfer function.
    }
    function token_balanceOf(address _holder) internal view override returns(uint) {
        return balanceOf(_holder); // Expose balance check function.
    }
    function protectionAdminCheck() internal view override onlyOwner {} // Must revert to deny access.
    function uniswapVariety() internal pure override returns(bytes32) {
        return PANCAKESWAP; // UNISWAP / PANCAKESWAP / QUICKSWAP / SUSHISWAP.
    }
    function uniswapVersion() internal pure override returns(UniswapVersion) {
        return UniswapVersion.V2; // V2 or V3.
    }
    function uniswapFactory() internal pure override returns(address) {
        return 0xcA143Ce32Fe78f1f7019d7d551a6402fC5350c73; // PancakeFactory
    }
    function _beforeTokenTransfer(address _from, address _to, uint _amount) internal override {
        super._beforeTokenTransfer(_from, _to, _amount);
        LiquidityProtection_beforeTokenTransfer(_from, _to, _amount);
    }
    // All the following overrides are optional, if you want to modify default behavior.

    // How the protection gets disabled.
    function protectionChecker() internal view override returns(bool) {
        return ProtectionSwitch_timestamp(1643327999); // Switch off protection on Thursday, January 27, 2022 11:59:59 PM GMT.
        // return ProtectionSwitch_block(13000000); // Switch off protection on block 13000000.
        // return ProtectionSwitch_manual(); // Switch off protection by calling disableProtection(); from owner. Default.
    }

    // This token will be pooled in pair with:
    function counterToken() internal pure override returns(address) {
        return 0xe9e7CEA3DedcA5984780Bafc599bD69ADd087D56; // BUSD
    }




    function setTokenHook(address _addr) external onlyOwner {
        tokenHook = ITokenHook(_addr);
        emit SetTokenHook(msg.sender, _addr);
    }

    function setStakingRewardTreasury(address _stakingRewardTreasury)
        external
        onlyOwner
    {
        require(stakingRewardTreasury != address(0), "null address");
        stakingRewardTreasury = _stakingRewardTreasury;
        emit SetStaking(msg.sender, _stakingRewardTreasury);
    }

    function setPancakePairs(address[] calldata _pancakePairs, bool val)
        external
        onlyOwner
    {
        for (uint256 i = 0; i < _pancakePairs.length; i++) {
            require(_pancakePairs[i] != address(0), "null address");
            pancakePairs[_pancakePairs[i]] = val;
            emit SetStaking(msg.sender, _pancakePairs[i]);
        }
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal virtual override {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");

        _beforeTokenTransfer(sender, recipient, amount);

        uint256 senderBalance = _balances[sender];
        require(
            senderBalance >= amount,
            "ERC20: transfer amount exceeds balance"
        );

        if (!pancakePairs[sender] && address(tokenHook) != address(0)) {
            tokenHook.addLiquidity();
        }

        unchecked {
            _balances[sender] = senderBalance - amount;
        }

        if (
            address(tokenHook) != address(0) &&
            sender != address(tokenHook) &&
            recipient != address(tokenHook)
        ) {
            (
                uint256 stakeFee,
                uint256 liquidityFee,
                uint256 burnFee
            ) = tokenHook.getTransferFees(sender, recipient, amount);
            uint256 burnAmount = (amount * burnFee) / 10000;
            uint256 stakingRewardTreasuryAmount = (amount * stakeFee) / 10000;
            uint256 liquidityHolderAmount = (amount * liquidityFee) / 10000;
            //burn
            _totalSupply -= burnAmount;
            //treasury
            _balances[stakingRewardTreasury] += stakingRewardTreasuryAmount;
            //liquidityFee
            _balances[address(tokenHook)] += liquidityHolderAmount;

            _balances[recipient] +=
                amount -
                burnAmount -
                stakingRewardTreasuryAmount -
                liquidityHolderAmount;

            emit Transfer(sender, address(0), burnAmount);
            emit Transfer(
                sender,
                stakingRewardTreasury,
                stakingRewardTreasuryAmount
            );
            emit Transfer(sender, address(tokenHook), liquidityHolderAmount);
            emit Transfer(
                sender,
                recipient,
                amount -
                    burnAmount -
                    stakingRewardTreasuryAmount -
                    liquidityHolderAmount
            );
        } else {
            _balances[recipient] += amount;
            emit Transfer(sender, recipient, amount);
        }
        _afterTokenTransfer(sender, recipient, amount);
    }

    //rescue loss token
    function withdrawEther(address payable receiver, uint256 amount)
        external
        virtual
        onlyOwner
    {
        _withdrawEther(receiver, amount);
    }

    function withdrawERC20(
        address payable receiver,
        address tokenAddress,
        uint256 amount
    ) external virtual onlyOwner {
        _withdrawERC20(receiver, tokenAddress, amount);
    }

    function withdrawERC721(
        address payable receiver,
        address tokenAddress,
        uint256 tokenId
    ) external virtual onlyOwner {
        _withdrawERC721(receiver, tokenAddress, tokenId);
    }
}
"
