pragma solidity 0.8.4;





interface ISwapFactory {
    function getPair(address token0,address token1) external view returns(address);
}

contract WIFI is Context,IERC20Metadata {
    using Address for address;
    using SafeMath for uint256;

    mapping(address => uint256) private _balances;
    mapping(address => mapping(address => uint256)) private _allowances;

    string public constant override name = "WIFI";
    string public constant override symbol = "WIFI";
    uint8 public constant override decimals = 18;
    uint256 public constant override totalSupply = 5000000 * 1e18;

    mapping (address => uint) public nonces;

    uint public constant burnRate = 10;

    address public immutable liquidityOperator;

    // mainnet
    address public constant usdt = address(0x55d398326f99059fF775485246999027B3197955);
    address public constant wbnb = address(0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c);
    ISwapFactory public constant pancakeFactory = ISwapFactory(address(0xcA143Ce32Fe78f1f7019d7d551a6402fC5350c73));

    constructor(
        address _initHolder,
        address _liquidityOperator
    ){
        require(_initHolder != address(0),"init holder can not be address zero");
        
        liquidityOperator = _liquidityOperator;

        _balances[_initHolder] = totalSupply;
        emit Transfer(address(0), _initHolder, totalSupply);
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _balances[account];
    }

    function transfer(address recipient, uint256 amount) public override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function allowance(address owner, address spender) public view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) public override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _transfer(sender, recipient, amount);

        uint256 currentAllowance = _allowances[sender][_msgSender()];
        require(currentAllowance >= amount, "ERC20: transfer amount exceeds allowance");
        unchecked {
            _approve(sender, _msgSender(), currentAllowance - amount);
        }

        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue) public returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender] + addedValue);
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) public returns (bool) {
        uint256 currentAllowance = _allowances[_msgSender()][spender];
        require(currentAllowance >= subtractedValue, "ERC20: decreased allowance below zero");
        unchecked {
            _approve(_msgSender(), spender, currentAllowance - subtractedValue);
        }

        return true;
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(_balances[sender] >= amount, "ERC20: transfer amount exceeds balance");

        uint recipientAmount = amount;
        if(recipient != address(0) && sender != liquidityOperator && recipient != liquidityOperator && (isSwapPair(recipient))){
            uint burnAmount = amount.mul(burnRate) / 100;
            recipientAmount = recipientAmount.sub(burnAmount);
            _transferFee(sender, address(0), burnAmount);
        }
        
        _balances[sender] = _balances[sender].sub(amount);
        _balances[recipient] = _balances[recipient].add(recipientAmount);
        emit Transfer(sender, recipient, recipientAmount);
    }

    function _transferFee(address _from,address _to,uint _fee) internal {
        if(_fee > 0){
            _balances[_to] = _balances[_to].add(_fee);
            emit Transfer(_from, _to, _fee);
        }
    }

    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) internal {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function isSwapPair(address pair) public view returns(bool){
        if(pair == address(0)){
            return false;
        }

        return pancakeFactory.getPair(address(this), usdt) == pair || pancakeFactory.getPair(address(this), wbnb) == pair;
    }
}
