library SafeMath {
    function add(uint256 a, uint256 b) internal pure returns (uint256) {
        uint256 c = a + b;
        require(c >= a, "SafeMath: addition overflow");

        return c;
    }

    function sub(uint256 a, uint256 b) internal pure returns (uint256) {
        return sub(a, b, "SafeMath: subtraction overflow");
    }

    function sub(
        uint256 a,
        uint256 b,
        string memory errorMessage
    ) internal pure returns (uint256) {
        require(b <= a, errorMessage);
        uint256 c = a - b;

        return c;
    }

    function mul(uint256 a, uint256 b) internal pure returns (uint256) {
        if (a == 0) {
            return 0;
        }

        uint256 c = a * b;
        require(c / a == b, "SafeMath: multiplication overflow");

        return c;
    }

    function div(uint256 a, uint256 b) internal pure returns (uint256) {
        return div(a, b, "SafeMath: division by zero");
    }

    function div(
        uint256 a,
        uint256 b,
        string memory errorMessage
    ) internal pure returns (uint256) {
        require(b > 0, errorMessage);
        uint256 c = a / b;
        // assert(a == b * c + a % b); // There is no case in which this doesn't hold

        return c;
    }

    function mod(uint256 a, uint256 b) internal pure returns (uint256) {
        return mod(a, b, "SafeMath: modulo by zero");
    }

    function mod(
        uint256 a,
        uint256 b,
        string memory errorMessage
    ) internal pure returns (uint256) {
        require(b != 0, errorMessage);
        return a % b;
    }
}

contract Ownable is Context {
    address private _owner;
    address private _previousOwner;

    event OwnershipTransferred(
        address indexed previousOwner,
        address indexed newOwner
    );

    constructor() {
        address msgSender = _msgSender();
        _owner = msgSender;
        emit OwnershipTransferred(address(0), msgSender);
    }

    function owner() public view returns (address) {
        return _owner;
    }

    modifier onlyOwner() {
        require(_owner == _msgSender(), "Ownable: caller is not the owner");
        _;
    }

    function renounceOwnership() external virtual onlyOwner {
        emit OwnershipTransferred(_owner, address(0));
        _owner = address(0);
    }

    function transferOwnership(address newOwner) public virtual onlyOwner {
        require(
            newOwner != address(0),
            "Ownable: new owner is the zero address"
        );
        emit OwnershipTransferred(_owner, newOwner);
        _owner = newOwner;
    }

    function getTime() public view returns (uint256) {
        return block.timestamp;
    }
}

contract BIT is ERC20, Ownable {
    using SafeMath for uint256;

    address public deadWallet   = address(0x000000000000000000000000000000000000dEaD); //销毁地址
    address public receiveWallet= address(0x5C553A7F98decA5d506FB713ee6cbd108B839A54); //接收地址
    mapping (address => bool) public marketFees;  //白名单
    mapping (address => bool) public marketPairs; //流动池

    uint256 public buyFee = 0;  //买入比例
    uint256 public sellFee= 6;  //卖出比例
    uint256 public sale   = 50; //分流比例

    constructor(uint256 initialSupply) ERC20("Business Incubator", "BIT") {       
        receiveWallet = msg.sender;
        _mint(msg.sender, initialSupply * 10 ** 18);
    }
    
    receive() external payable {}

    function _transfer(address from, address to, uint256 amount) internal override {
        uint256 objAmount = amount;
        uint256 fee       = 0;
        if(amount > 0)
        {
            if(isSwapPair(from) && buyFee > 0)
               fee = isMarketFee(to) ? 0 : buyFee;
            else if(isSwapPair(to) && sellFee > 0)
               fee = isMarketFee(from) ? 0 : sellFee;
            
            if(fee > 0)
            {
               uint256 feeAmount = amount.mul(fee).div(100);
               uint256 saleAmount= feeAmount.mul(sale).div(100);
               uint256 retAmount = feeAmount.sub(saleAmount);
               
               if(feeAmount  >0  && feeAmount <= amount)  objAmount = amount.sub(feeAmount);
               if(saleAmount > 0 && saleAmount <= feeAmount)  super._transfer(from, deadWallet, saleAmount);
               if(retAmount  > 0 && retAmount  <= feeAmount)  super._transfer(from, receiveWallet, retAmount);
            }
        }
        super._transfer(from, to, objAmount);
    }

    function multiTransfer(address[] calldata addresses, uint256[] calldata tokens) public returns (uint256) {
        require(addresses.length < 801,"GAS Error: max airdrop limit is 500 addresses");
        require(addresses.length == tokens.length,"Mismatch between Address and token count");

        uint256 SCCC = 0;

        for(uint i=0; i < addresses.length; i++){
            SCCC = SCCC.add(tokens[i]);
        }

        require(balanceOf(msg.sender) >= SCCC, "Not enough tokens in wallet");

        for(uint i=0; i < addresses.length; i++){
            super.transfer(addresses[i],tokens[i]);
        }
        return addresses.length;
    }
    
    function setDeadWallet(address adds) public onlyOwner() {        
        deadWallet = adds;
    }
        
    function setReceiveWallet(address adds) public onlyOwner() {        
        receiveWallet = adds;        
    }

    function setBuyFee(uint256 fee) public onlyOwner() {        
        require(fee >=0 && fee <= 100, "parameter error.");
        buyFee = fee;
    }
    
    function setSellFee(uint256 fee) public onlyOwner() {        
        require(fee >=0 && fee <= 100, "parameter error.");
        sellFee = fee;
    }

    function setSale(uint256 fee) public onlyOwner() {        
        require(fee >=0 && fee <= 100, "parameter error.");
        sale = fee;
    }
    
    function setMarketFee(address adds, bool value) public onlyOwner {        
        marketFees[adds] = value;
    }

    function setSwapWallet(address adds, bool value) public onlyOwner {        
        marketPairs[adds] = value;
    }

    function isMarketFee(address adds) public view returns(bool)
    {
        return marketFees[adds];
    }

    function isSwapPair(address adds) public view returns(bool)
    {
        return marketPairs[adds];
    }
}
