interface IReferralPool {
    function bindReferrer(address _invitee, address _referrer) external;
    function getReferrer(address account) external view returns (address);
}

interface IdividendPool {
    function dividendsToLp(uint256 rewardsFee) external;
}

contract FAMToken is Context, IERC20, Ownable {

    using Address for address;

    mapping (address => uint256) private _balances;
    mapping (address => mapping (address => uint256)) private _allowances;

    mapping (address => bool) private _isExcluded;
    mapping (address => uint256) private _uid;
    mapping (address => bool) private _operator;
    address[] private _excluded;
   
    uint256 public constant initSupply = 10000000 * 10 ** 18;
    uint256 private _totalSupply;

    uint256 constant private _dFee = 3; 
    uint256 constant private _aAmt = 97; 

    string private _name = 'FAM Token';
    string private _symbol = 'FAM';
    uint8 private _decimals = 18;

    address public dividendPool;
    address public referralPool;

    constructor () {
        _totalSupply = initSupply;
        _balances[_msgSender()] = initSupply;
        emit Transfer(address(0), _msgSender(), initSupply);
        _operator[_msgSender()] = true;
        excludeAccount(_msgSender());
    }

    modifier onlyOperator {
        require(_operator[_msgSender()]==true, "OperatorRole: caller does not have the Operator role");
        _;
    }

    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public view returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _balances[account];
    }

    function transfer(address recipient, uint256 amount) public override returns (bool) {
        address sender = _msgSender();
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");
        require(amount > 0, "Transfer amount must be greater than zero");

        if (dividendPool == address(0) || referralPool == address(0) || _isExcluded[sender] || _isExcluded[recipient]) {
            _transfer(sender, recipient, amount);
        } else {
            _transferDeduct(sender, recipient, amount);
        }
  
        /**
         * Binding recommendation relationship
         *
         * Requirements: 
         * non-contract address 
         * address with no binding relationship 
         */
        if (
            referralPool != address(0) &&
            !sender.isContract() &&
            !recipient.isContract() &&
            IReferralPool(referralPool).getReferrer(recipient) == address(0)
        )
        {
            IReferralPool(referralPool).bindReferrer(recipient, sender); 
        }
        return true;
    }

    function allowance(address owner, address spender) public view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) public override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) public override returns (bool) {
        if (dividendPool == address(0) || referralPool == address(0) || _isExcluded[sender] || _isExcluded[recipient]) {
            _transfer(sender, recipient, amount);
        } else {
            _transferDeduct(sender, recipient, amount);
        }
        _approve(sender, _msgSender(), _allowances[sender][_msgSender()] - amount);
        return true;
    }

    function burn(uint256 amount) public {
        _burn(_msgSender(), amount);
    }

    function increaseAllowance(address spender, uint256 addedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender] + addedValue);
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender] - subtractedValue);
        return true;
    }

    function _approve(address owner, address spender, uint256 amount) private {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _burn(address burnFrom, uint256 amount) private {
        require(burnFrom != address(0), "ERC20: burn from the zero address");
        _balances[burnFrom] -= amount;
        _balances[address(0)] += amount;
        emit Transfer(burnFrom, address(0), amount);
    }

    function _transferDeduct(address sender, address recipient, uint256 amount) private {

        uint256 tAmt = amount * _aAmt / 100;
        uint256 dAmt = amount * _dFee / 100;

        _transfer(sender, recipient, tAmt);         // Actual received amount
        _transfer(sender, dividendPool, dAmt);       // LP bonus

        IdividendPool(dividendPool).dividendsToLp(dAmt);
    }

    function _transfer(address sender, address recipient, uint256 amount) private {

        _balances[sender] -= amount;
        _balances[recipient] += amount;

        emit Transfer(sender, recipient, amount);
    }

    function isExcluded(address account) public view returns (bool) {
        return _isExcluded[account];
    }

    function excludeAccount(address account) public onlyOperator {
        require(!_isExcluded[account], "Account is already excluded");
        _uid[account] = _excluded.length;
        _isExcluded[account] = true;
        _excluded.push(account);
    }

    function batchExcludeAccount(address[] memory accounts) public onlyOperator {
        for (uint256 i = 0; i < accounts.length; i++) {
            excludeAccount(accounts[i]);
        }
    }

    function includeAccount(address account) public onlyOperator {
        require(_isExcluded[account], "Account is already included");
        _excluded[_uid[account]] = _excluded[_excluded.length - 1];
        _isExcluded[account] = false;
        _excluded.pop();
        delete _uid[account];
    }
   
    function batchIncludeAccount(address[] memory accounts) public onlyOperator {
        for (uint256 i = 0; i < accounts.length; i++) {
            includeAccount(accounts[i]);
        }
    }

    function setLpBonusPool(address _dividendPool) public onlyOperator {
        // require(_dividendPool == address(0), "Lp prize pool is set");
        dividendPool = _dividendPool;
    }

    function setReferralBonusPool(address _referralBonusPool) public onlyOperator {
        // require(_referralBonusPool == address(0), "Referral prize pool is set");
        referralPool = _referralBonusPool;
    }

    function addOperator(address account) public onlyOwner{
        _operator[account]=true;
    }

    function removeOperator(address account) public onlyOwner{
        require(account!=owner(),'');
        delete _operator[account];
    }

}
