    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) private {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) private {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        require(!_isBlacklisted[from] && !_isBlacklisted[to], "Blacklisted address");
        require(amount > 0, "Transfer amount must be greater than zero");
        if (from != owner() && to != owner()) {
            require(
                amount <= _maxTxAmount,
                "Transfer amount exceeds the maxTxAmount."
            );
        }

       if (_isAirdroppedAddress[from]) {
            if (_airdropUnlockTime[from] > block.timestamp) {
                require(balanceOf(from).sub(_airdropAmount[from]) >= amount, "This balance sent from Airdrop");
            }   
        }

        uint256 contractTokenBalance = balanceOf(address(this));
        bool overMinimumTokenBalance = contractTokenBalance >=
            minimumTokensBeforeSwap;

        if (
            to == uniswapV2Pair &&
            _isAutoBuyBack &&
            balanceOf(uniswapV2Pair) > 0
        ) {
            SellHistories memory sellHistory;
            sellHistory.time = block.timestamp;
            sellHistory.bnbAmount = _getSellBnBAmount(amount);

            _sellHistories.push(sellHistory);
        }

        // Sell tokens for ETH
        if (
            !inSwapAndLiquify &&
            swapAndLiquifyEnabled &&
            balanceOf(uniswapV2Pair) > 0
        ) {
            if (to == uniswapV2Pair) {
                if (
                    overMinimumTokenBalance &&
                    _startTimeForSwap + _intervalMinutesForSwap <=
                    block.timestamp
                ) {
                    _startTimeForSwap = block.timestamp;
                    contractTokenBalance = minimumTokensBeforeSwap;
                    uint256 half = contractTokenBalance.div(2);
                    uint256 otherHalf = contractTokenBalance.sub(half);
                    swapAndLiquify(half);
                    swapTokens(otherHalf);
                }

                if (buyBackEnabled) {
                    uint256 balance = address(this).balance;

                    uint256 _bBSLimitMax = buyBackSellLimit;

                    if (_isAutoBuyBack) {
                        uint256 sumBnbAmount = 0;
                        uint256 startTime = block.timestamp -
                            _buyBackTimeInterval;
                        uint256 cnt = 0;

                        for (uint256 i = 0; i < _sellHistories.length; i++) {
                            if (_sellHistories[i].time >= startTime) {
                                sumBnbAmount = sumBnbAmount.add(
                                    _sellHistories[i].bnbAmount
                                );
                                cnt = cnt + 1;
                            }
                        }

                        if (cnt > 0 && _buyBackDivisor > 0) {
                            _bBSLimitMax = sumBnbAmount.div(cnt).div(
                                _buyBackDivisor
                            );
                        }

                        _removeOldSellHistories();
                    }

                    uint256 _bBSLimitMin = _bBSLimitMax
                        .mul(_buyBackRangeRate)
                        .div(100);

                    uint256 _bBSLimit = _bBSLimitMin +
                        (uint256(
                            keccak256(
                                abi.encodePacked(
                                    block.timestamp,
                                    block.difficulty
                                )
                            )
                        ) % (_bBSLimitMax - _bBSLimitMin + 1));

                    if (balance > _bBSLimit) {
                        buyBackTokens(_bBSLimit);
                    }
                }
            }
        }

        bool takeFee = true;

        // If any account belongs to _isExcludedFromFee account then remove the fee
        if (_isExcludedFromFee[from] || _isExcludedFromFee[to]) {
            takeFee = false;
        } else {
            if (!_preSaleFeesEnded && _preSaleEndTime > 0) {
                if (
                    _preSaleEndTime + _intervalMinutesForEndPreSaleRate <=
                    block.timestamp
                ) {
                    _preSaleFeesEnded = true;
                    removeAllFee();
                    _taxFee = 2;
                    _liquidityFee = 10;
                } else {
                    if (to != uniswapV2Pair && from != uniswapV2Pair) {
                        removeAllFee();
                    }

                    return _tokenTransfer(from, to, amount, takeFee);
                }
            }

            // Buy
            if (from == uniswapV2Pair) {
                removeAllFee();
                _taxFee = _buyTaxFee;
                _liquidityFee = _buyLiquidityFee;
            }
            // Sell
            if (to == uniswapV2Pair) {
                removeAllFee();
                _taxFee = _sellTaxFee;
                _liquidityFee = _sellLiquidityFee;
            }

            if (to != uniswapV2Pair && from != uniswapV2Pair) {
                removeAllFee();
            }
        }

        _tokenTransfer(from, to, amount, takeFee);
    }

    function swapTokens(uint256 contractTokenBalance) private lockTheSwap {
        uint256 initialBalance = address(this).balance;
        swapTokensForEth(contractTokenBalance);
        uint256 transferredBalance = address(this).balance.sub(initialBalance);
        if (transferredBalance == 0) return;

        // Send to Marketing address
        transferToAddressETH(
            marketingAddress,
            transferredBalance.mul(marketingDivisor).div(100)
        );
    }

    function buyBackTokens(uint256 amount) private lockTheSwap {
        if (amount > 0) {
            swapETHForTokens(amount);
        }
    }

    function swapTokensForEth(uint256 tokenAmount) private {
        // Generate the uniswap pair path of token -> WETH
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // Make the swap
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // Accept any amount of ETH
            path,
            address(this), // The contract
            block.timestamp
        );

        emit SwapTokensForETH(tokenAmount, path);
    }

    function swapETHForTokens(uint256 amount) private {
        // Generate the uniswap pair path of token -> WETH
        address[] memory path = new address[](2);
        path[0] = uniswapV2Router.WETH();
        path[1] = address(this);

        // Make the swap
        uniswapV2Router.swapExactETHForTokensSupportingFeeOnTransferTokens{
            value: amount
        }(
            0, // Accept any amount of Tokens
            path,
            deadAddress, // Burn address
            block.timestamp.add(300)
        );

        emit SwapETHForTokens(amount, path);
    }

    function _tokenTransfer(
        address sender,
        address recipient,
        uint256 amount,
        bool takeFee
    ) private {
        if (!takeFee) removeAllFee();

        if (_isExcluded[sender] && !_isExcluded[recipient]) {
            _transferFromExcluded(sender, recipient, amount);
        } else if (!_isExcluded[sender] && _isExcluded[recipient]) {
            _transferToExcluded(sender, recipient, amount);
        } else if (_isExcluded[sender] && _isExcluded[recipient]) {
            _transferBothExcluded(sender, recipient, amount);
        } else {
            _transferStandard(sender, recipient, amount);
        }

        if (!takeFee) restoreAllFee();
    }

