pragma solidity ^0.8.0;

interface IPancakeSwapFactory {
    function getPair(address tokenA, address tokenB)
        external
        view
        returns (address pair);

    function createPair(address tokenA, address tokenB)
        external
        returns (address pair);
}

interface IPancakeSwapPair {
    function getReserves()
        external
        view
        returns (
            uint112 reserve0,
            uint112 reserve1,
            uint32 blockTimestampLast
        );

    function token0() external view returns (address);

    function balanceOf(address account) external view returns (uint256);

    function totalSupply() external view returns (uint256);
}

contract RTCToken is Ownable, ERC20("RTC", "RTC") {
    /////////////////////////////// Total supply  //////////////////////////////
    uint256 public constant TOTALSUPPLY = 70_000;

    /////////////////////////////// DEX and TOKEN //////////////////////////////
    address public constant USDT = 0x55d398326f99059fF775485246999027B3197955; // BSCMainnet
    address public constant FACTORY = 0xcA143Ce32Fe78f1f7019d7d551a6402fC5350c73; // BSCMainnet

    ////////////////////////////// Token Distribution //////////////////////////
    address public constant COMMUNITYREWARDS = 0x0A4Ad3F53f7CaA2f1ac1cca82a06888D14C6f5FE; // Community Rewards 23.72%
    address public constant ECOLOGICALFUND = 0xd7e78856669059A3f9fB309317C9f84Ea9Ed9356; // Ecological Fund 10%
    address public constant MARKETCAP = 0x1db5784f50B667eDA27A8bc4334af33496d740C0; // MarketCap 7%
    address public constant LIQUIDITY = 0x867e01A6b6931067fD710768184C43e0b9e995C8; // Liquidity 11.9%
    address public constant SEEDHOLDER = 0x9326E6612a3cb2815E95129eDBF4Fa2FA5467705; // Seed Token Holder 2.38%
    address public constant STRATEGICHOLDER = 0xbD0Ed7c84AF8cC91349c439b7EAa6b153c94E013; // Strategic Token Holders (listed cex and investors) 10%
    address public constant TEAMCONSULTANTS = 0x0e89Fd7820EcD9c2E40bf9848E62F58b83453343; // Team and Consultants 5%
    address public constant VOTEBURN = 0x3b98140276f66b2c5BeE166F7DB779A0eA7C9B0F; // Vote to Burn 20%
    address public constant INITIALLOCKUP = 0x775cE8C2d9e0F45FA3b1B86C1bc9b5ADB19C06bF; // Initial Lockup 10%

    uint256 public constant TAXFREETIME = 43 days;
    uint256 public constant FIRSTLIMITTIME = 30 minutes;
    uint256 public constant FIRSTLIMITAMOUNT = 305 * (10**17);
    uint256 public constant DIVISOR = 10000;
    address public immutable lpPair; // USDT-RTC-LP

    //////////////////////////////// Front Tax ////////////////////////////////
    uint256 public transferFee = 9990;
    uint256 public freeTransferFeeTime = 30 minutes;
    bool public isImmunityTx = true;
    address public feeTo = 0x01a09dAF4923b2DD9cC087f7F8634f3415C45B77; // Fee To
    uint256 public buyFee;
    uint256 public sellFee;

    uint256 public openTime; // Time when liquidity was first added

    mapping(address => bool) public isImmunity;

    constructor() {
        lpPair = IPancakeSwapFactory(FACTORY).createPair(USDT, address(this));

        _mint(COMMUNITYREWARDS, (TOTALSUPPLY * (10**18) * 2372) / DIVISOR);
        _mint(ECOLOGICALFUND, (TOTALSUPPLY * (10**18) * 1000) / DIVISOR);
        _mint(MARKETCAP, (TOTALSUPPLY * (10**18) * 700) / DIVISOR);
        _mint(LIQUIDITY, (TOTALSUPPLY * (10**18) * 1190) / DIVISOR);
        _mint(SEEDHOLDER, (TOTALSUPPLY * (10**18) * 238) / DIVISOR);
        _mint(STRATEGICHOLDER, (TOTALSUPPLY * (10**18) * 1000) / DIVISOR);
        _mint(TEAMCONSULTANTS, (TOTALSUPPLY * (10**18) * 500) / DIVISOR);
        _mint(VOTEBURN, (TOTALSUPPLY * (10**18) * 2000) / DIVISOR);
        _mint(INITIALLOCKUP, (TOTALSUPPLY * (10**18) * 1000) / DIVISOR);

        address[] memory ims = new address[](9);

        ims[0] = COMMUNITYREWARDS;
        ims[1] = ECOLOGICALFUND;
        ims[2] = MARKETCAP;
        ims[3] = LIQUIDITY;
        ims[4] = SEEDHOLDER;
        ims[5] = STRATEGICHOLDER;
        ims[6] = TEAMCONSULTANTS;
        ims[7] = VOTEBURN;
        ims[8] = INITIALLOCKUP;

        turnToImmunity(ims, true);
    }

    /**
     * @dev override _transfer.
     */
    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        uint256 fee = 0;
        // Sell or Buy
        if (from == lpPair || to == lpPair) {
            if (openTime == 0) {
                openTime = block.timestamp;
            }
            if (from == lpPair) {
                // Buy
                if (!isOpening() && isImmunityTx) {
                    require(isImmunity[to], "EXCEPTION");
                }
                if (
                    !isOpening() &&
                    !isImmunity[to] &&
                    block.timestamp <= openTime + FIRSTLIMITTIME
                ) {
                    require(
                        this.balanceOf(to) + amount <= FIRSTLIMITAMOUNT,
                        "EXCEED LIMIT"
                    );
                }
                uint256 feeRate = isImmunity[to] ? 0 : getBuyFeeRate();
                if (feeRate != 0) {
                    fee = (amount * feeRate) / DIVISOR;
                    super._transfer(from, feeTo, fee);
                }
            } else {
                // Sell
                uint256 feeRate = isImmunity[from] ? 0 : getSellFeeRate();
                if (feeRate != 0) {
                    fee = (amount * feeRate) / DIVISOR;
                    super._transfer(from, feeTo, fee);
                }
            }
        } else if (
            block.timestamp <= openTime + freeTransferFeeTime &&
            transferFee != 0 &&
            !isImmunity[from] &&
            !isImmunity[to]
        ) {
            fee = (amount * transferFee) / DIVISOR;
            super._transfer(from, feeTo, fee);
        }
        amount -= fee;
        super._transfer(from, to, amount);
    }

    function isOpening() public view returns (bool) {
        return openTime != 0 && block.timestamp == openTime;
    }

    function _isAddLiquidity() internal view returns (bool) {
        (uint256 r0, uint256 r1, ) = IPancakeSwapPair(lpPair).getReserves();
        uint256 r;
        if (USDT < address(this)) {
            r = r0;
        } else {
            r = r1;
        }

        uint256 uBalance = IERC20(USDT).balanceOf(lpPair);
        return uBalance > r;
    }

    function _isRemoveLiquidity() internal view returns (bool) {
        (uint256 r0, uint256 r1, ) = IPancakeSwapPair(lpPair).getReserves();

        uint256 r;
        if (USDT < address(this)) {
            r = r0;
        } else {
            r = r1;
        }

        uint256 uBalance = IERC20(USDT).balanceOf(lpPair);
        return r >= uBalance;
    }

    /**
     * @dev getBuyFeeRate.
     */
    function getBuyFeeRate() public view returns (uint256) {
        bool isRemove = _isRemoveLiquidity();
        if (isRemove) return 0;
        if (buyFee != 0) return buyFee;
        if (block.timestamp > TAXFREETIME + openTime || isOpening()) return 0;
        return 500;
    }

    /**
     * @dev getSellFeeRate.
     */
    function getSellFeeRate() public view returns (uint256) {
        bool isAdd = _isAddLiquidity();
        if (isAdd) return 0;
        if (sellFee != 0) return sellFee;
        if (block.timestamp > TAXFREETIME + openTime || isOpening()) return 0;
        if (block.timestamp < 10 minutes + openTime) return 3500;
        if (block.timestamp < 20 minutes + openTime) return 2500;
        if (block.timestamp < 30 minutes + openTime) return 1500;
        return 500;
    }

    ////////////////////////////// Ownership //////////////////////////
    function turnToImmunity(address[] memory _accs, bool flag)
        public
        onlyOwner
    {
        for (uint256 i = 0; i < _accs.length; i++) {
            if (!isImmunity[_accs[i]]) {
                isImmunity[_accs[i]] = flag;
            }
        }
    }

    function turnOnPublic() external onlyOwner {
        isImmunityTx = false;
    }

    function changeFeeTo(address _feeTo) external onlyOwner {
        feeTo = _feeTo;
    }

    function changeTxFee(uint256 _buyFee, uint256 _sellFee) external onlyOwner {
        require(_buyFee < DIVISOR || _sellFee < DIVISOR, "Over divisor");
        buyFee = _buyFee;
        sellFee = _sellFee;
    }
}
