pragma solidity ^0.8.22;

interface IAC {
    function isOpenWL() external view returns (bool);
    function isWL(address addr) external view returns (bool);
    function isBL(address addr) external view returns (bool);
}

interface IMainLogic {
    function ownerOf(address addr, uint256 id) external view returns (bool);
    function getPetInfo(uint256 id) external view returns (uint8 agi, uint8 lv, uint8 hp, uint8 ak);
    function getInviterAddr(address addr) external view returns (address);
    function transferTo(address from, address to, uint256[] memory ids, uint256[] memory amounts) external;
}

interface IEpicToken {
    function mint(address to, uint amount) external;
    function burn(address to, uint amount) external;
}

interface IPassNFT {
    function usePassNFT(address account, uint256 amount) external;
}

interface ILpRouter {
    function addLiquidity(address tokenA, address tokenB, uint amountADesired, uint amountBDesired, uint amountAMin, uint amountBMin, address to, uint deadline) external returns (uint amountA, uint amountB, uint liquidity);
    function getAmountsOut(uint amountIn, address[] memory path) external view returns (uint[] memory amounts);
    function getAmountsIn(uint amountOut, address[] memory path) external view returns (uint[] memory amounts);
    function quote(uint amountA, uint reserveA, uint reserveB) external pure returns (uint amountB);
}

interface ILP {
    function getReserves() external view returns (uint112 reserve0, uint112 reserve1, uint32 blockTimestampLast);
}"
