      "content": "// SPDX-License-Identifier: MIT
// OpenContract
pragma solidity ^0.8.22;

import "@openzeppelin/contracts/access/Ownable.sol";

/**
 *  This contract module provides permission checks
 *  when Contract 2 calls Contract 1 to prevent permission attacks.
 */
abstract contract RoleV2 is Ownable {
    mapping(address => bool) private _role;

    /**
     *  Throws if called by any account other than the allowed contract.
     */
    modifier onlyControl() {
        require(_role[msg.sender], "You are not game logic contract");
        _;
    }

    /**
     * Modify allowed contract addresses.
     * Can only be called by the current owner.
     */
    function modiControl(address addr, bool option) external onlyOwner {
        _role[addr] = option;
    }
}
