    "contracts/MainLogic.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import "erc1155delta/contracts/ERC1155Delta.sol";
import "erc1155delta/contracts/extensions/ERC1155DeltaQueryable.sol";
import "@openzeppelin/contracts/token/ERC1155/utils/ERC1155Receiver.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./Library/Rolev2.sol";
import "./Library/LibI.sol";

contract MainLogic is ERC1155Delta, ERC1155Receiver, ERC1155DeltaQueryable, RoleV2 {
    struct Pet {
        uint8 agi;
        uint8 lv;
        uint8 hp;
        uint8 ak;
    }

    struct Player {
        address inviter;
        uint256 inviteYield;
        uint256 invitee;
        uint256 box;
    }

    mapping(uint256 => Pet) public pet;
    mapping(address => Player) public player;

    address private contracts = msg.sender;
    uint256 public TBox = 50_000;
    uint256 public BurnedNFT;
    uint256 private Index;

    IAC private AC = IAC(0x9A3b178bA42419911e6E3A20E5a720120f7A3c22);
    IEpicToken private EPIC = IEpicToken(0xb95C558c921D462C382FA7fCDa9A26409070262c);
    ILpRouter private LpRouter = ILpRouter(0x10ED43C718714eb63d5aA57B78B54704E256024E);
    ILP private LP = ILP(0x0465AB1F72b01A5c554142F29321C315E9cdDBC4);
    IERC20 private USDT = IERC20(0x55d398326f99059fF775485246999027B3197955);
    IERC20 private EPIC2 = IERC20(address(EPIC));

    constructor() ERC1155Delta("") {}

    function getPetInfo(uint256 id) external view returns (uint8 agi, uint8 lv, uint8 hp, uint8 ak) {
        agi = pet[id].agi;
        lv = pet[id].lv;
        hp = pet[id].hp;
        ak = pet[id].ak;
    }

    function setPetInfo(address to, uint256 id, uint8 hp,uint8 ak) external onlyControl {
        require(ownerOf(to, id), "Not your NFT");
        require(uint(pet[id].hp) + uint(hp) > 255, "Max HP");
        require(uint(pet[id].ak) + uint(ak) > 255, "Max AK");
        pet[id].hp = hp;
        pet[id].ak = ak;
    }

    function getInviterAddr(address addr) external view returns (address) {
        return player[addr].inviter;
    }

    function addInviter(address inviter) external {
        if (AC.isOpenWL()) {
            require(AC.isWL(inviter), "Inviter not in WL");
        }
        player[msg.sender].inviter = inviter;
        player[inviter].invitee += 1;
    }

    function buyBox(uint256 n) external {
        address inviterAddr = player[msg.sender].inviter;
        uint256 bCost = n * 20 ether;
        require(TBox - n >= 0, "Sold out");
        if (AC.isOpenWL()) {
            require(AC.isWL(msg.sender) || inviterAddr != address(0), "Not in WL");
        } else {
            bCost *= 2;
        }

        uint256 inviteYield = bCost / 10; // inviter get 10%
        uint256 teamYield = bCost / 10; // team get 10%
        uint256 transferUSDT = bCost - inviteYield - teamYield;

        bool trueInviter = (inviterAddr != address(0) && inviterAddr != msg.sender) ? true : false;
        inviterAddr = (trueInviter) ? inviterAddr : contracts;
        player[inviterAddr].inviteYield += inviteYield;

        USDT.transferFrom(msg.sender, address(this), transferUSDT);
        USDT.transferFrom(msg.sender, inviterAddr, inviteYield);
        USDT.transferFrom(msg.sender, contracts, teamYield);

        (uint reserveA, uint reserveB, uint timeBlock) = LP.getReserves();
        uint EpicAmount = LpRouter.quote(transferUSDT, reserveA, reserveB);
        EPIC.mint(address(this), EpicAmount);
        USDT.approve(address(LpRouter), type(uint256).max);
        EPIC2.approve(address(LpRouter), type(uint256).max);
        LpRouter.addLiquidity(address(USDT), address(EPIC), transferUSDT, EpicAmount, transferUSDT * 990 / 1000, EpicAmount * 990 / 1000, address(0), block.timestamp + 1);
        player[msg.sender].box += n;
        TBox -= n;
    }

    // Pet Max value: 255
    function openBox(uint256 n) external {
        require(player[msg.sender].box - n >= 0, "Out of index");
        player[msg.sender].box -= n;
        for (uint256 i; i < n; i++) {
            uint256 rand = uint256(keccak256(abi.encodePacked(i, Index))) % 100;
            uint256 max = 48 + ((rand >= 65) ? 48 : 0) + ((rand >= 80) ? 48 : 0) + ((rand >= 90) ? 48 : 0) + (rand >= 97 ? 48 : 0);
            pet[Index] = Pet(
                uint8(rand % 5),
                uint8(1 + ((rand >= 65) ? 1 : 0) + ((rand >= 80) ? 1 : 0) + ((rand >= 90) ? 1 : 0) + ((rand >= 97) ? 1 : 0)),
                uint8(uint256(keccak256(abi.encodePacked(i, Index, "hp"))) % max + 15),
                uint8(uint256(keccak256(abi.encodePacked(i, Index, "ak"))) % max + 15)
            );
            Index++;
        }
        _mint(msg.sender, n);
    }

    function upgradePet(uint256 id) external {
        require(pet[id].lv < 5 && ownerOf(msg.sender, id), "Max Lv or not your NFT");
        uint256 transferUSDT = (uint256(pet[id].lv) + 1) * 10 ether;

        USDT.transferFrom(msg.sender, address(this), transferUSDT);
        (uint reserveA, uint reserveB, uint timeBlock) = LP.getReserves();
        uint EpicAmount = LpRouter.quote(transferUSDT, reserveA, reserveB);
        EPIC.mint(address(this), EpicAmount);
        USDT.approve(address(LpRouter), type(uint256).max);
        EPIC2.approve(address(LpRouter), type(uint256).max);
        LpRouter.addLiquidity(address(USDT), address(EPIC), transferUSDT, EpicAmount, transferUSDT * 990 / 1000, EpicAmount * 990 / 1000, address(0), block.timestamp + 1);

        uint256 rand = uint256(keccak256(abi.encodePacked(id, pet[id].ak, pet[id].hp, block.timestamp, block.coinbase))) % 100;
        if (rand < 80) {
            pet[id] = Pet(
                pet[id].agi,
                pet[id].lv + 1,
                uint8(uint256(keccak256(abi.encodePacked(Index, pet[id].hp, block.timestamp + 1))) % 31 + 18) + pet[id].hp,
                uint8(uint256(keccak256(abi.encodePacked(Index, pet[id].ak, block.timestamp - 1))) % 31 + 18) + pet[id].ak
            );
        } else if (rand >= 95) {
            _burn(msg.sender, id);
            delete pet[id];
            BurnedNFT++;
        }
    }

    function transferTo(address from, address to, uint256[] memory ids, uint256[] memory amounts) external onlyControl {
        _safeBatchTransferFrom(from, to, ids, amounts, "");
    }

    function uri(uint256 tokenId) public view override returns (string memory) {
        return string(abi.encodePacked("https://ipfs.io/ipfs/bafybeifibgki7xlibsvb4uril4suzjqkywstl2k4cvjcsm7o7ekgfbo2au/", Strings.toString(pet[tokenId].agi), ".json"));
    }

    function ownerOf(address addr, uint256 id) public view returns (bool) {
        return isOwnerOf(addr, id);
    }

    function onERC1155Received(address operator, address from, uint256 id, uint256 value, bytes memory data) external view returns (bytes4) {
        return bytes4(keccak256("onERC1155Received(address,address,uint256,uint256,bytes)"));
    }

    function onERC1155BatchReceived(address operator, address from, uint256[] memory ids, uint256[] memory values, bytes memory data) external view returns (bytes4) {
        return bytes4(keccak256("onERC1155BatchReceived(address,address,uint256[],uint256[],bytes)"));
    }

    function supportsInterface(bytes4 interfaceId) public view override(ERC1155Receiver, ERC1155Delta) returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    function CleanUpRemainingEpic() external onlyOwner {
        uint256 cEPIC = EPIC2.balanceOf(address(this));
        EPIC.burn(address(this), cEPIC);
    }
}
"
    },
