contract DuUtility is Ownable {
    using SafeERC20 for IERC20;
    using SafeMath for uint256;

    //Struct for Buyers
    struct tokenBuyer{
        bool isRegistered;
        string firstName;
        string lastName;
        uint256 time;
        uint256 billsPaid;
        
    }

    //Mapping for Buyers
    mapping (address => tokenBuyer) public Customer;

    mapping (address => bool) public _blackListed;

    // DU token address
    IERC20 public immutable token = IERC20(0xe298eD3543B45037A2D4037ac6dfeB2E801f9803);

    // Registeration Fees
    uint256 private duFees = 100 * 10 ** 18;
    
    // Bill limit
    uint256 private billLimit = 3;

    // Addresses where funds are collected
    address public signUpRecieverWallet = 0x2892c461aea8AcC81e18F3eF60Af86eee50D4992;
    address public billFeeRecieverWallet = 0x9ae23306beF9F33b08b697829646189C58F87D56;

    //constructor() {}

    // Sign Up using Du tokens
    function signUpWithDU(string memory _firstName, string memory _lastName) public {
        require(Customer[msg.sender].isRegistered == false, "Already Registered");
        require(token.balanceOf(msg.sender) >= duFees, "Need sufficient Du tokens to register");
        Customer[msg.sender].firstName = _firstName;
        Customer[msg.sender].lastName = _lastName;

        token.safeTransferFrom(msg.sender, signUpRecieverWallet, duFees);

        Customer[msg.sender].isRegistered = true;
    }


    // Pay using Du tokens
    function payBillWithDU(uint256 _amount) public {
        require(!_blackListed[msg.sender], "This account is blacklisted");
        require(Customer[msg.sender].isRegistered, "User is not registered");

        if (isNewMonth()) {
         Customer[msg.sender].billsPaid = 0; 
        }

        if(Customer[msg.sender].billsPaid == 0) {
         Customer[msg.sender].time = block.timestamp;
        }

        Customer[msg.sender].billsPaid++;

        require(Customer[msg.sender].billsPaid <= billLimit,"Monthly Limit Reached");

        token.safeTransferFrom(msg.sender, billFeeRecieverWallet, _amount);
    }



   // Helper function to check if it's a new month
   function isNewMonth() internal view returns (bool) {
    if(block.timestamp > Customer[msg.sender].time + 30 days) {
        return true;
    }
    else {
        return false;
    }
   // return block.timestamp > Customer[msg.sender].time + 30 days;
   }

   //SETTER FUNCTIONS
   function blacklistFromService(address account) public onlyOwner {
     _blackListed[account] = true;
   }

   function unBlacklistFromService(address account) public onlyOwner {
     _blackListed[account] = false;
   }

   function changeDuSignUpFee(uint256 _amount) public onlyOwner {
     duFees = _amount;
   }

   function changeBillMonthlyLimit(uint256 _limit) public onlyOwner {
     billLimit = _limit;
   }

   function changeFeeRecieverWallets(address _signUpFeeWallet, address _billFeeWallet) public onlyOwner {
     signUpRecieverWallet = _signUpFeeWallet;
     billFeeRecieverWallet = _billFeeWallet;
   }

   //GETTER FUNCTIONS
   function getDUFee() public view returns (uint256) {
     return duFees;
   }

   function getBillLimit() public view returns (uint256) {
     return billLimit;
   }

   function checkIfEligible(address account) public view returns (bool) {
    if(Customer[account].billsPaid <= billLimit) {
        return false;
    } else {
        return true;
    }
   }


    

}
