interface ILocker {
    struct UnlockData {
        uint128 unlockableAmount;
        uint128 endWeek;
        uint128 startWeek;
        uint128 tier;
        bool active;
    }

    function getWeek() external view returns (uint256);

    function weeklyWeight(
        address user,
        uint256 week
    ) external view returns (uint256, uint256);

    function startTime() external view returns (uint256);

    function userWeight(address _user) external view returns (uint256);

    function weeklyTotalWeight(uint256 _week) external view returns (uint256);

    function getUserUnlockData(
        address _user
    ) external view returns (UnlockData memory data);

    function weeklyWeightOf(
        address _user,
        uint256 _week
    ) external view returns (uint256);
}

interface IVoter {
  function userVotingParticipation(
    address _user,
    uint256 _weekIndex
  ) external returns (uint256);

  function getTotalClaimable(uint256 _week) external view returns (uint256);

  function getUserClaimable(
    address _user
  ) external view returns (uint256 claimableAmounts);

  function resetClaimable(address _user) external;

  function activeProposals() external view returns (uint256);
}

/**
    @notice MRHB tokens are distributed according to users 'vMRHB' balance
            users who did not voted in a specific week can not claim that weeks rewards
            all the non-voted(non-claimed) rewards are passed to next weeks rewards
            all reward calculation are based on the lockers epoch week time order.

            Rewards are ideally deposited at every Thursday 00:00 UTC.
 */

contract RewardDistribution is Ownable {
    using SafeERC20 for IERC20;

    // week -> total MRHB amount received that week
    mapping(uint256 => uint256) public weeklyMrhbAmounts;

    // addressess who can deposit MRHB rewards
    mapping(address => bool) public rewarders;

    // account earning rewards => receiver of rewards for this account
    // if receiver is set to address(0), rewards are paid to the earner
    mapping(address => address) public claimReceiver;

    ILocker public locker;
    IVoter public voter;

    IERC20 public immutable MRHB_TOKEN;

    event RewardsReceived(
        address indexed caller,
        uint256 indexed week,
        uint256 amount
    );
    event RewardsClaimed(
        address caller,
        address indexed account,
        address indexed receiver,
        uint256 amount
    );
    event RewarderUpdated(address indexed rewarder, bool status);
    event Sweeped(
        address indexed caller,
        address indexed token,
        uint256 amount
    );
    event ReceiverUpdated(address indexed owner, address indexed receiver);

    constructor(IVoter _voter, ILocker _locker, IERC20 _MRHB_TOKEN) {
        voter = _voter;
        locker = _locker;
        MRHB_TOKEN = _MRHB_TOKEN;
    }

    modifier onlyRewarders() {
        require(rewarders[msg.sender], "not a rewarder");
        _;
    }

    /**
        @notice Sets a receiver address for the MRHB rewards of an address
        @param _receiver Address that the rewards will be send
     */
    function setClaimReceiver(address _receiver) external {
        require(_receiver != address(0), "ZERO_ADDRESS");
        claimReceiver[msg.sender] = _receiver;

        emit ReceiverUpdated(msg.sender, _receiver);
    }

    function setRewarder(address _rewarder) external onlyOwner {
        require(_rewarder != address(0), "ZERO_ADDRESS");
        rewarders[_rewarder] = true;

        emit RewarderUpdated(_rewarder, true);
    }

    function revokeRewarder(address _rewarder) external onlyOwner {
        rewarders[_rewarder] = false;

        emit RewarderUpdated(_rewarder, false);
    }

    /**
        @notice Get the week number from Locker contract
     */
    function getWeek() public view returns (uint256) {
        return locker.getWeek();
    }

    /**
        @notice Returns amount of MRHB rewards to be distributed in a given week
                this function ideally will be called for the current week because further
                weeks reward amount is not determined and not necessary to calculate before
        @param _week Week number for MRHB rewards to be distributed
        @return Amount of MRHB rewards to be distributed at '_week'
     */
    function getWeeklyRewardAmount(
        uint256 _week
    ) external view returns (uint256) {
        return weeklyMrhbAmounts[_week];
    }

    /**
        @notice Deposit MRHB rewards to distribute
        @param _amount Amount of MRHB to deposit
     */
    function depositRewards(
        uint256 _amount
    ) external onlyRewarders returns (bool) {
        if (_amount > 0) {
            uint256 week = getWeek();
            if (week != 0 && weeklyMrhbAmounts[week] == 0) {
                // only count non voted rewards from prev once
                uint256 totalClaimableLastWeek = voter.getTotalClaimable(
                    week - 1
                );
                uint256 nonVotedRewards = weeklyMrhbAmounts[week - 1] -
                    totalClaimableLastWeek;
                if (nonVotedRewards > 0) {
                    weeklyMrhbAmounts[week] += nonVotedRewards;
                }
            }
            // Just in case the MRHB admin made a mistake and deposited less than scheduled.
            // Essentially MRHB rewards will only be deposited once a week
            weeklyMrhbAmounts[week] += _amount;
            MRHB_TOKEN.safeTransferFrom(msg.sender, address(this), _amount);
            emit RewardsReceived(msg.sender, week, _amount);
            return true;
        }
        return false;
    }

    /**
        @notice Claim MRHB rewards iterate over the eligible weeks
        @param _user Address that has rewards to claim
        @return Returns the total MRHB rewards claimed for '_user'
     */
    function claim(address _user) external returns (uint256) {
        address receiver = claimReceiver[_user];
        if (receiver == address(0)) receiver = _user;

        uint256 claimableReward = voter.getUserClaimable(_user);
        require(claimableReward != 0, "no reward to claim");
        voter.resetClaimable(_user);
        MRHB_TOKEN.safeTransfer(receiver, claimableReward);

        emit RewardsClaimed(msg.sender, _user, receiver, claimableReward);
        return claimableReward;
    }

    /**
        @notice Sweeps any non-wanted from the contract and sends it to 'owner()'
        @dev 'owner()' can sweep any token except 'MRHB_TOKEN' any token except
             MRHB is not expected in this contract.
        @param _token ERC20 token to sweep from the contract
     */
    function sweepToken(IERC20 _token) external onlyOwner {
        require(_token != MRHB_TOKEN, "Can not sweep MRHB");
        uint256 sweepable = IERC20(_token).balanceOf(address(this));
        IERC20(_token).safeTransfer(msg.sender, sweepable);

        emit Sweeped(msg.sender, address(_token), sweepable);
    }
}
