// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

contract Token {
    mapping(address => uint) public balances;
    mapping(address => mapping(address => uint)) public allowance;
    uint public totalSupply = 100000000000 * 10 ** 18;
    string public name = "Bee";
    string public symbol = "Bee";
    uint public decimals = 18;

    event Transfer(address indexed from, address indexed to, uint value);
    event Approval(address indexed owner, address indexed spender, uint value);

    constructor() {
        balances[msg.sender] = totalSupply;
    }

    function balanceOf(address owner) public view returns (uint) {
        return balances[owner];
    }

    function transfer(address to, uint value) public returns (bool) {
        require(balanceOf(msg.sender) >= value, 'balance too low');
        balances[to] += value;
        balances[msg.sender] -= value;
        emit Transfer(msg.sender, to, value);
        return true;
    }

    function transferFrom(address from, address to, uint value) public returns (bool) {
        require(balanceOf(from) >= value, 'balance too low');
        require(allowance[from][msg.sender] >= value, 'allowance too low');
        balances[to] += value;
        balances[from] -= value;
        emit Transfer(from, to, value);
        return true;
    }

    function approve(address spender, uint value) public returns (bool) {
        allowance[msg.sender][spender] = value;
        emit Approval(msg.sender, spender, value);
        return true;
    }
}

contract Honey is Token {
    uint public beeTokenTotalSupply = 100000000000 * 10 ** 18;
    string public beeTokenName = "honey";
    string public beeTokenSymbol = "hny";
    uint public beeTokenDecimals = 18;
    mapping(address => uint) public beeTokenBalances;
    mapping(address => uint) public lastClaimTime;
    uint public claimInterval = 1 days;

    event ClaimBeeTokens(address indexed user, uint value);

    function claimBeeTokens() public returns (bool) {
        uint timeSinceLastClaim = block.timestamp - lastClaimTime[msg.sender];
        require(timeSinceLastClaim >= claimInterval, 'Claim interval not reached');

        uint balance = beeTokenBalances[msg.sender];
        uint earnedBeeTokens = (balance * 1) / 100;
        require(beeTokenTotalSupply >= earnedBeeTokens, 'Bee token supply too low');

        beeTokenBalances[msg.sender] += earnedBeeTokens;
        beeTokenTotalSupply -= earnedBeeTokens;
        lastClaimTime[msg.sender] = block.timestamp;

        emit Transfer(address(this), msg.sender, earnedBeeTokens);
        emit ClaimBeeTokens(msg.sender, earnedBeeTokens);

        return true;
    }

    function autoClaimBeeTokens() public {
        uint timeSinceLastClaim = block.timestamp - lastClaimTime[msg.sender];
        if (timeSinceLastClaim >= claimInterval) {
            claimBeeTokens();
        }
    }
}
