contract FirstToken is ERC20, ERC20Control {
	using SafeMath for uint256;
	uint256 public maxSupply = 100 * 1e6 * 1e18; // 100.000.000 Token

	constructor(address newRouter, address multiSigWallet)
		ERC20("HealthFi Token", "HEFI")
	{
		_mint(_msgSender(), maxSupply);
		IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(
			newRouter // address uniswap
		);
		uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
			.createPair(address(this), _uniswapV2Router.WETH());
		uniswapV2Router = _uniswapV2Router;

		_setupRole(DEFAULT_ADMIN_ROLE, multiSigWallet);
	}

	function sweepTokenForBosses(uint256 tokenAmount)
		external
		onlyRole(SWEEPER_ROLE)
	{
		uint256 contractTokenBalance = balanceOf(address(this));
		if (tokenAmount > contractTokenBalance) {
			tokenAmount = contractTokenBalance;
		}
		if (tokenAmount >= tokenForBosses) {
			_swapTokensForEth(tokenForBosses);
		} else {
			_swapTokensForEth(tokenAmount);
		}
	}

	function withDraw() external onlyRole(BOSS_CONTROL_ROLE) {
		// just in-case swap not work
		uint256 contractTokenBalance = balanceOf(address(this));
		_transfer(address(this), addressForBosses, contractTokenBalance);
	}

	function _swapTokensForEth(uint256 tokenAmount) private {
		// generate the uniswap pair path of token -> weth
		address[] memory path = new address[](2);
		path[0] = address(this);
		path[1] = uniswapV2Router.WETH();

		_approve(address(this), address(uniswapV2Router), tokenAmount);
		// make the swap
		uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
			tokenAmount,
			0, // accept any amount of ETH
			path,
			addressForBosses, // The contract
			block.timestamp
		);
	}

	function _transfer(
		address sender,
		address recipient,
		uint256 _amount
	) internal virtual override {
		uint256 transferFeeRate;

		uint256 amount = _amount;

		if (!whitelist[sender] || !whitelist[recipient]) {
			transferFeeRate = recipient == uniswapV2Pair
				? sellFeeRate
				: (sender == uniswapV2Pair ? buyFeeRate : 0);
		}

		if (transferFeeRate > 0) {
			uint256 _fee = amount.mul(transferFeeRate).div(100);
			super._transfer(sender, address(this), _fee); // TransferFee
			amount = amount.sub(_fee);
		}

		super._transfer(sender, recipient, amount);
	}
}
