pragma solidity 0.8.4;



contract ApeMoveERC721LazyMint is ApeMoveERC721Enumerable, EIP712 {
    mapping(address => bool) public signers;

    string private constant SIGNING_DOMAIN = "APEMOVE";
    string private constant SIGNATURE_VERSION = "1";

    /**
     * @dev Create a new L7NFTLazyMint contract and and assign `DEFAULT_ADMIN_ROLE, MINTER_ROLE` for the creator.
     * This construction function should be called from an exchange.
     *
     */
    constructor(string memory name_, string memory symbol_)
        ApeMoveERC721Enumerable(name_, symbol_)
        EIP712(SIGNING_DOMAIN, SIGNATURE_VERSION)
    {}

    function addSigner(address _signer) external onlyOwner {
        require(_signer != address(0), "ERR_SIGNER_IS_ZERO_ADDRESS");
        signers[_signer] = true;
    }

    function removeSigner(address _signer) external onlyOwner {
        require(_signer != address(0), "ERR_SIGNER_IS_ZERO_ADDRESS");
        signers[_signer] = false;
    }

    function equip(uint256 tokenId, bytes calldata signature) external {
        address _signer = _verify(_hash(msg.sender, tokenId), signature);

        require(signers[_signer], "ERR_INVALID_SIGNATURE");

        equip(tokenId);
    }

    function redeem(
        address owner,
        address redeemer,
        uint256 tokenId,
        bytes calldata signature
    ) external {
        address _signer = _verify(_hash(owner, tokenId), signature);

        require(signers[_signer], "ERR_INVALID_SIGNATURE");

        require(
            msg.sender == owner || isApprovedForAll(owner, msg.sender),
            "MUST_BE_OWNER_OR_APPROVED"
        );

        redeem(owner, tokenId);

        if (owner != redeemer) {
            _safeTransfer(owner, redeemer, tokenId, "");
        }
    }

    /// @notice Returns a hash of the given PendingNFT, prepared using EIP712 typed data hashing rules.
    function _hash(address owner, uint256 tokenId)
        internal
        view
        returns (bytes32)
    {
        return
            _hashTypedDataV4(
                keccak256(
                    abi.encode(
                        keccak256("APEMOVENFT(address owner, uint256 tokenId)"),
                        owner,
                        tokenId
                    )
                )
            );
    }

    /**
     * @dev Returns the signer for a pair of digested message and signature.
     */
    function _verify(bytes32 digest, bytes memory signature)
        internal
        pure
        returns (address)
    {
        bytes memory prefix = "\x19Ethereum Signed Message:\n32";
        bytes32 prefixedHash = keccak256(abi.encodePacked(prefix, digest));
        return ECDSA.recover(prefixedHash, signature);
    }
}

// File: contracts/nft/ApeMoveShoesNFT.sol



pragma solidity 0.8.4;



contract ApeMoveShoesNFT is ApeMoveERC721LazyMint, AccessControlEnumerable {
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");

    modifier onlyMinter() {
        require(
            hasRole(MINTER_ROLE, _msgSender()),
            "ApeMoveShoesNFT: Must be have minter role"
        );
        _;
    }

    constructor() ApeMoveERC721LazyMint("ApeMove Sneaker", "APESNEAKER") {
        __baseURI = "https://metadata.apemove.io/";
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _setupRole(MINTER_ROLE, _msgSender());
    }

    function mint(address to, uint256 tokenId) external virtual onlyMinter {
        require(!_exists(tokenId), "ApeMoveShoesNFT: tokenId is exists");
        _mint(to, tokenId);
    }

    function mintBatch(address[] memory accounts, uint256[] memory tokenIds)
        external
        virtual
        onlyMinter
    {
        uint256 length = accounts.length;
        require(
            length > 0 && length == tokenIds.length,
            "ApeMoveShoesNFT: accounts is invalid"
        );

        for (uint256 i = 0; i < length; i++) {
            require(
                !_exists(tokenIds[i]),
                "ApeMoveShoesNFT: tokenId is exists"
            );
            _mint(accounts[i], tokenIds[i]);
        }
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(AccessControlEnumerable, ApeMoveERC721Enumerable)
        returns (bool)
    {
        return ApeMoveERC721Enumerable.supportsInterface(interfaceId);
    }
}
