pragma solidity 0.8.19;

import {IRouterClient} from "@chainlink/contracts-ccip/src/v0.8/ccip/interfaces/IRouterClient.sol";
import {OwnerIsCreator} from "@chainlink/contracts-ccip/src/v0.8/shared/access/OwnerIsCreator.sol";
import {Client} from "@chainlink/contracts-ccip/src/v0.8/ccip/libraries/Client.sol";
import {LinkTokenInterface} from "@chainlink/contracts/src/v0.8/interfaces/LinkTokenInterface.sol";

interface IERC20 {
    function transfer(address recipient, uint256 amount) external returns (bool);

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) external returns (bool);
}

/// @title - A simple contract for sending string data across chains.
contract SenderBSC is OwnerIsCreator {

    IERC20 public LINK;
    IERC20 public TOKEN;

    uint public priceTransfer;
    uint public gas = 200000;

    error NotEnoughBalance(uint256 currentBalance, uint256 calculatedFees); // Used to make sure contract has enough balance.

    // Event emitted when a message is sent to another chain.
    event MessageSent(
        bytes32 indexed messageId, // The unique ID of the CCIP message.
        uint64 indexed destinationChainSelector, // The chain selector of the destination chain.
        address receiver, // The address of the receiver on the destination chain.
        uint[] text, // The text being sent.
        address feeToken, // the token address used to pay CCIP fees.
        uint256 fees // The fees paid for sending the CCIP message.
    );

    IRouterClient router;

    LinkTokenInterface linkToken;

    constructor(address _router, address _link) {
        router = IRouterClient(_router);
        linkToken = LinkTokenInterface(_link);
    } 

    function send(
        uint64 destinationChainSelector,
        address receiver,
        uint[] memory amount
    ) external onlyOwner returns (bytes32 messageId) {

        TOKEN.transferFrom(msg.sender, address(this), amount[0]);

        Client.EVM2AnyMessage memory evm2AnyMessage = Client.EVM2AnyMessage({
            receiver: abi.encode(receiver), 
            data: abi.encode(amount, msg.sender), 
            tokenAmounts: new Client.EVMTokenAmount[](0), 
            extraArgs: Client._argsToBytes(            
                Client.EVMExtraArgsV1({gasLimit: gas, strict: false})
            ),

            feeToken: address(linkToken)
        });


        uint256 fees = router.getFee(destinationChainSelector, evm2AnyMessage);

        if (fees > linkToken.balanceOf(address(this)))
            revert NotEnoughBalance(linkToken.balanceOf(address(this)), fees);


        linkToken.approve(address(router), fees);


        messageId = router.ccipSend(destinationChainSelector, evm2AnyMessage);

        // Emit an event with message details
        emit MessageSent(
            messageId,
            destinationChainSelector,
            receiver,
            amount,
            address(linkToken),
            fees
        );

        // Return the message ID
        return messageId;
    }

    //ADMIN Functions
    function setPrice(uint _price) public onlyOwner {
        priceTransfer = _price;
    }

    function setGas(uint _gas) public onlyOwner {
        gas = _gas;
    }

    function withdrawLink(uint _amount) public onlyOwner {
        LINK.transfer(msg.sender, _amount);
    }

    function withdrawToken(uint _amount) public onlyOwner {
        TOKEN.transfer(msg.sender, _amount);
    }

    function setContracts( address _router, address _link, IERC20 _linkForWithdraw, IERC20 _token) public onlyOwner {
        TOKEN = _token;
        router = IRouterClient(_router);
        linkToken = LinkTokenInterface(_link);
        LINK = _linkForWithdraw;
    }
}"
