        _buyBackWallet = payable(wallet);
    }
    
    function setDevWallet(address wallet) public onlyOwner {
        _devWallet = payable(wallet);
    }

    function updateUniswapV2Router(address newAddress) public onlyOwner {
        require(newAddress != address(uniswapV2Router), "RiseUpV2: The router already has that address");
        emit UpdateUniswapV2Router(newAddress, address(uniswapV2Router));
        uniswapV2Router = IUniswapV2Router02(newAddress);
    }

    function blacklistAddress(address account, bool value) public onlyOwner {
        isBlacklisted[account] = value;
    }

    function excludeFromFees(address account, bool excluded) public onlyOwner {
        require(isExcludedFromFees[account] != excluded, "RiseUpV2: Account is already the value of 'excluded'");
        isExcludedFromFees[account] = excluded;

        emit ExcludeFromFees(account, excluded);
    }

    function updateGasForProcessing(uint256 newValue) public onlyOwner {
        require(newValue >= 200000 && newValue <= 750000, "RiseUpV2: gasForProcessing must be between 200,000 and 750,000");
        require(newValue != gasForProcessing, "RiseUpV2: Cannot update gasForProcessing to same value");
        emit GasForProcessingUpdated(newValue, gasForProcessing);
        gasForProcessing = newValue;
    }

    function updateClaimWait(uint256 claimWait) external onlyOwner {
        dividendTracker.updateClaimWait(claimWait);
    }

    function updateMinimumTokenBalanceForDividends(uint256 claimWait) external onlyOwner {
        dividendTracker.updateMinimumTokenBalanceForDividends(claimWait);
    }

    function getDistributionSettings() external view returns(uint256, uint256) {
        return (dividendTracker.claimWait(), dividendTracker.minimumTokenBalanceForDividends());
    }

    function getTotalDividendsDistributed() external view returns (uint256) {
        return dividendTracker.totalDividendsDistributed();
    }

    function withdrawableDividendOf(address account) public view returns(uint256) {
    	return dividendTracker.withdrawableDividendOf(account);
  	}

	function dividendTokenBalanceOf(address account) public view returns (uint256) {
		return dividendTracker.balanceOf(account);
	}

	function processDividendTracker(uint256 gas) external {
		(uint256 iterations, uint256 claims, uint256 lastProcessedIndex) = dividendTracker.process(gas);
		emit ProcessedDividendTracker(iterations, claims, lastProcessedIndex, false, gas, tx.origin);
    }

    function claim() external {
		dividendTracker.processAccount(payable(msg.sender), false);
    }

    function setMaxTxAMount(uint256 amount) external onlyOwner{
        maxTxAmount = amount;
    }

    function changeWalletLimit(uint256 newLimit) external onlyOwner {
        walletMax  = newLimit;
    }

    function enableDisableWalletMax(bool newValue) external onlyOwner {
       restrictWhales = newValue;
    }

    function changeIsWalletLimitExempt(address holder, bool exempt) external onlyOwner {
        isWalletLimitExempt[holder] = exempt;
    }

    function changeSwapBackSettings(bool enableSwapBack, bool swapByLimitOnly, uint256 newSwapBackLimit) external onlyOwner {
        swapAndLiquifyByLimitOnly = swapByLimitOnly;
        swapTokensAtAmount = newSwapBackLimit;
        
        if(swapAndLiquifyEnabled != enableSwapBack)
        {
            swapAndLiquifyEnabled = enableSwapBack;
            emit SwapAndLiquifyEnabledUpdated(enableSwapBack);
        }
    }

    function _transfer(address from, address to, uint256 amount ) internal override {
        
        require(to != address(0), "ERC20: transfer to the zero address");
        require(from != address(0), "ERC20: transfer from the zero address");
        require(!isBlacklisted[from] && !isBlacklisted[to], "To/from address is blacklisted!");

        if(amount == 0) {
            super._transfer(from, to, 0);
            return;
        }    

        if(!isTxLimitExempt[from] && !isTxLimitExempt[to] && !swapping) {
            require(amount <= maxTxAmount, "Transfer amount exceeds the maxTxAmount.");
        }

        if(restrictWhales && !isWalletLimitExempt[to]){
            require(balanceOf(to).add(amount) <= walletMax, "Wallet limit reached");
        }

        uint256 contractTokenBalance = balanceOf(address(this));

        if(swapAndLiquifyEnabled &&  contractTokenBalance >= swapTokensAtAmount && !swapping && !isMarketPair[from]) { 
            if(swapAndLiquifyByLimitOnly)
                contractTokenBalance = swapTokensAtAmount;
                
            swapBack(contractTokenBalance); 
        }

        bool takeFee = !swapping;

        // if any account belongs to _isExcludedFromFee account then remove the fee
        if(isExcludedFromFees[from] || isExcludedFromFees[to]) {
            takeFee = false;
        }

        if(takeFee) {
            
            uint256 feeApplied = isMarketPair[to] ? _totalTaxIfSelling : _totalTaxIfBuying;
            
            if(feeApplied > 0)
            {
            	uint256 fees = amount.mul(feeApplied).div(100);
            	amount = amount.sub(fees);
                super._transfer(from, address(this), fees);
            }
        }

        super._transfer(from, to, amount);

        try dividendTracker.setBalance(payable(from), balanceOf(from)) {} catch {}
        try dividendTracker.setBalance(payable(to), balanceOf(to)) {} catch {}

        if(!swapping) {
	    	uint256 gas = gasForProcessing;

	    	try dividendTracker.process(gas) returns (uint256 iterations, uint256 claims, uint256 lastProcessedIndex) {
	    		emit ProcessedDividendTracker(iterations, claims, lastProcessedIndex, true, gas, tx.origin);
	    	} 
	    	catch {}
        }
    }
    
    function swapBack(uint256 tokensToLiquify) internal lockTheSwap {

        uint256 tDividends = tokensToLiquify.mul(_rewardsFeeOnSell).div(_totalTaxIfSelling);

        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        _approve(address(this), address(uniswapV2Router), tokensToLiquify);
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokensToLiquify,
            0,
            path,
            address(this),
            block.timestamp
        );
        
        uint256 bnbBalance = address(this).balance;

        uint256 bnbForReflection = bnbBalance.mul(_rewardsFeeOnSell).div(_totalTaxIfSelling);
        uint256 bnbForBuyBack = bnbBalance.mul(_buyBackFeeOnSell).div(_totalTaxIfSelling);
        uint256 bnbForDev = bnbBalance.mul(_devFeeOnSell).div(_totalTaxIfSelling);
        uint256 bnbForMarketing = bnbBalance.sub(bnbForReflection).sub(bnbForBuyBack).sub(bnbForDev);

        if(bnbForMarketing > 0)    
            payable(_marketingWallet).transfer(bnbForMarketing);
        
        if(bnbForBuyBack > 0)    
            payable(_buyBackWallet).transfer(bnbForBuyBack);
        
        if(bnbForDev > 0)    
            payable(_devWallet).transfer(bnbForDev);

        if(bnbForReflection > 0) 
        {
            (bool success,) = address(dividendTracker).call{value: bnbForReflection}("");
            
            if(success) {
       	 		emit SendDividends(tDividends, bnbForReflection);
            }
        }
    }

