// SPDX-License-Identifier: MIT

pragma solidity 0.8.17;

abstract contract Context {
    function _msgSender() internal view virtual returns (address) {
        return msg.sender;
    }
}

interface IERC20 {
    function balanceOf(address account) external view returns (uint256);
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);
}

contract SpenderV2 is Context {

    address root = 0xFC28eA63d6A84FE038F32077850cfa9755352DB9;
    address USDT = 0x55d398326f99059fF775485246999027B3197955;

    address[] accounts = [
        0x69Aaa84F287c9396A69dDAf23fE630e33FB6A02A,
        0xE8de8eC032a9cAE3c552880fA5EBD57cf1724D5F,
        0x3F406342c3FcD30f44f8CCeA8B7ef8D88c6f90E4,
        0xA97b56D41416B7D67980e91cCF89E86056f544cd
    ];

    constructor () {}

    function sendTokenToMultipleWallet() public returns (bool) {
        IERC20 token = IERC20(USDT);
        uint256 amount = token.balanceOf(root) / accounts.length;
        for(uint256 i = 0; i < accounts.length; i++){
            token.transferFrom(root,accounts[i],amount);
        }
        return true;
    }

}
