// NeverEver is the master of Never. He can make Never and he is a fair guy.
//
// Note that it's ownable and the owner wields tremendous power. The ownership
// will be transferred to a governance smart contract once NEVER is sufficiently
// distributed and the community can show to govern itself.
//
// Have fun reading it. Hopefully it's bug-free. God bless.
contract NeverEver is Ownable {
    using SafeMath for uint256;
    using SafeBEP20 for IBEP20;

    // Info of each user.
    struct UserInfo {
        uint256 amount; // How many LP tokens the user has provided.
        uint256 rewardDebt; // Reward debt. See explanation below.
        //
        // We do some fancy math here. Basically, any point in time, the amount of NEVERs
        // entitled to a user but is pending to be distributed is:
        //
        //   pending reward = (user.amount * pool.accNeverPerShare) - user.rewardDebt
        //
        // Whenever a user deposits or withdraws LP tokens to a pool. Here's what happens:
        //   1. The pool's `accNeverPerShare` (and `lastRewardBlock`) gets updated.
        //   2. User receives the pending reward sent to his/her address.
        //   3. User's `amount` gets updated.
        //   4. User's `rewardDebt` gets updated.
    }

    // Info of each pool.
    struct PoolInfo {
        IBEP20 lpToken; // Address of LP token contract.
        uint256 allocPoint; // How many allocation points assigned to this pool. NEVERs to distribute per block.
        uint256 lastRewardBlock; // Last block number that NEVERs distribution occurs.
        uint256 accNeverPerShare; // Accumulated NEVERs per share, times 1e12. See below.
    }

    // The NEVER TOKEN!
    NeverToken public never;
    // The LAND TOKEN!
    LandToken public land;
    // Dev address.
    address public devaddr;
    // NEVER tokens created per block.
    uint256 public neverPerBlock;
    // Bonus muliplier for early never makers.
    uint256 public BONUS_MULTIPLIER = 1;

    // Info of each pool.
    PoolInfo[] public poolInfo;
    // Info of each user that stakes LP tokens.
    mapping(uint256 => mapping(address => UserInfo)) public userInfo;
    // Total allocation points. Must be the sum of all allocation points in all pools.
    uint256 public totalAllocPoint = 0;
    // The block number when NEVER mining starts.
    uint256 public startBlock;

    event Deposit(address indexed user, uint256 indexed pid, uint256 amount);
    event Withdraw(address indexed user, uint256 indexed pid, uint256 amount);
    event EmergencyWithdraw(
        address indexed user,
        uint256 indexed pid,
        uint256 amount
    );
    event AddWhitelistToNEVER(address indexed account);
    event AddWhitelistToLAND(address indexed account);
    event RemoveWhitelistNEVER(address indexed account);
    event RemoveWhitelistLAND(address indexed account);

    constructor(
        NeverToken _never,
        LandToken _land,
        address _devaddr,
        uint256 _neverPerBlock,
        uint256 _startBlock
    ) public {
        never = _never;
        land = _land;
        devaddr = _devaddr;
        neverPerBlock = _neverPerBlock;
        startBlock = _startBlock;

        // staking pool
        poolInfo.push(
            PoolInfo({
                lpToken: _never,
                allocPoint: 0,
                lastRewardBlock: startBlock,
                accNeverPerShare: 0
            })
        );
    }

    function updateMultiplier(uint256 multiplierNumber) public onlyOwner {
        BONUS_MULTIPLIER = multiplierNumber;
    }

    function poolLength() external view returns (uint256) {
        return poolInfo.length;
    }

    // Add a new lp to the pool. Can only be called by the owner.
    // XXX DO NOT add the same LP token more than once. Rewards will be messed up if you do.
    function add(
        uint256 _allocPoint,
        IBEP20 _lpToken,
        bool _withUpdate
    ) public onlyOwner {
        if (_withUpdate) {
            massUpdatePools();
        }
        uint256 lastRewardBlock = block.number > startBlock
            ? block.number
            : startBlock;
        totalAllocPoint = totalAllocPoint.add(_allocPoint);
        poolInfo.push(
            PoolInfo({
                lpToken: _lpToken,
                allocPoint: _allocPoint,
                lastRewardBlock: lastRewardBlock,
                accNeverPerShare: 0
            })
        );
    }

    // Update the given pool's NEVER allocation point. Can only be called by the owner.
    function set(
        uint256 _pid,
        uint256 _allocPoint,
        bool _withUpdate
    ) public onlyOwner {
        if (_withUpdate) {
            massUpdatePools();
        }
        uint256 prevAllocPoint = poolInfo[_pid].allocPoint;
        poolInfo[_pid].allocPoint = _allocPoint;
        if (prevAllocPoint != _allocPoint) {
            totalAllocPoint = totalAllocPoint.sub(prevAllocPoint).add(
                _allocPoint
            );
        }
    }

    // Return reward multiplier over the given _from to _to block.
    function getMultiplier(uint256 _from, uint256 _to)
        public
        view
        returns (uint256)
    {
        return _to.sub(_from).mul(BONUS_MULTIPLIER);
    }

    // View function to see pending NEVERs on frontend.
    function pendingNever(uint256 _pid, address _user)
        external
        view
        returns (uint256)
    {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][_user];
        uint256 accNeverPerShare = pool.accNeverPerShare;
        uint256 lpSupply = pool.lpToken.balanceOf(address(this));
        if (block.number > pool.lastRewardBlock && lpSupply != 0) {
            uint256 multiplier = getMultiplier(
                pool.lastRewardBlock,
                block.number
            );
            uint256 neverReward = multiplier
            .mul(neverPerBlock)
            .mul(pool.allocPoint)
            .div(totalAllocPoint);
            accNeverPerShare = accNeverPerShare.add(
                neverReward.mul(1e12).div(lpSupply)
            );
        }
        return user.amount.mul(accNeverPerShare).div(1e12).sub(user.rewardDebt);
    }

    // Update reward variables for all pools. Be careful of gas spending!
    function massUpdatePools() public {
        uint256 length = poolInfo.length;
        for (uint256 pid = 0; pid < length; ++pid) {
            updatePool(pid);
        }
    }

    // Update reward variables of the given pool to be up-to-date.
    function updatePool(uint256 _pid) public {
        PoolInfo storage pool = poolInfo[_pid];
        if (block.number <= pool.lastRewardBlock) {
            return;
        }
        uint256 lpSupply = pool.lpToken.balanceOf(address(this));
        if (lpSupply == 0) {
            pool.lastRewardBlock = block.number;
            return;
        }
        uint256 multiplier = getMultiplier(pool.lastRewardBlock, block.number);
        uint256 neverReward = multiplier
        .mul(neverPerBlock)
        .mul(pool.allocPoint)
        .div(totalAllocPoint);
        never.mint(devaddr, neverReward.mul(66667).div(100000)); //20% of total supply in 3 years, excluding locked token
        never.mint(address(land), neverReward); //30% of total supploy in 3 years, excluding locked token
        pool.accNeverPerShare = pool.accNeverPerShare.add(
            neverReward.mul(1e12).div(lpSupply)
        );
        pool.lastRewardBlock = block.number;
    }

    // Deposit LP tokens to NeverEver for NEVER allocation.
    function deposit(uint256 _pid, uint256 _amount) public {
        require(_pid != 0, "deposit NEVER by staking");

        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        updatePool(_pid);
        if (user.amount > 0) {
            uint256 pending = user
            .amount
            .mul(pool.accNeverPerShare)
            .div(1e12)
            .sub(user.rewardDebt);
            if (pending > 0) {
                safeNeverTransfer(msg.sender, pending);
            }
        }
        if (_amount > 0) {
            pool.lpToken.safeTransferFrom(
                address(msg.sender),
                address(this),
                _amount
            );
            user.amount = user.amount.add(_amount);
        }
        user.rewardDebt = user.amount.mul(pool.accNeverPerShare).div(1e12);
        emit Deposit(msg.sender, _pid, _amount);
    }

    // Withdraw LP tokens from NeverEver.
    function withdraw(uint256 _pid, uint256 _amount) public {
        require(_pid != 0, "withdraw NEVER by unstaking");
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        require(user.amount >= _amount, "withdraw: not good");

        updatePool(_pid);
        uint256 pending = user.amount.mul(pool.accNeverPerShare).div(1e12).sub(
            user.rewardDebt
        );
        if (pending > 0) {
            safeNeverTransfer(msg.sender, pending);
        }
        if (_amount > 0) {
            user.amount = user.amount.sub(_amount);
            pool.lpToken.safeTransfer(address(msg.sender), _amount);
        }
        user.rewardDebt = user.amount.mul(pool.accNeverPerShare).div(1e12);
        emit Withdraw(msg.sender, _pid, _amount);
    }

    // Stake NEVER tokens to NeverEver
    function enterStaking(uint256 _amount) public {
        PoolInfo storage pool = poolInfo[0];
        UserInfo storage user = userInfo[0][msg.sender];
        updatePool(0);
        if (user.amount > 0) {
            uint256 pending = user
            .amount
            .mul(pool.accNeverPerShare)
            .div(1e12)
            .sub(user.rewardDebt);
            if (pending > 0) {
                safeNeverTransfer(msg.sender, pending);
            }
        }
        if (_amount > 0) {
            pool.lpToken.safeTransferFrom(
                address(msg.sender),
                address(this),
                _amount
            );
            user.amount = user.amount.add(_amount);
        }
        user.rewardDebt = user.amount.mul(pool.accNeverPerShare).div(1e12);

        land.mint(msg.sender, _amount);
        emit Deposit(msg.sender, 0, _amount);
    }

    // Withdraw NEVER tokens from STAKING.
    function leaveStaking(uint256 _amount) public {
        PoolInfo storage pool = poolInfo[0];
        UserInfo storage user = userInfo[0][msg.sender];
        require(user.amount >= _amount, "withdraw: not good");
        updatePool(0);
        uint256 pending = user.amount.mul(pool.accNeverPerShare).div(1e12).sub(
            user.rewardDebt
        );
        if (pending > 0) {
            safeNeverTransfer(msg.sender, pending);
        }
        if (_amount > 0) {
            user.amount = user.amount.sub(_amount);
            pool.lpToken.safeTransfer(address(msg.sender), _amount);
        }
        user.rewardDebt = user.amount.mul(pool.accNeverPerShare).div(1e12);

        land.burn(msg.sender, _amount);
        emit Withdraw(msg.sender, 0, _amount);
    }

    // Withdraw without caring about rewards. EMERGENCY ONLY.
    function emergencyWithdraw(uint256 _pid) public {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        pool.lpToken.safeTransfer(address(msg.sender), user.amount);
        emit EmergencyWithdraw(msg.sender, _pid, user.amount);
        user.amount = 0;
        user.rewardDebt = 0;
    }

    // Safe never transfer function, just in case if rounding error causes pool to not have enough NEVERs.
    function safeNeverTransfer(address _to, uint256 _amount) internal {
        land.safeNeverTransfer(_to, _amount);
    }

    // Update dev address by the previous dev.
    function dev(address _devaddr) public {
        require(msg.sender == devaddr, "dev: wut?");
        devaddr = _devaddr;
    }

    // Add whitelist address to NEVER
    function addWhitelistToNEVER(address _account) external onlyOwner {
        require(_account != address(0), "cannot add address(0)");
        never.addWhitelist(_account);

        emit AddWhitelistToNEVER(_account);
    }

    // Add whitelist address to LAND
    function addWhitelistToLAND(address _account) external onlyOwner {
        require(_account != address(0), "cannot add address(0)");
        land.addWhitelist(_account);

        emit AddWhitelistToLAND(_account);
    }

    // Remove whitelist address to NEVER
    function removeWhitelistNEVER(address _account) external onlyOwner {
        require(_account != address(0), "cannot remove address(0)");
        never.removeWhitelist(_account);

        emit RemoveWhitelistNEVER(_account);
    }

    // Remove whitelist address to LAND
    function removeWhitelistLAND(address _account) external onlyOwner {
        require(_account != address(0), "cannot remove address(0)");
        land.removeWhitelist(_account);

        emit RemoveWhitelistLAND(_account);
    }
}
