    "contracts/rose/Butterfly.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Factory.sol";
import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Pair.sol";
import "@uniswap/v2-periphery/contracts/interfaces/IUniswapV2Router02.sol";

contract Butterfly is ERC20 {
    using EnumerableSet for EnumerableSet.AddressSet;

    uint256 private constant DENOMINATOR = 10000;
    address private constant DEAD = 0x000000000000000000000000000000000000dEaD;

    bool public trade;
    uint256 public gasPrice = 20 gwei;
    uint256 public startBlock;
    address public operator;
    address public liquidityProvider;
    address public token;
    address public router;
    address public pair;
    address public foundation;
    address[8] public markets;
    uint256[4] priceStage = [0.1e18, 0.01e18, 0.001e18, 0.0001e18];
    uint256[4] burnFees = [100, 100, 400, 800];
    uint256[4] lpFees = [200, 300, 300, 500];
    uint256[4] marketFees = [20, 100, 150, 200];
    uint256[4] market2Fees = [30, 100, 200, 300];
    uint256[4] market3Fees = [50, 100, 200, 300];
    uint256[4] market4Fees = [20, 50, 200, 200];
    uint256[4] market5Fees = [60, 150, 400, 500];
    uint256[4] market6Fees = [20, 100, 150, 200];
    uint256[8] inFees = [100, 200, 20, 30, 50, 20, 60, 20];
    uint256 public transferFee = 1000;
    mapping(address => bool) public excludedFee;
    EnumerableSet.AddressSet private blacklist;
    mapping(address => uint256) public purchaseAmount;

    constructor(string memory name, string memory symbol, address _router, address _token, address _foundation) ERC20(name, symbol) {
        _mint(msg.sender, 9.99999999e8 * 10**18);
        operator = msg.sender;
        liquidityProvider = msg.sender;
        token = _token;
        foundation = _foundation;
        router = _router;
        pair = IUniswapV2Factory(IUniswapV2Router02(_router).factory()).createPair(address(this), _token);

        excludedFee[msg.sender] = true;
    }

    function setTransferFee(uint256 _transferFee) external {
        if(operator == msg.sender) {
            transferFee = _transferFee;
        }
    }

    function setMarkets(address[8] calldata _markets) external {
        if(operator == msg.sender) {
            for(uint i = 0; i < _markets.length; i++) {
                markets[i] = _markets[i];
            }
        }
    }

    function setOperator(address _operator) external {
        if(operator == msg.sender) {
            operator = _operator;
            excludedFee[_operator] = true;
        }
    }

    function setLiquidityProvider(address _liquidityProvider) external {
        if(operator == msg.sender) {
            liquidityProvider = _liquidityProvider;
            excludedFee[_liquidityProvider] = true;
        }
    }

    function setExcludedFee(address account, bool status) public {
        if(operator == msg.sender) {
            excludedFee[account] = status;
        }
    }

    function multipleSetExcludedFee(address[] calldata accounts, bool status) external {
        for(uint i = 0; i < accounts.length; i++) {
            setExcludedFee(accounts[i], status);
        }
    }

    function setTrade(bool _trade) external {
        if(liquidityProvider == msg.sender) {
            trade = _trade;
            startBlock = block.number + 2;
        }
    }

    function addBlacklist(address account) public {
        if(operator == msg.sender) {
            blacklist.add(account);
        }
    }

    function multipleAddBlacklist(address[] calldata accounts) external {
        for(uint i = 0; i < accounts.length; i++) {
            addBlacklist(accounts[i]);
        }
    }

    function removeBlacklist(address account) public {
        if(operator == msg.sender) {
            blacklist.remove(account);
        }
    }

    function multipleRemoveBlacklist(address[] calldata accounts) external {
        for(uint i = 0; i < accounts.length; i++) {
            removeBlacklist(accounts[i]);
        }
    }

    function getBlacklist() external view returns(address[] memory list) {
        uint length = blacklist.length();
        list = new address[](length);
        for(uint i = 0; i < length; i++) {
            list[i] = blacklist.at(i);
        }
    }

    function transferLimit(address account, uint256 amount) private view {
        if(account != pair) {
            uint256 balance = balanceOf(account);
            require(amount <= balance * 9900 / DENOMINATOR, "touch transfer limit");
        }
    }

    function purchaseLimit(address account) private {
        (uint256 reserve0, uint256 reserve1, ) = IUniswapV2Pair(pair).getReserves();
        address token0 = IUniswapV2Pair(pair).token0();
        uint256 balance = IUniswapV2Pair(token).balanceOf(pair);
        uint256 amount;
        if(token0 == token) {
            if(balance > reserve0) {
                amount = balance - reserve0;
            }
        } else {
            if(balance > reserve1) {
                amount = balance - reserve1;
            }
        }
        if(block.number <= startBlock + 600) {
            uint256 newPurchaseAmount = amount + purchaseAmount[account];
            require(newPurchaseAmount <= 10e18, "exceeds purchase limit");
            purchaseAmount[account] = newPurchaseAmount;
        }
    }

    function isAddLiquidity(address to) private view returns(bool) {
        if(to != pair) {
            return false;
        }
        (uint256 reserve0, uint256 reserve1, ) = IUniswapV2Pair(pair).getReserves();
        address token0 = IUniswapV2Pair(pair).token0();
        uint256 balance = IUniswapV2Pair(token).balanceOf(pair);
        if(token0 == token) {
            return balance > reserve0;
        } else {
            return balance > reserve1;
        }
    }

    function passThrough(address from, address to) private view returns(bool) {
        if(from == liquidityProvider || to == liquidityProvider) {
            return true;
        }
        if(!trade && isAddLiquidity(to)) {
            return true;
        }
        return false;
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual override {
        if(passThrough(from, to)) {
            super._transfer(from, to, amount);
            return;
        }

        transferLimit(from, amount);
        
        address account = from != pair ? from : to;
        require(!blacklist.contains(account), "in blacklist");
        
        if(from == pair || to == pair) {
            require(account == tx.origin, "caller must be human");
            require(trade, "prohibit swap");
            if(block.number <= startBlock || tx.gasprice >= gasPrice) {
                blacklist.add(account);
            }
            if(!excludedFee[account]) {
                uint256[] memory fees;
                if(from == pair) {
                    purchaseLimit(to);
                }
                if(from == pair || isAddLiquidity(to)) {
                    fees = getTax(Status.IN);
                } else {
                    fees = getTax(Status.OUT);
                }
                uint256 tempAmount = amount;
                for(uint i = 0; i < fees.length; i++) {
                    uint feeAmount = fees[i] * tempAmount / DENOMINATOR;
                    if(feeAmount > 0) {
                        amount -= feeAmount;
                        if(markets[i] == DEAD) {
                            burnFeeDistribute(from, feeAmount);
                        } else {
                            super._transfer(from, markets[i], feeAmount);
                        }
                        
                    }
                }
            }
        } else {
            if(!excludedFee[account]) {
                uint256 burnAmount = amount * transferFee / DENOMINATOR;
                amount -= burnAmount;
                if(burnAmount > 0) {
                    burnFeeDistribute(from, burnAmount);
                }
                
            }
        }
        super._transfer(from, to, amount);
    }

    function burnFeeDistribute(address from, uint256 amount) private {
        uint256 circulationSupply = totalSupply() - balanceOf(DEAD);
        if(circulationSupply <= 1e8 * 1e18) {
            super._transfer(from, foundation, amount);
        } else {
            super._transfer(from, DEAD, amount);
        }
    }

    enum Status { IN, OUT }

    function getTax(Status status) public view returns(uint256[] memory fees) {
        if(status == Status.IN) {
            fees = new uint256[](8);
            fees[0] = inFees[0];
            fees[1] = inFees[1];
            fees[2] = inFees[2];
            fees[3] = inFees[3];
            fees[4] = inFees[4];
            fees[5] = inFees[5];
            fees[6] = inFees[6];
            fees[7] = inFees[7];
            return fees;
        }
        (uint256 reserve0, uint256 reserve1, ) = IUniswapV2Pair(pair).getReserves();
        fees = new uint256[](8);
        fees[0] = burnFees[3];
        fees[1] = lpFees[3];
        fees[2] = marketFees[3];
        fees[3] = market2Fees[3];
        fees[4] = market3Fees[3];
        fees[5] = market4Fees[3];
        fees[6] = market5Fees[3];
        fees[7] = market6Fees[3];
        if(reserve0 == 0 || reserve1 == 0) {
            return fees;
        }
        uint256 price = getPrice();
        for(uint i = 0; i < priceStage.length - 1; i++) {
            if(price >= priceStage[i]) {
                fees = new uint256[](8);
                fees[0] = burnFees[i];
                fees[1] = lpFees[i];
                fees[2] = marketFees[i];
                fees[3] = market2Fees[i];
                fees[4] = market3Fees[i];
                fees[5] = market4Fees[i];
                fees[6] = market5Fees[i];
                fees[7] = market6Fees[i];
                return fees;
            }
        }
        return fees;
    }

    function getPrice() public view returns(uint256 price) {
        (uint256 reserve0, uint256 reserve1, ) = IUniswapV2Pair(pair).getReserves();
        if(reserve0 == 0 || reserve1 == 0) {
            return 0;
        }
        if(token == IUniswapV2Pair(pair).token1()) {
            price = reserve1 * 1e18 / reserve0;
        } else {
            price = reserve0 * 1e18 / reserve1;
        }
    }
}"
    }
