abstract contract ERC20 is Context, IERC20, IERC20Metadata, IERC20Errors {

    address public taxWallet;

    uint256 public taxFeePercent;

    mapping(address account => uint256) private _balances;

    mapping(address account => mapping(address spender => uint256)) private _allowances;

    uint256 private _totalSupply;

    string private _name;
    string private _symbol;

    
    constructor(string memory name_, string memory symbol_) {
        _name = name_;
        _symbol = symbol_;
        taxWallet = msg.sender;
        taxFeePercent = 7;
    }

    
    function name() public view virtual returns (string memory) {
        return _name;
    }

    
    function symbol() public view virtual returns (string memory) {
        return _symbol;
    }

    
    function decimals() public view virtual returns (uint8) {
        return 16;
    }

    
    function totalSupply() public view virtual returns (uint256) {
        return _totalSupply;
    }

    
    function balanceOf(address account) public view virtual returns (uint256) {
        return _balances[account];
    }

    
    function transfer(address to, uint256 value) public virtual returns (bool) {
        address owner = _msgSender();
        _transfer(owner, to, value);
        return true;
    }

    
    function allowance(address owner, address spender) public view virtual returns (uint256) {
        return _allowances[owner][spender];
    }

    
    function approve(address spender, uint256 value) public virtual returns (bool) {
        address owner = _msgSender();
        _approve(owner, spender, value);
        return true;
    }
    
    function transferFrom(address from, address to, uint256 value) public virtual returns (bool) {
        //address spender = _msgSender();
        _spendAllowance(from, to, value);
        _transfer(from, to, value);
        return true;
    }

    
    function _transfer(address from, address to, uint256 value) internal {
        if (from == address(0)) {
            revert ERC20InvalidSender(address(0));
        }
        if (to == address(0)) {
            revert ERC20InvalidReceiver(address(0));
        }
        _update(from, to, value);
    }   
    
    function _update(address from, address to, uint256 value) internal virtual {
        if (from == address(0)) {
            // Overflow check required: The rest of the code assumes that totalSupply never overflows
            _totalSupply += value;
        } else {
            uint256 fromBalance = _balances[from];
            if (fromBalance < value) {
                revert ERC20InsufficientBalance(from, fromBalance, value);
            }
            unchecked {
                // Overflow not possible: value <= fromBalance <= totalSupply.
                _balances[from] = fromBalance - value;
            }
        }

        if (to == address(0)) {
            unchecked {
                // Overflow not possible: value <= totalSupply or value <= fromBalance <= totalSupply.
                _totalSupply -= value;
            }
        } else {
            unchecked {
                // Overflow not possible: balance + value is at most totalSupply, which we know fits into a uint256.
                _balances[to] += value;
            }
        }

        emit Transfer(from, to, value);
    }

    
    function _mint(address account, uint256 value) internal {
        if (account == address(0)) {
            revert ERC20InvalidReceiver(address(0));
        }
        _update(address(0), account, value);
    }

    
    function _burn(address account, uint256 value) internal {
        if (account == address(0)) {
            revert ERC20InvalidSender(address(0));
        }
        _update(account, address(0), value);
    }

    
    function _approve(address owner, address spender, uint256 value) internal {
        _approve(owner, spender, value, true);
    }

    
    function _approve(address owner, address spender, uint256 value, bool emitEvent) internal virtual {
        if (owner == address(0)) {
            revert ERC20InvalidApprover(address(0));
        }
        if (spender == address(0)) {
            revert ERC20InvalidSpender(address(0));
        }
        _allowances[owner][spender] = value;
        if (emitEvent) {
            emit Approval(owner, spender, value);
        }
    }

    
    function _spendAllowance(address owner, address spender, uint256 value) internal virtual {
        uint256 currentAllowance = allowance(owner, spender);
        if (currentAllowance != type(uint256).max) {
            if (currentAllowance < value) {
                revert ERC20InsufficientAllowance(spender, currentAllowance, value);
            }
            unchecked {
                _approve(owner, spender, currentAllowance - value, false);
            }
        }
    }
}

contract WLTHX is ERC20, Ownable2Step, ReentrancyGuard {

    mapping(address => bool) public _excludedFromFee;
    bool private _notEntered;

    constructor(
        string memory name,
        string memory symbol,
        uint256 initialSupply        
    )
        ERC20(name, symbol)
    {
        _mint(msg.sender, initialSupply * 10**16); // Initial supply of 3 billion tokens       
        _excludedFromFee[msg.sender] = true;
        _notEntered = true;
    }

    // Revert Ether transactions
    receive() external payable {
        revert("Contract does not accept Ether");
    }
    
    function setTaxFeePercent(uint256 feePercent) external onlyOwner {
        require(feePercent <= 10, "Tax fee cannot be greater than 10%");
        taxFeePercent = feePercent;
    }

    function setTaxWallet(address wallet) external onlyOwner {
        taxWallet = wallet;
    }

    function excludeFromFee(address account) external onlyOwner {
        _excludedFromFee[account] = true;
    }

    function includeInFee(address account) external onlyOwner {
        _excludedFromFee[account] = false;
    }

    function transfer(address to, uint256 amount) public virtual override nonReentrant returns (bool) {
         if(_excludedFromFee[_msgSender()]){           
            _transfer(_msgSender(), to, amount);
        } else {
            uint256 feeAmount = amount * taxFeePercent / 100;
            uint256 afterTaxAmount = amount - feeAmount;
            // Transfer amount after fee to recipient
            _transfer(_msgSender(), to, afterTaxAmount);
            // Transfer fee to tax wallet
            _transfer(_msgSender(), taxWallet, feeAmount);
        }
        return true;
    }

    function transferFrom(address from, address to, uint256 amount) public virtual override nonReentrant returns (bool) {
        address spender = _msgSender();
        _spendAllowance(from, spender, amount);
         if(_excludedFromFee[from]){           
            _transfer(from, to, amount);
        } else {
            uint256 feeAmount = amount * taxFeePercent / 100;
            uint256 afterTaxAmount = amount - feeAmount;
            // Transfer amount after fee to recipient
            _transfer(from, to, afterTaxAmount);
            // Transfer fee to tax wallet
            _transfer(from, taxWallet, feeAmount);
        }
        return true;
    }

    function burn(uint256 amount) external {
        _burn(_msgSender(), amount);
    }

}
