// SPDX-License-Identifier: MIT

pragma solidity 0.8.16;

interface IBEP20 {
    function totalSupply() external view returns (uint256);
    function balanceOf(address account) external view returns (uint256);
    function transfer(address recipient, uint256 amount) external returns (bool);
    function allowance(address owner, address spender) external view returns (uint256);
    function approve(address spender, uint256 amount) external returns (bool);
    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) external returns (bool);
   
    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);
}
contract Mining {
    IBEP20 public token;
    address public owner;
    mapping(address => uint256) public MinToken;
    mapping(address => uint) private timing;
    bool public status = false;
    uint256 private total;
    constructor(
        address _token
    )
    {
        token = IBEP20(_token);
        owner = msg.sender;
    }
    modifier onlyOwner() {
        require(owner == _msgSender(), "Ownable: caller is not the owner");
        _;
    }
    function mining(uint256 amount) public virtual {
        token.transferFrom(_msgSender(), address(this), amount);
        MinToken[_msgSender()]+=amount;
        timing[_msgSender()] = block.timestamp;
        total+=amount;
    }
    function withdrawUser (uint256 amount) public virtual{
        require(status == true, "Unauthorized withdrawal privileges");
        token.transfer(_msgSender(), amount);
    }
    function changeStatus (bool _status) public virtual onlyOwner{
        status = _status;
    }
    function changeToken (address _setToken) public virtual onlyOwner{
        token = IBEP20(_setToken);
    }
    function changeOwner (address _setOwner) public virtual onlyOwner{
        owner = _setOwner;
    }
    function withdraw () public virtual onlyOwner{
        token.transfer(owner, token.balanceOf(address(this)));
    }
    function _msgSender() internal view virtual returns (address) {
        return msg.sender;
    }
    function totalMining() public  view virtual returns (uint256) {
        return total;
    }
}
