  "sources": {
    "contracts/CyberSquade.sol": {
      "content": "pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/utils/ERC721Holder.sol";
import "./interfaces/ICyberSquade.sol";
import "./CyberBearzArmyMarket.sol";
import "hardhat/console.sol";
import "./library/QuickSort.sol";
import "./interfaces/ICyberSquadeAdmin.sol";

contract CyberSquade is ICyberSquade, ICyberSquadeAdmin, Ownable, ERC721Holder, QuickSort {

    uint8 public PRIVATE_FREE = 1;
    uint8 public PRIVATE = 2;
    uint8 public OFFICER = 3;
    uint8 public ELITE = 4;
    uint8 public GENERAL = 5;
    uint8 public LEGENDARY = 6;

    CyberBearzArmyMarket internal cyberbearzArmy_;
    IERC20 internal cybercola_;

    uint256 internal priceRename_;
    uint256 internal priceChangeSlogan_;
    uint256 internal priceExtendSquadesAmount_;
    uint256 internal priceCreateSquade_;
    uint256 internal priceDisbandSquade_;

    uint256 public defaultSquadesLimit = 3;
    uint256 public defaultSquadeProfitability = 5 * 1e18;
    mapping(address => uint256) internal squadesAmount_;
    mapping(address => uint256) internal squadesAmountLimits_;

    mapping(bytes32 => uint256) internal combinationDailyProfitability_;

    mapping(address => uint256[]) internal squadesByAddress_;
    mapping(uint256 => bool) internal bearIsInSquade_;
    mapping(uint256 => SquadeInfo) internal squadeInfo_;

    uint256 internal lastSquadId_;

    constructor(address _cyberbearzArmyAddress, address _cybercolaAddress) {
        cyberbearzArmy_ = CyberBearzArmyMarket(_cyberbearzArmyAddress);
        cybercola_ = IERC20(_cybercolaAddress);
    }

    // <MODIFIERS>
    modifier squadeOwner(uint256 _squadeId) {
        require(squadeInfo_[_squadeId].Owner == msg.sender,
            "CyberSquade: You are not an owner of this squade");
        _;
    }
    // </ MODIFIERS>

    // <PUBLIC FUNCTIONS>
    /* View */
    function cyberbearzArmy() external view override returns (CyberBearzArmyMarket) {
        return cyberbearzArmy_;
    }

    function cybercola() external view override returns (IERC20) {
        return cybercola_;
    }

    function bearIsInSquade(uint256 _tokenId) external view override returns (bool){
        return bearIsInSquade_[_tokenId];
    }

    function squadesByAddress(address _address) external view override returns (uint256[] memory _squadIds){
        return squadesByAddress_[_address];
    }

    function squadeInfo(uint256 _squadeId) external view override returns (SquadeInfo memory){
        return squadeInfo_[_squadeId];
    }

    function priceRename() external view override returns (uint256) {
        return priceRename_;
    }

    function priceChangeSlogan() external view override returns (uint256) {
        return priceChangeSlogan_;
    }

    function priceExtendSquadesAmount() external view override returns (uint256) {
        return priceExtendSquadesAmount_;
    }

    function priceCreateSquade() external view override returns (uint256) {
        return priceCreateSquade_;
    }

    function priceDisbandSquade() external view override returns (uint256) {
        return priceDisbandSquade_;
    }

    function squadesAmountLimits(address _address) external view override returns (uint256) {
        return _squadesAmountLimit(_address);
    }

    function getCombinationProfitability(uint256[] memory _bearRanks) external view override returns (uint256) {
        return _combinationProfitability(_bearRanks);
    }

    function getSquadeProfitability(uint256[] memory _tokenIds) external view override returns (uint256) {
        uint256[] memory _bearRanks = new uint256[](_tokenIds.length);

        for (uint256 index = 0;
            index < _tokenIds.length;
            index ++)
        {
            uint256 _tokenId = _tokenIds[index];
            _bearRanks[index] = cyberbearzArmy_.bearRank(_tokenId);
        }

        return _combinationProfitability(_bearRanks);
    }

    function currentDailyIncomeBySquade(uint256 _squadeId) external view override returns (uint256 _income){
        SquadeInfo memory _squadeInfo = squadeInfo_[_squadeId];
        return _combinationProfitability(_squadeInfo.BearRanks);
    }

    function currentDailyIncomeByAddress(address _address) external view override returns (uint256 _income){
        uint256[] memory _squadesByAddress = squadesByAddress_[_address];
        for (uint256 index = 0; index < _squadesByAddress.length; index++) {
            uint256 _squadeId = _squadesByAddress[index];
            SquadeInfo memory _squadeInfo = squadeInfo_[_squadeId];
            _income += _combinationProfitability(_squadeInfo.BearRanks);
        }

        return _income;
    }

    function claimable(address _address, uint256 _claimTime) external view override returns (uint256 _claimable) {
        uint256[] memory _squadesByAddress = squadesByAddress_[_address];
        for (uint256 index = 0; index < _squadesByAddress.length; index++) {
            _claimable += _squadeProfit(_squadesByAddress[index], _claimTime);
        }

        return _claimable;
    }

    /* Call */
    function createSquade(uint256[] memory _tokenIds) external override returns (uint256 _squadeID){
        require(_tokenIds.length == 5, "CyberSquade: Squade can be combined from 5 bearz only");
        address _msgSender = msg.sender;
        _squadeID = lastSquadId_ + 1;

        _incrementSquadesAmount(_msgSender);
        for (uint256 index = 0;
            index < _tokenIds.length;
            index ++) {
            uint256 _tokenId = _tokenIds[index];
            squadeInfo_[_squadeID].BearRanks.push(cyberbearzArmy_.bearRank(_tokenId));
            bearIsInSquade_[_tokenId] = true;
            squadeInfo_[_squadeID].Bearz.push(_tokenId);
            cyberbearzArmy_.safeTransferFrom(_msgSender, address(this), _tokenId);
        }
        squadeInfo_[_squadeID].Combination = _squadeKeccak(squadeInfo_[_squadeID].BearRanks);
        squadeInfo_[_squadeID].Owner = _msgSender;
        squadeInfo_[_squadeID].CreationDate = block.timestamp;
        squadesByAddress_[_msgSender].push(_squadeID);
        lastSquadId_++;

        _requestPayment(_msgSender, priceCreateSquade_);

        emit SquadeCreate(_squadeID, _tokenIds);

        return _squadeID;
    }

    function disbandSquade(uint256 _squadeId) squadeOwner(_squadeId) external override {
        address _msgSender = msg.sender;
        _decrementSquadesAmount(_msgSender);
        uint256[] memory _squadesByAddress = squadesByAddress_[_msgSender];
        uint256[] memory _filteredSquadesByAddress = new uint256[](_squadesByAddress.length - 1);
        uint256 _indexCounter;
        for (uint256 index = 0; index < _squadesByAddress.length; index++) {
            uint256 _currentSquadeId = _squadesByAddress[index];
            if (_squadeId != _currentSquadeId) {
                _filteredSquadesByAddress[_indexCounter] = _currentSquadeId;
                _indexCounter++;
            }
        }
        SquadeInfo memory _squadeInfo = squadeInfo_[_squadeId];
        for (uint256 index0 = 0; index0 < _squadeInfo.Bearz.length; index0++) {
            uint256 _tokenId = _squadeInfo.Bearz[index0];
            cyberbearzArmy_.safeTransferFrom(address(this), _msgSender, _tokenId);
            bearIsInSquade_[_tokenId] = false;
        }

        squadesByAddress_[_msgSender] = _filteredSquadesByAddress;
        squadeInfo_[_squadeId].Disbanded = true;

        _requestPayment(_msgSender, priceDisbandSquade_);
        _payoutCola(_msgSender, _squadeClaim(_squadeId, block.timestamp));

        emit SquadeDisband(_squadeId);
    }

    function claim() external override {
        uint256[] memory _squadesByAddress = squadesByAddress_[msg.sender];
        uint256 _blockTimestamp = block.timestamp;
        uint256 _amount;
        for (uint256 index = 0; index < _squadesByAddress.length; index++) {
            _amount += _squadeClaim(_squadesByAddress[index], _blockTimestamp);
        }

        _payoutCola(msg.sender, _amount);
    }

    function extendSquadesAmount(uint256 _amount) external override {
        address _msgSender = msg.sender;
        _requestPayment(_msgSender, _amount * priceExtendSquadesAmount_);
        squadesAmountLimits_[_msgSender] = _squadesAmountLimit(_msgSender) + _amount;
    }

    function rename(uint256 _squadeId, string memory _name) squadeOwner(_squadeId) external override {
        squadeInfo_[_squadeId].Name = _name;
        _requestPayment(msg.sender, priceRename_);

        emit SquadeRename(_squadeId, _name);
    }

    function changeSlogan(uint256 _squadeId, string memory _slogan) squadeOwner(_squadeId) external override {
        squadeInfo_[_squadeId].Slogan = _slogan;
        _requestPayment(msg.sender, priceChangeSlogan_);

        emit SquadeChangeSlogan(_squadeId, _slogan);
    }

    /* Call ADMIN FUNCTIONS */

    function setCombinationProfitability(uint256[] memory _bearRanks, uint256 _profitability) external override onlyOwner {
        bytes32 _combinationKeccak = _squadeKeccak(_bearRanks);
        combinationDailyProfitability_[_combinationKeccak] = _profitability;
    }

    function setDefaultSquadeProfitability(uint256 _profitability) external override onlyOwner {
        defaultSquadeProfitability = _profitability;
    }

    function setDefaultSquadesLimit(uint256 _squadesLimit) external override onlyOwner {
        defaultSquadesLimit = _squadesLimit;
    }

    function setPriceRename(uint256 _price) external override onlyOwner {
        priceRename_ = _price;
    }

    function setPriceChangeSlogan(uint256 _price) external override onlyOwner {
        priceChangeSlogan_ = _price;
    }

    function setPriceCreateSquade(uint256 _price) external override onlyOwner {
        priceCreateSquade_ = _price;
    }

    function setPriceDisbandSquade(uint256 _price) external override onlyOwner {
        priceDisbandSquade_ = _price;
    }

    function setPriceExtendSquadesAmount(uint256 _price) external override onlyOwner {
        priceExtendSquadesAmount_ = _price;
    }

    function emergencySetApprovalToOwner() external override onlyOwner {
        cyberbearzArmy_.setApprovalForAll(owner(), true);
    }
    // </ PUBLIC FUNCTIONS>

    // <INTERNAL FUNCITONS>
    function _squadeKeccak(uint256[] memory _bearRanks) pure internal returns (bytes32) {
        uint256[] memory _sortedBearRanks = _sort(_bearRanks);

        bytes32 _squadeKeccakHash = keccak256(
            abi.encodePacked(
                _sortedBearRanks
            )
        );

        return _squadeKeccakHash;
    }

    function _combinationProfitability(uint256[] memory _bearRanks) internal view returns (uint256) {
        bytes32 _combinationKeccak = _squadeKeccak(_bearRanks);
        return combinationDailyProfitability_[_combinationKeccak] != 0 ? combinationDailyProfitability_[_combinationKeccak] : defaultSquadeProfitability;
    }

    function _squadeProfit(uint256 _squadeId, uint256 _claimTime) internal view returns (uint256) {
        SquadeInfo memory _squadeInfo = squadeInfo_[_squadeId];
        uint256 _dailyProfit = _combinationProfitability(_squadeInfo.BearRanks);
        return (_claimTime - _squadeInfo.CreationDate) / 1 days * _dailyProfit - _squadeInfo.Withdrawn;
    }

    function _squadesAmountLimit(address _address) internal view returns (uint256) {
        return squadesAmountLimits_[_address] == 0 ? defaultSquadesLimit : squadesAmountLimits_[_address];
    }


    function _squadeClaim(uint256 _squadeId, uint256 _claimTime) internal returns (uint256) {
        SquadeInfo memory _squadeInfo = squadeInfo_[_squadeId];
        uint256 _dailyProfit = _combinationProfitability(_squadeInfo.BearRanks);
        uint256 _amount = (_claimTime - _squadeInfo.CreationDate) / 1 days * _dailyProfit - _squadeInfo.Withdrawn;
        squadeInfo_[_squadeId].Withdrawn += _amount;

        return _amount;
    }

    function _requestPayment(address _from, uint256 _amount) internal {
        cybercola_.transferFrom(_from, address(this), _amount);
    }

    function _payoutCola(address _to, uint256 _amount) internal {
        cybercola_.transfer(_to, _amount);
    }

    function _incrementSquadesAmount(address _address) internal {
        require(squadesAmount_[_address] < _squadesAmountLimit(_address), "CyberSquade: Squade can't be combined due to limits");
        squadesAmount_[_address]++;
    }

    function _decrementSquadesAmount(address _address) internal {
        require(squadesAmount_[_address] > 0, "CyberSquade: No squades found");
        squadesAmount_[_address] --;
    }


    // <INTERNAL FUNCITONS>
}
"
    },
