      "content": "//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.4;

import "../CyberBearzArmyMarket.sol";
import "../Cybercola.sol";

interface ICyberSquade {

    struct SquadeInfo {
        bytes32 Combination;
        address Owner;
        string Name;
        string Slogan;
        uint256[] Bearz;
        uint256[] BearRanks;
        uint256 Withdrawn;
        uint256 CreationDate;
        bool Disbanded;
    }

    event SquadeRename(uint256 squadeId, string name);
    event SquadeChangeSlogan(uint256 squadeId, string slogan);
    event SquadeCreate(uint256 squadeId, uint256[] tokenIds);
    event SquadeDisband(uint256 squadeId);

    function bearIsInSquade(uint256 _tokenId) external view returns (bool);

    function currentDailyIncomeBySquade(uint256 _squadeId) external view returns (uint256 _income);

    function getSquadeProfitability(uint256[] memory _tokenIds) external view returns (uint256);

    function currentDailyIncomeByAddress(address _address) external view returns (uint256 _income);

    function claimable(address _address, uint256 _claimTime) external view returns (uint256 _claimable);

    function cyberbearzArmy() external view returns (CyberBearzArmyMarket);

    function cybercola() external view returns (IERC20);

    function squadeInfo(uint256 _squadeId) external view returns (SquadeInfo memory);

    function squadesByAddress(address _address) external view returns (uint256[] memory _squadIds);

    function priceRename() external view returns (uint256);

    function priceChangeSlogan() external view returns (uint256);

    function priceExtendSquadesAmount() external view returns (uint256);

    function priceCreateSquade() external view returns (uint256);

    function priceDisbandSquade() external view returns (uint256);

    function squadesAmountLimits(address _address) external view returns (uint256);

    function createSquade(uint256[] memory _tokenIds) external returns (uint256 _squadId);

    function disbandSquade(uint256 _squadeId) external;

    function claim() external;

    function rename(uint256 _squadeId, string memory _name) external;

    function changeSlogan(uint256 _squadeId, string memory _slogan) external;

    function extendSquadesAmount(uint256 _amount) external;

    function getCombinationProfitability(uint256[] memory _bearRanks) external view returns (uint256);
}
"
