    "contracts/CyberBearzArmyMarket.sol": {
      "content": "//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.4;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./library/CyberBearzArmy.sol";
import "./library/StakeOwnerExtension.sol";
import "./library/WithdrawExtension.sol";

contract CyberBearzArmyMarket is
    CyberBearzArmy,
    StakeOwnerExtension,
    WithdrawExtension
{
    using SafeMath for uint256;
    using SafeMath for uint16;

    using Strings for uint256;

    /**
     * @dev Get bear rank price
     * param rank ID
     * returns price in wei
     */
    mapping(uint8 => uint256) public bearRankPrice;
    address private inheritedToken;

    event BuyBear(address _owner, uint256 _tokenId, uint8 _rank);

    constructor(
        string memory _baseURI,
        address _stakeOwner,
        address _inheritedToken
    ) CyberBearzArmy(_baseURI) {
        _setStakeOwnerAddress(_stakeOwner);
        _setStakeOwnerStake(5);

        _setBearRankPrice(1, 0);
        _setBearRankPrice(2, (2 * 10**18) / 100); // 0.02 BNB
        _setBearRankPrice(3, (4 * 10**18) / 100); // 0.04 BNB
        _setBearRankPrice(4, (8 * 10**18) / 100); // 0.08 BNB
        _setBearRankPrice(5, (16 * 10**18) / 100); // 0.16 BNB
        _setBearRankPrice(6, (32 * 10**18) / 100); // 0.32 BNB
        inheritedToken = _inheritedToken;
    }

    /**
     * @dev Buy bear
     * @param _rank rank ID
     * @return token ID
     */
    function buyBear(uint8 _rank)
        external
        payable
        rankExists(_rank)
        returns (uint256)
    {
        uint256 msgValue = msg.value;
        address msgSender = msg.sender;
        uint16 _amountMinted = amountMinted[_rank];
        uint16 _amountTotal = amountTotal[_rank];
        require(
            msgValue >= bearRankPrice[_rank],
            "CyberBearzArmyMarket: bear costs more"
        );
        require(
            _amountMinted < _amountTotal,
            "CyberBearzArmyMarket: all bearz in this rank are sold"
        );
        tokenId = tokenId.add(1);
        _amountMinted = _amountMinted + 1;
        amountMinted[_rank] = _amountMinted;
        bearRank[tokenId] = _rank;
        bearTokenIdInRank[tokenId] = _amountMinted;

        payable(stakeOwner).transfer(msgValue.mul(stakeOwnerStake).div(100));

        _mint(msgSender, tokenId);
        emit BuyBear(msgSender, tokenId, _rank);

        return tokenId;
    }

    /**
     * @dev Mint bear
     * @param _rank rank ID
     * @param _to reciever
     * @return token ID
     */
    function mintBear(uint8 _rank, address _to)
        public
        onlyOwner
        rankExists(_rank)
        returns (uint256)
    {
        uint16 _amountMinted = amountMinted[_rank];
        uint16 _amountTotal = amountTotal[_rank];
        require(
            _amountMinted < _amountTotal,
            "CyberBearzArmyMarket: all bearz in this rank are sold"
        );
        tokenId = tokenId.add(1);
        _amountMinted = _amountMinted + 1;
        amountMinted[_rank] = _amountMinted;
        bearRank[tokenId] = _rank;
        bearTokenIdInRank[tokenId] = _amountMinted;

        _mint(_to, tokenId);

        return tokenId;
    }

    /**
     * @dev Mint many bearz
     * @param _rank rank ID
     * @param _to reciever
     * @param _amount amount of bearz to buy
     */
    function mintBearz(
        uint8 _rank,
        uint16 _amount,
        address _to
    ) public onlyOwner rankExists(_rank) {
        uint16 _amountMinted = amountMinted[_rank];
        uint16 _amountTotal = amountTotal[_rank];
        require(
            _amount <= _amountTotal.sub(_amountMinted),
            "CyberBearzArmyMarket: looks like it is not so many bearz are left"
        );
        for (uint256 index = 0; index < _amount; index++) {
            tokenId = tokenId.add(1);
            _amountMinted = _amountMinted + 1;
            amountMinted[_rank] = _amountMinted;
            bearRank[tokenId] = _rank;
            bearTokenIdInRank[tokenId] = _amountMinted;

            _mint(_to, tokenId);
        }
    }

    /**
     * @dev Buy many bearz
     * @param _rank rank ID
     * @param _amount amount of bearz to buy
     */
    function buyBearz(uint8 _rank, uint16 _amount)
        external
        payable
        rankExists(_rank)
    {
        uint256 msgValue = msg.value;
        address msgSender = msg.sender;
        uint16 _amountMinted = amountMinted[_rank];
        uint16 _amountTotal = amountTotal[_rank];
        require(
            _amount == 1 || _rank != 1,
            "CyberBearzArmyMarket: let another users get some free bearz, greedy"
        );
        require(
            _amount <= _amountTotal.sub(_amountMinted),
            "CyberBearzArmyMarket: looks like it is not so many bearz are left"
        );
        require(
            msgValue >= bearRankPrice[_rank] * _amount,
            "CyberBearzArmyMarket: bearz cost more"
        );

        for (uint256 index = 0; index < _amount; index++) {
            tokenId = tokenId.add(1);
            _amountMinted = _amountMinted + 1;
            amountMinted[_rank] = _amountMinted;
            bearRank[tokenId] = _rank;
            bearTokenIdInRank[tokenId] = _amountMinted;

            _mint(msgSender, tokenId);
            emit BuyBear(msgSender, tokenId, _rank);
        }

        payable(stakeOwner).transfer(msgValue.mul(stakeOwnerStake).div(100));
    }

    /**
     * @dev Set bear rank price
     * @param _rank rank ID
     * @param _price price in wei
     */
    function setBearRankPrice(uint8 _rank, uint256 _price) external onlyOwner {
        _setBearRankPrice(_rank, _price);
    }

    function renounceOwnership() public override onlyOwner {}

    /**
     * @dev Check if token is sold
     * @param _tokenId token ID
     * @return token is sold (true if sold/false if not)
     */
    function tokenSold(uint256 _tokenId) external view returns (bool) {
        return _exists(_tokenId);
    }

    function _setBearRankPrice(uint8 _rank, uint256 _price)
        internal
        rankExists(_rank)
    {
        bearRankPrice[_rank] = _price;
    }

    /**
     * Mint tokens to owners of the inherited tokens with the same index
     */
    function mintInheritedTokens(uint32 _start, uint32 _end)
        external
        onlyOwner
    {
        CyberBearzArmy _inheritedToken = CyberBearzArmy(inheritedToken);

        for (uint256 _i = _start; _i <= _end; _i++) {
            address _tokenOwner = _inheritedToken.ownerOf(_i);
            uint8 _rank = _inheritedToken.bearRank(_i);
            mintBear(_rank, _tokenOwner);
        }
    }
}
