import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract Cybercola is ERC20, Ownable {
    bool isInitialized;
    address public addressCyberSquade;

    constructor() ERC20("$CYBERCOLA", "$CYBERCOLA"){
    }

    function initialize(address _addressCyberSquade) external onlyOwner {
        require(!isInitialized, "Cybercola: Contract is already initialized");
        isInitialized = true;

        addressCyberSquade = _addressCyberSquade;
        _mint(owner(), 3_720_000 * 1e18);
        _mint(_addressCyberSquade, 1_240_000 * 1e18);
    }

    function setAddressCyberSquad(address _addressCyberSquade) external onlyOwner {
        addressCyberSquade = _addressCyberSquade;
    }

    function mintExtraColaToCyberSquadeOnly(uint256 _amount) external onlyOwner {
        _mint(addressCyberSquade, _amount);
    }
}
