import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "../interfaces/ICyberBearzArmy.sol";

contract CyberBearzArmy is ICyberBearzArmy, ERC721Enumerable, Ownable {
    using SafeMath for uint256;
    using Strings for uint256;

    uint8 public PRIVATE_FREE = 1;
    uint8 public PRIVATE = 2;
    uint8 public OFFICER = 3;
    uint8 public ELITE = 4;
    uint8 public GENERAL = 5;
    uint8 public LEGENDARY = 6;

    /**
     * @dev Stores amount of tokens minted in specific rank.
     * param rank ID
     * returns amount of bearz sold
     */
    mapping(uint8 => uint16) public amountMinted; // amount of bearz sold (rank: amount)

    /**
     * @dev Stores amount of tokens that can be sold in specific rank.
     * param rank ID
     * returns amount of bearz can available for sale
     */
    mapping(uint8 => uint16) public amountTotal; // amount of bearz can be sold (rank: amount_total)

    /**
     * @dev Get bear rank by token ID
     * param token ID
     * returns rank ID
     */
    mapping(uint256 => uint8) public bearRank; // bear token rank (tokenId: rankId)

    /**
     * @dev Get bear token ID in specific rank
     * param token ID (in contract)
     * returns token ID (in rank)
     */
    mapping(uint256 => uint16) public bearTokenIdInRank; // bear token id in rank category (tokenId: tokenId_in_bear_rank)

    /**
     * @dev Domain/path where tokens' metadata is stored
     */
    string public baseURI; // Base URI

    /**
     * @dev Last token ID
     */
    uint256 public tokenId = 0;

    modifier rankExists(uint8 _rank) {
        require(0 < _rank && _rank <= 6, "Rank doesn't exist");
        _;
    }

    constructor(string memory _baseURI) ERC721("CYBERBEARZ ARMY", "BRZ") {
        _setBaseURI(_baseURI);
        _setAmountTotal(1, 512);
        _setAmountTotal(2, 2048);
        _setAmountTotal(3, 768);
        _setAmountTotal(4, 512);
        _setAmountTotal(5, 192);
        _setAmountTotal(6, 64);
    }

    /**
     * @dev Sets domain/path where tokens' metadata is stored.
     * @param _baseUri domain/path
     */
    function setBaseURI(string memory _baseUri) external override onlyOwner {
        _setBaseURI(_baseUri);
    }

    /**
     * @dev Returns token URI.
     * @param _tokenId token ID
     * @return token metadata URI
     */
    function tokenURI(uint256 _tokenId)
        public
        view
        virtual
        override
        returns (string memory)
    {
        string memory _baseUri = _baseURI();
        if (_exists(_tokenId)) {
            return
                bytes(_baseUri).length > 0
                    ? string(
                        abi.encodePacked(
                            _baseUri,
                            Strings.toString(bearRank[_tokenId]),
                            "/",
                            Strings.toString(bearTokenIdInRank[_tokenId]),
                            "/index.json"
                        )
                    )
                    : "";
        } else {
            return
                bytes(_baseUri).length > 0
                    ? string(
                        abi.encodePacked(
                            _baseUri,
                            "cyberbearzarmy-bear-wait.json"
                        )
                    )
                    : "";
        }
    }

    /**
     * @dev Get array of tokenNumbers for the _owner
     * @param _owner address to get tokens by
     * @return list of token IDs
     */
    function getBearzOfOwner(address _owner)
        external
        view
        override
        returns (uint256[] memory)
    {
        uint256 tokenCount = balanceOf(_owner);
        if (tokenCount == 0) {
            return new uint256[](0);
        } else {
            uint256[] memory result = new uint256[](tokenCount);
            uint256 index;
            for (index = 0; index < tokenCount; index++) {
                result[index] = tokenOfOwnerByIndex(_owner, index);
            }
            return result;
        }
    }

    /**
     * @dev Set amount of tokens which can be minted in specific rank
     * @param _rank rank ID
     * @param _amountTotal amount (has to be larger than amount of minted tokens)
     */
    function setAmountTotal(uint8 _rank, uint16 _amountTotal)
        external
        override
        onlyOwner
    {
        _setAmountTotal(_rank, _amountTotal);
    }

    function _setAmountTotal(uint8 _rank, uint16 _amountTotal)
        internal
        rankExists(_rank)
    {
        require(
            _amountTotal >= amountMinted[_rank],
            "CyberBearzArmy: minted bearz amount is larger than this total limit"
        );
        amountTotal[_rank] = _amountTotal;
    }

    function _setBaseURI(string memory _baseUri) internal {
        baseURI = _baseUri;
    }

    function _baseURI() internal view override returns (string memory) {
        return baseURI;
    }

    function _minted(uint256 _tokenId) internal view returns (bool) {
        address _tokenOwner = ownerOf(_tokenId);
        if (_tokenOwner != address(0) && _tokenOwner != address(this)) {
            return true;
        }
        return false;
    }
}
"
    },
