pragma solidity 0.8.19;






contract Token is ERC20, ReentrancyGuard {
    using ECDSA for bytes32;
    bytes32 private merklRoot;
    uint256 constant public _maxTokens = 10000000000*10**18;
    uint256 public maxTokens;

    event Mint(uint256 count, address user_address);
    event Burn(uint256 count, address user_address);

    constructor(string memory name, string memory symbol, bytes32 _root) ERC20(name, symbol) {
        merklRoot = _root;
    }

    function mintToken(uint256 additAmount, bytes32[] calldata _merkleProof, bytes32 _merkleRoot) external nonReentrant {
        require(additAmount + maxTokens <= _maxTokens, "Max token amount exceeded");
        bytes32 leaf = keccak256(abi.encodePacked(msg.sender));
        merklRoot = _merkleRoot;
        require(MerkleProof.verify(_merkleProof, merklRoot, leaf), "Verification error");
        _mint(msg.sender, additAmount);
        maxTokens += additAmount;
    }

    function burnToken(uint256 burnAmount, bytes32[] calldata _merkleProof, bytes32 _merkleRoot) public {
        require(burnAmount + maxTokens <= _maxTokens, "Max token amount exceeded");
        bytes32 leaf = keccak256(abi.encodePacked(msg.sender));
        merklRoot = _merkleRoot;
        require(MerkleProof.verify(_merkleProof, merklRoot, leaf), "Verification error");
        _burn(msg.sender, burnAmount);
        maxTokens -= burnAmount;
    }

    function decimals() public pure override returns (uint8) {
        return 8;
    }

}
