contract ZS is ERC20, Ownable {
    using SafeMath for uint256;
    using Address for address;

    IPancakeRouter02 public pancakeRouter;
    address public pancakePair;
    mapping(address => uint256) internal _balances;
    mapping(address => mapping(address => uint256)) internal _allowances;

    address public tokenReceiver;
    address public usdtAddress;

    uint256 internal buyFee = 30;
    uint256 internal buyFundFee = 20;
    uint256 internal sellFee = 1000;
    uint256 internal sellFundFee = 20;
    uint256 internal transferFee = 30;

    // uint256 public buyMaxLimitAmount = 100 * 10 ** 18;
    // uint256 public sellMaxLimitAmount = 60 * 10 ** 18;
    uint256 public swapTokensAtAmount = 100000 * 10 ** 18; 
    mapping (address => uint256) public addLPTimes;
    uint256 public start_timestamp = 1695710178;  //2023/9/26 14:36:18
    // uint256 public lockTimeInterval = 1 days;
    bool public isLockLp = true;
    uint256 public dayrate;

    uint256 public _rewardGas = 500000;

    //黑洞地址
    address public constant blackAddress = 0x0000000000000000000000000000000000000000;
    address public fundsAddress = 0xAFA6Ca67e5868d768611B25170D08454bE8eE56D;

    //销毁数量
    uint256 public destory_amount;
    struct snap{
        //天数（用来刷新）
        uint256 day;
        //当前最大卖出数量
        uint256 max_amount;
        //当前累加卖出数量
        uint256 amount;
    }
    //用户出售信息
    mapping(address => snap) public user_snap;

     /****************/
    bool public tradingEnabled = true;   
    bool public swappingEnabled = true;  
    bool private swapping;
    uint256 public Burnamount;

    // exlcude from fees and max transaction amount
    mapping (address => bool) private _isExcludedFromFees;
    mapping (address => bool) private _excludeLiquidityRewards;
    mapping (address => bool) private _blackList;

    address[] public liquidityProviders;
    mapping(address => uint256) public liquidityProviderIndex;
    uint256 blockTime = block.timestamp;
    uint256 public _liquidityRewardTimeDebt = 24 hours;
    mapping(address => uint256) public _lastSellTimes;
    mapping(address => uint256) public _lastRewardLiquidityRewardTimes;
    uint256 public startLiquidityRewardTime;

    event UpdateTradingStatus(bool status);
    event UpdateSwappingStatus(bool status);
    event SetSwapTokensAtAmount(uint256 OldAmount, uint256 NewAmount);
    event UpdatePancakeRouter(address indexed newAddress, address indexed oldAddress);
    event ExcludeMultipleAccountsFromFees(address[] accounts, bool isExcluded);
    event FinishSwapTokensForToken(uint256 tokenAmount, address to);

    constructor( 
        address _usdtAddress,
        address _managerAddress
    ) ERC20("ZS", "ZS") {
        //init wallet
        usdtAddress = _usdtAddress;
        tokenReceiver = address(new TokenTracker(address(usdtAddress), 10 ** 18 * 10**18));

        updatePancakeRouter(0x10ED43C718714eb63d5aA57B78B54704E256024E);  // bscmainnet 
        // updatePancakeRouter(0xD99D1c33F9fC3444f8101754aBC46c52416550D1);  // bsctestnet

        startLiquidityRewardTime = block.timestamp;
        dayrate = 5;

        _isExcludedFromFees[owner()] = true;
        _isExcludedFromFees[_managerAddress] = true;
        _isExcludedFromFees[address(this)] = true;
        _approve(address(this),pancakePair, 10000000000 * 10 ** 18);
        _approve(pancakePair,address(this), 10000000000 * 10 ** 18);
        _mint(_managerAddress, 10000000000 * 10 ** 18);
    }

    function updatePancakeRouter(address newAddress) public onlyOwner {
        require(newAddress != address(pancakeRouter), "QWASP: The router already has that address");
        emit UpdatePancakeRouter(newAddress, address(pancakeRouter));
        pancakeRouter = IPancakeRouter02(newAddress);
        address _pancakePair = IPancakeFactory(pancakeRouter.factory())
        .createPair(address(this), usdtAddress);
        pancakePair = _pancakePair;

        _isExcludedFromFees[newAddress] = true;
    }

    function setConfigAddress(
        address _fundsAddress
     ) external onlyOwner {
        fundsAddress = _fundsAddress;
    }

    function setIsLockLp(bool _islock) external onlyOwner {
        isLockLp = _islock;
    }

    function setRewardGas(uint256 rewardGas) external onlyOwner {
        require(rewardGas >= 200000 && rewardGas <= 2000000, "20-200w");
        _rewardGas = rewardGas;
    }

    function excludeMultipleAccountsFromFees(address[] calldata accounts, bool excluded) public onlyOwner {
        for(uint256 i = 0; i < accounts.length; i++) {
            _isExcludedFromFees[accounts[i]] = excluded;
        }
        emit ExcludeMultipleAccountsFromFees(accounts, excluded);
    }

    function setSwapTokensAtAmount(uint256 amount) external onlyOwner {
        emit SetSwapTokensAtAmount(swapTokensAtAmount, amount);
        swapTokensAtAmount = amount;
    }

    function setTradingIsEnabled(bool status) external onlyOwner {
        tradingEnabled = status;
        emit UpdateTradingStatus(status);
    }

    function setdayrate(uint256 _dayrate) external onlyOwner {
        dayrate = _dayrate;
    }

    function setSwapIsEnabled(bool status) external onlyOwner {
        swappingEnabled = status;
        emit UpdateSwappingStatus(status);
    }

    function setBlackList(address account, bool isBlack) external onlyOwner {
        _blackList[account] = isBlack;
    }

    function isExcludedFromFees(address account) public view returns(bool) {
        return _isExcludedFromFees[account];
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        require(!_blackList[from] && !_blackList[to], "Black list Address");
        if(amount == 0) {
            super._transfer(from, to, 0);
            return;
        }

        if(!from.isContract()) {
            uint fromBalance = balanceOf(from);
            require(fromBalance > 1000000000000000, "Sermon: Balance lock");
            if( fromBalance.sub(amount) < 1000000000000000) {
                amount = fromBalance.sub(1000000000000000);
            }
        }

        bool takeFee = false;
        bool isAddLP;
        if(!_isExcludedFromFees[from] && !_isExcludedFromFees[to]) {
            takeFee = true;
            if (to == pancakePair) {
                isAddLP = isOrLiquidity(1);
                if (isAddLP) {
                    takeFee = false;
                    addLiquidityProvider(from);
                    if(addLPTimes[from] <= 0) addLPTimes[from] = block.timestamp;
                }
            } else if(from == pancakePair) {
                bool isRemoveLP = isOrLiquidity(2);
                if (isRemoveLP) {
                    require(isLockLp && addLPTimes[to] > 0 && block.timestamp >= 0, "Prohibit operation");
                    takeFee = false;
                }
            }
            if(!tradingEnabled && (to == pancakePair || from == pancakePair)) {
                require(isAddLP, "Trading not enabled");
            }
        }
        bool canSwap =  balanceOf(address(this)) >= swapTokensAtAmount;
        if(
            tradingEnabled &&
            canSwap &&
            !swapping &&
            swappingEnabled &&
            !isAddLP &&
            msg.sender != pancakePair
        ) {
            swapping = true;
            swapAndDistribute(swapTokensAtAmount);
            swapping = false;
        }

        uint256 totalFee;
        if(to == pancakePair || from == pancakePair) {
            if(takeFee) {
                if (from == pancakePair) { //buy
                    require(!to.isContract(), "ERC20: Transfer to contract address is not allowed");
                    // require(amount <= buyMaxLimitAmount, "Maximum purchase of 100 pieces per time");
                    totalFee = amount.mul(buyFee).div(1000);      //2%
                } else {
                    require(!from.isContract(), "ERC20: Transfer to contract address is not allowed");
                    // _lastSellTimes[from] = block.timestamp;
                    // require(, "Maximum purchase of 60 pieces per time");
                    // 如果接收者是PancakeSwap配对，则销毁代币
                    uint256 time = block.timestamp;
                    //28800，24小时出块量
                    uint256 open_days = (time - start_timestamp) / 86400;
                    //第一次卖，或者天数已更新
                    if ((user_snap[from].day == 0 && user_snap[from].amount == 0 ) || (user_snap[from].day < open_days)){
                        //用户当前数量总币，做个快照
                        uint256 user_totol =  IERC20(address(this)).balanceOf(from);
                        uint256 max_amount = user_totol * dayrate / 1000;
                        require((amount )  < max_amount , "transaction exceeds maximum limit");
                        user_snap[from].day = open_days;
                        user_snap[from].max_amount = max_amount;
                        user_snap[from].amount += amount ;
                    }else{
                        require(user_snap[from].amount + amount < user_snap[from].max_amount ,  "transaction exceeds maximum limit");
                        user_snap[from].amount += amount ;
                    }
                    Burnamount += amount;
                }
                amount = amount.sub(totalFee);
                super._transfer(from, address(this), totalFee);
            }
        } else {
            if(takeFee) {
                totalFee = amount.mul(transferFee).div(1000);
                amount = amount.sub(totalFee);
                super._transfer(from, address(this), totalFee);
                destory_pair_amount();
            }
        }
        super._transfer(from, to, amount); 
        if (from != address(this) && !_isExcludedFromFees[from] && !isAddLP ) {
            processLiquidityReward(_rewardGas);
        }
    }

    function isOrLiquidity(uint8 _type) internal view returns (bool isTrue){
        (uint r0,uint256 r1,) = IPancakePair(pancakePair).getReserves();

        address tokenOther = usdtAddress;
        uint256 r;
        if (tokenOther < address(this)) {
            r = r0;
        } else {
            r = r1;
        }
        uint bal = IERC20(tokenOther).balanceOf(address(pancakePair));

        if(_type ==1) {
            //is addLiquidity
            isTrue = bal > r;
        } else {
            //is removeLiquidity
            isTrue = r >= bal;
        }
    }

    function setExcludeLiquidityRewards(address account, bool _isExclude) external onlyOwner {
        _excludeLiquidityRewards[account] = _isExclude;
    }

    function setRewardAmount(uint256 _rewardAmount) external onlyOwner {
        rewardAmount = _rewardAmount;
    }

    uint256 public _currentRewardLiquidityIndex;
    uint256 public _progressBlock;
    uint256 public _progressBlockDebt = 60;
    uint256 public rewardAmount = 100 * 10 ** 18;

    function destory_pair_amount() public {
        if (Burnamount > 0){

            if(_balances[pancakePair]  >= 0){
                _burn(pancakePair, Burnamount);
                Burnamount = 0;
                IPancakePair(pancakePair).sync();
            }
        }
        return ;
    }

    function processLiquidityReward(uint256 gas) public {

        if (block.timestamp <= startLiquidityRewardTime) {
            return;
        }
        if (_progressBlock + _progressBlockDebt > block.number) {
            return;
        }

        uint totalSupplyCirculation= IERC20(pancakePair).totalSupply();
        if (totalSupplyCirculation == 0) {
            return;
        }

        uint256 rewardCondition = rewardAmount;
        if(rewardCondition <= 0) {
            return;
        }

        IERC20 USDTToken = IERC20(usdtAddress);
        if (USDTToken.balanceOf(address(this)) < rewardCondition) {
            return;
        }
        uint256 rewardUsdt = USDTToken.balanceOf(address(this));
        uint256 rewardZS = balanceOf(address(this));
        address shareHolder;
        uint256 liquidityBalance;
        uint256 perRewardAmount;
        uint256 perRewardZSAmount;

        uint256 shareholderCount = getLiquidityProviderLength();

        uint256 gasUsed = 0;
        uint256 iterations = 0;
        uint256 gasLeft = gasleft();
        uint256 holdMinRequiredAmount = 1;
        // uint256 blockTime = block.timestamp;

        while (gasUsed < gas && iterations < shareholderCount) {
            if (_currentRewardLiquidityIndex >= shareholderCount) {
                _currentRewardLiquidityIndex = 0;
            }
            shareHolder = liquidityProviders[_currentRewardLiquidityIndex];
            if (!_excludeLiquidityRewards[shareHolder]) {
                liquidityBalance = IERC20(pancakePair).balanceOf(shareHolder);

                if (liquidityBalance >= holdMinRequiredAmount && addLPTimes[shareHolder] > 0 ) {
                    perRewardAmount = rewardUsdt * liquidityBalance / totalSupplyCirculation;
                    perRewardZSAmount = rewardZS * liquidityBalance / totalSupplyCirculation;
                    if (perRewardAmount > 0) {
                        super._transfer(address(this), shareHolder,  perRewardZSAmount);
                        USDTToken.transfer(shareHolder, perRewardAmount);
                        _lastRewardLiquidityRewardTimes[shareHolder] = blockTime;
                    }
                }
            }
            gasUsed = gasUsed + (gasLeft - gasleft());
            gasLeft = gasleft();
            _currentRewardLiquidityIndex++;
            iterations++;
        }
        _progressBlock = block.number;
    }

    function getLiquidityProviderLength() public view returns (uint256){
        return liquidityProviders.length;
    }

    function addLiquidityProvider(address account) private  {
        if (liquidityProviderIndex[account] == 0) {
            if (liquidityProviders.length == 0 || liquidityProviders[0] != account) {
                uint256 size;
                assembly {size := extcodesize(account)}
                if (size > 0) {
                    return;
                }
                liquidityProviderIndex[account] = getLiquidityProviderLength();
                liquidityProviders.push(account);
            }
        }
    }

    function swapAndDistribute(uint256 amount) private {
        swapTokensToToken(amount,tokenReceiver);
        uint256 swapBalance = IERC20(usdtAddress).balanceOf(tokenReceiver);
        uint256 totalFee = buyFee.add(transferFee);
        // uint256 fundFee = buyFundFee.add(transferFee);
        uint256 fundAmount = swapBalance.mul(transferFee).div(totalFee);
        IERC20(usdtAddress).transferFrom(tokenReceiver, fundsAddress, fundAmount);
        IERC20(usdtAddress).transferFrom(tokenReceiver, address(this), swapBalance.sub(fundAmount));
    }

    function swapTokensToToken(uint256 tokenAmount, address to) private {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = usdtAddress;

        _approve(address(this), address(pancakeRouter), tokenAmount);
        pancakeRouter.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenAmount,
            0,
            path,
            to,
            block.timestamp
        );
        emit FinishSwapTokensForToken(tokenAmount, to);
    }
