interface IStakeERC721EarnDMD {
    event Harvest(address indexed user, address indexed poolAddress, uint256 amount);
    event Stake(address indexed user, address indexed poolAddress, uint256 indexed tokenId, uint256 amount);
    event Unstake(address indexed user, address indexed poolAddress, uint256 indexed tokenId, uint256 amount);
    event EmergencyUnstake(address indexed user, address indexed poolAddress, uint256 indexed tokenId, uint256 amount);
    event SetDMDPerSecond(address indexed user, uint256 DMDPerSecond);
    function poolLength() external view returns (uint256);

    function add(
        uint256 _allocPoint,
        address _pair,
        bool _withUpdate
    ) external;

    function set(address _pair, uint256 _allocPoint) external;
    function setlastRewardTime(address _pair, uint256 _lastRewardTime) external;
    function setDMDPerSecond(uint256 _DMDPerSecond) external;
    function getTotalReward(uint256 _from, uint256 _to) external view returns (uint256 totalReward);
    function pendingToken(address _pair, address _user) external view returns (uint256);
    function massUpdatePools() external;
    function updatePool(address _pair) external;
    function harvest(address _pair) external;
    function stake(address _pair, uint256 _tokenId) external;
    function batchStake(address _pair, uint256[] calldata _tokenIds) external;
    function unstake(address _pair, uint256 _tokenId) external;
    function batchUnstake(address _pair, uint256[] calldata _tokenIds) external;
    function unstakeAll(address _pair) external;
    function emergencyUnstake(address _pair, uint256 _tokenId) external;
    function emergencyUnstakeAll(address _pair) external;
    function pauseStake() external;
    function unpauseStake() external;
}

pragma solidity 0.6.12;

contract StakeERC721EarnDMD is IStakeERC721EarnDMD, ERC721Holder, Ownable, Pausable, ReentrancyGuard {
    using SafeERC20 for IERC20;
    using SafeERC20 for ERC20;
    using SafeMath for uint256;
    using Address for address;
    using EnumerableSet for EnumerableSet.UintSet;

    // Info of each user.
    struct UserInfo {
        uint256 stakingAbility;
        uint256 rewardDebt;
    }

    // Info of each pool.
    struct PoolInfo {
        uint256 allocPoint; // How many allocation points assigned to this pool. TOKENs to distribute per block.
        uint256 lastRewardTime; // Last block.timestamp that TOKENs distribution occurs.
        uint256 accTokenPerShare; // Accumulated TOKENs per share, times 1e12. See below.
        uint256 totalstakingAbility; // total has deposit to pool stakingAbility
        bool exists; //
    }
    // Total allocation points. Must be the sum of all allocation points in all pools.
    uint256 public totalAllocPoint = 0;
    // The block.timestamp when TOKEN mining starts.
    uint256 public startTime;
    // TOKEN tokens created per block.
    uint256 public DMDPerSecond;
    // Accumulated TOKENs per share, times 1e12.
    uint256 public constant accTokenPerShareMultiple = 1E20;
    uint256 public constant ONE_ABILITY_EIGHTEEN = 1E18;
    // minted TOKEN amount
    uint256 public mintedAmount = 0;
    // The TOKEN TOKEN!
    ERC20 public token;
    address[] public poolAddresses;
    // Info of each pool.
    mapping(address => PoolInfo) public poolInfoMap;
    mapping(address => mapping(uint256 => uint256)) public nftTokenstakingAbilityMap;
    mapping(address => mapping(address => UserInfo)) private _poolUserInfoMap;
    mapping(address => mapping(address => EnumerableSet.UintSet)) private _poolUserStakingTokens;

    constructor(
        address _token,
        uint256 _startTime,
        uint256 _DMDPerSecond
    ) public {
        token = ERC20(_token);
        startTime = _startTime;
        DMDPerSecond = _DMDPerSecond;
    }

    function setstartTime(uint256 _startTime) public onlyOwner {
        require(block.timestamp <= _startTime && startTime >= block.timestamp, 'FORBIDDEN');
        startTime = _startTime;
    }

    // *** POOL MANAGER ***
    function poolLength() external view override returns (uint256) {
        return poolAddresses.length;
    }

    // Add a new lp to the pool. Can only be called by the owner.
    // XXX DO NOT add the same LP token more than once. Rewards will be messed up if you do.
    function add(
        uint256 _allocPoint,
        address _pair,
        bool _withUpdate
    ) external override onlyOwner {
        if (_withUpdate) {
            massUpdatePools();
        }
        PoolInfo storage pool = poolInfoMap[_pair];
        require(!pool.exists, 'pool already exists');
        uint256 lastRewardTime = block.timestamp > startTime ? block.timestamp : startTime;
        totalAllocPoint = totalAllocPoint.add(_allocPoint);
        poolInfoMap[_pair] = PoolInfo({
            allocPoint: _allocPoint,
            lastRewardTime: lastRewardTime,
            accTokenPerShare: 0,
            totalstakingAbility: 0,
            exists: true
        });
        poolAddresses.push(_pair);
    }

    // Update the given pool's TOKEN allocation point. Can only be called by the owner.
    function set(address _pair, uint256 _allocPoint) external override onlyOwner {
        PoolInfo storage pool = poolInfoMap[_pair];
        require(pool.exists, 'pool not exists');
        massUpdatePools();
        totalAllocPoint = totalAllocPoint.sub(pool.allocPoint).add(_allocPoint);
        pool.allocPoint = _allocPoint;
    }

    function setlastRewardTime(address _pair, uint256 _lastRewardTime) external override onlyOwner {
        PoolInfo storage pool = poolInfoMap[_pair];
        require(pool.exists, 'POOL NOT EXISTS');
        require(
            pool.accTokenPerShare == 0 && _lastRewardTime >= block.timestamp && pool.lastRewardTime >= block.timestamp,
            'err'
        );
        pool.lastRewardTime = _lastRewardTime;
    }

    function setDMDPerSecond(uint256 _DMDPerSecond) external override onlyOwner {
        require(DMDPerSecond != _DMDPerSecond, ' NOT NEED UPDATE');
        massUpdatePools();
        emit SetDMDPerSecond(_msgSender(), _DMDPerSecond);
        DMDPerSecond = _DMDPerSecond;
    }

    // Return total reward over the given _from to _to block.
    function getTotalReward(uint256 _from, uint256 _to) public view override returns (uint256 totalReward) {
        if (_to <= startTime) {
            return 0;
        }
        if (_from < startTime) {
            _from = startTime;
        }
        return _to.sub(_from).mul(DMDPerSecond);
    }

    // View function to see pending TOKENs on frontend.
    function pendingToken(address _pair, address _user) external view override returns (uint256) {
        PoolInfo memory pool = poolInfoMap[_pair];
        if (!pool.exists) {
            return 0;
        }
        UserInfo storage userInfo = _poolUserInfoMap[_pair][_user];
        uint256 accTokenPerShare = pool.accTokenPerShare;
        uint256 stakingAbility = pool.totalstakingAbility.mul(10**uint256(token.decimals())).div(ONE_ABILITY_EIGHTEEN);
        if (block.timestamp > pool.lastRewardTime && stakingAbility != 0) {
            uint256 totalReward = getTotalReward(pool.lastRewardTime, block.timestamp);
            uint256 tokenReward = totalReward.mul(pool.allocPoint).div(totalAllocPoint);
            accTokenPerShare = accTokenPerShare.add(tokenReward.mul(accTokenPerShareMultiple).div(stakingAbility));
        }
        return
            userInfo
                .stakingAbility
                .mul(accTokenPerShare)
                .mul(10**uint256(token.decimals()))
                .div(ONE_ABILITY_EIGHTEEN)
                .div(accTokenPerShareMultiple)
                .sub(userInfo.rewardDebt);
    }

    // Update reward vairables for all pools. Be careful of gas spending!
    function massUpdatePools() public override {
        uint256 length = poolAddresses.length;
        for (uint256 i = 0; i < length; ++i) {
            updatePool(poolAddresses[i]);
        }
    }

    function setNftTokenstakingAbility(
        address _token,
        uint256 _tokenId,
        uint256 _stakingAbility
    ) public onlyOwner {
        nftTokenstakingAbilityMap[_token][_tokenId] = _stakingAbility;
    }

    function setNftTokenstakingAbilityFromTo(
        address _token,
        uint256 _fromTokenId,
        uint256 _toTokenId,
        uint256 _stakingAbility
    ) public onlyOwner {
        for (; _fromTokenId <= _toTokenId; _fromTokenId++) {
            setNftTokenstakingAbility(_token, _fromTokenId, _stakingAbility);
        }
    }

    function batchsetNftTokenstakingAbility(
        address _token,
        uint256[] memory _tokenIds,
        uint256[] memory _stakingAbilitys
    ) public onlyOwner {
        require(_tokenIds.length == _stakingAbilitys.length, 'LENGTH ERROR');
        for (uint256 i = 0; i < _tokenIds.length; i++) {
            setNftTokenstakingAbility(_token, _tokenIds[i], _stakingAbilitys[i]);
        }
    }

    // Update reward variables of the given pool to be up-to-date.
    function _updatePool(PoolInfo storage pool) internal {
        if (!pool.exists || block.timestamp <= pool.lastRewardTime) {
            return;
        }
        uint256 stakingAbility = pool.totalstakingAbility.mul(10**uint256(token.decimals())).div(ONE_ABILITY_EIGHTEEN);
        if (stakingAbility == 0) {
            pool.lastRewardTime = block.timestamp;
            return;
        }
        uint256 totalReward = getTotalReward(pool.lastRewardTime, block.timestamp);
        uint256 tokenReward = totalReward.mul(pool.allocPoint).div(totalAllocPoint);
        token.mint(address(this), tokenReward);
        mintedAmount = mintedAmount.add(tokenReward);
        pool.accTokenPerShare = pool.accTokenPerShare.add(tokenReward.mul(accTokenPerShareMultiple).div(stakingAbility));
        pool.lastRewardTime = block.timestamp;
    }

    function updatePool(address _pair) public override {
        _updatePool(poolInfoMap[_pair]);
    }

    function _harvest(
        address _pair,
        PoolInfo storage pool,
        UserInfo storage userInfo
    ) internal {
        _updatePool(pool);
        if (userInfo.stakingAbility != 0) {
            uint256 pending = userInfo
                .stakingAbility
                .mul(pool.accTokenPerShare)
                .mul(10**uint256(token.decimals()))
                .div(ONE_ABILITY_EIGHTEEN)
                .div(accTokenPerShareMultiple)
                .sub(userInfo.rewardDebt);
            if (pending != 0) {
                safeTokenTransfer(_msgSender(), pending);
                emit Harvest(_msgSender(), _pair, pending);
            }
        }
    }

    function harvest(address _pair) external override nonReentrant {
        PoolInfo storage pool = poolInfoMap[_pair];
        UserInfo storage userInfo = _poolUserInfoMap[_pair][_msgSender()];
        _harvest(_pair, pool, userInfo);
        userInfo.rewardDebt = userInfo
            .stakingAbility
            .mul(pool.accTokenPerShare)
            .mul(10**uint256(token.decimals()))
            .div(ONE_ABILITY_EIGHTEEN)
            .div(accTokenPerShareMultiple);
    }

    // Stake LP tokens to TokenMaster for TOKEN allocation.
    function stake(address _pair, uint256 _tokenId) public override nonReentrant whenNotPaused {
        PoolInfo storage pool = poolInfoMap[_pair];
        UserInfo storage userInfo = _poolUserInfoMap[_pair][_msgSender()];
        _harvest(_pair, pool, userInfo);
        uint256 stakingAbility = nftTokenstakingAbilityMap[_pair][_tokenId];
        IERC721(_pair).safeTransferFrom(_msgSender(), address(this), _tokenId);
        if (stakingAbility == 0) {
            stakingAbility = ONE_ABILITY_EIGHTEEN;
            nftTokenstakingAbilityMap[_pair][_tokenId] = stakingAbility;
        }
        userInfo.stakingAbility = userInfo.stakingAbility.add(stakingAbility);
        _poolUserStakingTokens[_pair][_msgSender()].add(_tokenId);
        pool.totalstakingAbility = pool.totalstakingAbility.add(stakingAbility);
        userInfo.rewardDebt = userInfo
            .stakingAbility
            .mul(pool.accTokenPerShare)
            .mul(10**uint256(token.decimals()))
            .div(ONE_ABILITY_EIGHTEEN)
            .div(accTokenPerShareMultiple);
        emit Stake(_msgSender(), _pair, _tokenId, stakingAbility);
    }

    function batchStake(address _pair, uint256[] calldata _tokenIds) external override whenNotPaused {
        for (uint256 i = 0; i < _tokenIds.length; i++) {
            stake(_pair, _tokenIds[i]);
        }
    }

    // Unstake LP tokens from TokenMaster.
    function unstake(address _pair, uint256 _tokenId) public override nonReentrant {
        require(_poolUserStakingTokens[_pair][_msgSender()].contains(_tokenId), 'UNSTAKE FORBIDDEN');
        PoolInfo storage pool = poolInfoMap[_pair];
        UserInfo storage userInfo = _poolUserInfoMap[_pair][_msgSender()];
        _harvest(_pair, pool, userInfo);
        uint256 stakingAbility = nftTokenstakingAbilityMap[_pair][_tokenId];
        userInfo.stakingAbility = userInfo.stakingAbility.sub(stakingAbility);
        _poolUserStakingTokens[_pair][_msgSender()].remove(_tokenId);
        IERC721(_pair).safeTransferFrom(address(this), _msgSender(), _tokenId);
        pool.totalstakingAbility = pool.totalstakingAbility.sub(stakingAbility);
        userInfo.rewardDebt = userInfo
            .stakingAbility
            .mul(pool.accTokenPerShare)
            .mul(10**uint256(token.decimals()))
            .div(ONE_ABILITY_EIGHTEEN)
            .div(accTokenPerShareMultiple);
        emit Unstake(_msgSender(), _pair, _tokenId, stakingAbility);
    }

    function batchUnstake(address _pair, uint256[] calldata _tokenIds) external override {
        for (uint256 i = 0; i < _tokenIds.length; i++) {
            unstake(_pair, _tokenIds[i]);
        }
    }

    function unstakeAll(address _pair) external override {
        EnumerableSet.UintSet storage stakingTokens = _poolUserStakingTokens[_pair][_msgSender()];
        uint256 length = stakingTokens.length();
        for (uint256 i = 0; i < length; ++i) {
            unstake(_pair, stakingTokens.at(0));
        }
    }

    function emergencyUnstake(address _pair, uint256 _tokenId) public override nonReentrant {
        require(_poolUserStakingTokens[_pair][_msgSender()].contains(_tokenId), ' EMERGENCY UNSTAKE FORBIDDEN');
        PoolInfo storage pool = poolInfoMap[_pair];
        require(pool.exists, 'POOL NOT EXISTS');
        UserInfo storage userInfo = _poolUserInfoMap[_pair][_msgSender()];
        uint256 stakingAbility = nftTokenstakingAbilityMap[_pair][_tokenId];
        userInfo.stakingAbility = userInfo.stakingAbility.sub(stakingAbility);
        _poolUserStakingTokens[_pair][_msgSender()].remove(_tokenId);
        IERC721(_pair).safeTransferFrom(address(this), _msgSender(), _tokenId);
        pool.totalstakingAbility = pool.totalstakingAbility.sub(stakingAbility);
        userInfo.rewardDebt = userInfo
            .stakingAbility
            .mul(pool.accTokenPerShare)
            .mul(10**uint256(token.decimals()))
            .div(ONE_ABILITY_EIGHTEEN)
            .div(accTokenPerShareMultiple);
        emit EmergencyUnstake(_msgSender(), _pair, _tokenId, stakingAbility);
    }

    function emergencyUnstakeAll(address _pair) external override {
        EnumerableSet.UintSet storage stakingTokens = _poolUserStakingTokens[_pair][_msgSender()];
        uint256 length = stakingTokens.length();
        for (uint256 i = 0; i < length; ++i) {
            emergencyUnstake(_pair, stakingTokens.at(0));
        }
    }

    // Safe token transfer function, just in case if rounding error causes pool to not have enough TOKENs.
    function safeTokenTransfer(address _to, uint256 _amount) internal {
        uint256 tokenBal = token.balanceOf(address(this));
        if (_amount > tokenBal) {
            token.safeTransfer(_to, tokenBal);
        } else {
            token.safeTransfer(_to, _amount);
        }
    }

    function pauseStake() external override onlyOwner whenNotPaused {
        _pause();
    }

    function unpauseStake() external override onlyOwner whenPaused {
        _unpause();
    }

    function getUserInfo(address _pair, address user)
        public
        view
        returns (
            uint256,
            uint256,
            uint256[] memory
        )
    {
        UserInfo memory userInfo = _poolUserInfoMap[_pair][user];
        uint256[] memory tokenIds = new uint256[](_poolUserStakingTokens[_pair][user].length());
        for (uint256 i = 0; i < tokenIds.length; ++i) {
            tokenIds[i] = _poolUserStakingTokens[_pair][user].at(i);
        }
        return (userInfo.stakingAbility, userInfo.rewardDebt, tokenIds);
    }
}
