contract Blockable is AccessControl {
    using EnumerableSet for EnumerableSet.AddressSet;
    EnumerableSet.AddressSet private _blocklist;


    function getBlocklistLength() public view returns (uint256) {
        return EnumerableSet.length(_blocklist);
    }

    function isInBlocklist(address account) public view returns (bool) {
        if (EnumerableSet.length(_blocklist) == 0) {
            return false;
        }
        return EnumerableSet.contains(_blocklist, account);
    }

    function getBlocklist(uint256 _index) public view onlyCLevel returns (address){
        require(_index <= getBlocklistLength() - 1, "CryptoFifa: index out of bounds");
        return EnumerableSet.at(_blocklist, _index);
    }

    function addBlocklist(address _addBlocklist) public onlyCLevel returns (bool) {
        require(_addBlocklist != address(0), "CryptoFifa: _addBlocklist is the zero address");
        return EnumerableSet.add(_blocklist, _addBlocklist);
    }

    function delBlocklist(address _delBlocklist) public onlyCEO returns (bool) {
        require(_delBlocklist != address(0), "CryptoFifa: _delBlocklist is the zero address");
        return EnumerableSet.remove(_blocklist, _delBlocklist);
    }

    // modifier for blacklist function
    modifier notInBlocklist() {
        require(!isInBlocklist(_msgSender()), "caller is not the blocklist");
        _;
    }

}

