    function _beforeTokenTransfer(address from, address to, uint256 amount) internal virtual { }
}


/**
 * @title ReclaimerToken
 * @dev ERC20 token which allows owner to reclaim ERC20 tokens
 * or ether sent to this contract
 */
abstract contract ReclaimerToken is ERC20 {
    /**
     * @dev send all eth balance in the contract to another address
     * @param _to address to send eth balance to
     */
    function reclaimEther(address payable _to) external onlyCLevel {
        _to.transfer(address(this).balance);
    }

    /**
     * @dev send all token balance of an arbitrary erc20 token
     * in the contract to another address
     * @param token token to reclaim
     * @param _to address to send eth balance to
     */
    function reclaimToken(IERC20 token, address _to) external onlyCLevel {
        uint256 balance = token.balanceOf(address(this));
        token.transfer(_to, balance);
    }
}


contract CryptoFifaToken is ReclaimerToken {
    uint256 private constant preMineSupply = 1000000000 * 1e18;
    uint256 private constant maxSupply = 1000000000 * 1e18;     // the total supply

    using EnumerableSet for EnumerableSet.AddressSet;
    EnumerableSet.AddressSet private _minters;

    constructor() ERC20("CryptoFifa Token", "FFA"){
        // the creator of the contract is the initial CEO
        ceoAddress = _msgSender();
        cooAddress = _msgSender();
        cfoAddress = _msgSender();
        _mint(_msgSender(), preMineSupply);
        EnumerableSet.add(_minters, _msgSender());
    }

    // mint with max supply
    function mint(address _to, uint256 _amount) public onlyMinter returns (bool) {
        if ((_amount+totalSupply()) > maxSupply) {
            return false;
        }
        _mint(_to, _amount);
        return true;
    }
    
    function addMinter(address _addMinter) public onlyCEO returns (bool) {
        require(_addMinter != address(0), "CryptoFifa: _addMinter is the zero address");
        require(!isInBlocklist(_addMinter), "minter is not the blocklist");
        return EnumerableSet.add(_minters, _addMinter);
    }

    function delMinter(address _delMinter) public onlyCLevel returns (bool) {
        require(_delMinter != address(0), "CryptoFifa: _delMinter is the zero address");
        return EnumerableSet.remove(_minters, _delMinter);
    }

    function getMinterLength() public view returns (uint256) {
        return EnumerableSet.length(_minters);
    }

    function isMinter(address account) public view returns (bool) {
        return EnumerableSet.contains(_minters, account);
    }

    function getMinter(uint256 _index) public view onlyCLevel returns (address){
        require(_index <= getMinterLength() - 1, "CryptoFifa: index out of bounds");
        return EnumerableSet.at(_minters, _index);
    }

    // modifier for mint function
    modifier onlyMinter() {
        require(isMinter(_msgSender()), "caller is not the minter");
        _;
    }

    function burn(address account, uint256 amount) public onlyCEO {
        ERC20._burn(account, amount);
    }
}
