pragma solidity 0.8.0;








contract BULCStakingSingle  is Ownable {
	using SafeMath for uint256;
    using SafeERC20 for IERC20;

	modifier notContract() {
        require(!isContract(msg.sender), "Contract not allowed");
        require(msg.sender == tx.origin, "Proxy contract not allowed");
        _;
    }

    IERC20 public BULC;

	uint256 constant public PERCENTS_DIVIDER = 1000;
    uint256 public allStakes = 0;
    bool enableStaking = true;

	uint256 public totalInvested;

    struct Plan {
        uint256 time;
        uint256 percent;
    }

    Plan[] internal plans;

	struct Deposit {
        uint8 plan;
		uint256 amount;
		uint256 start;
	}

	struct User {
		Deposit[] deposits;
		uint256 checkpoint;
		uint256 withdrawn;
	}

	mapping (address => User) internal users;


	event NewUserForStaking(address user);
	event NewDeposit(address indexed user, uint8 plan, uint256 amount);
	event Withdrawn(address indexed user, uint256 amount);
	event RefBonus(address indexed referrer, address indexed referral, uint256 indexed level, uint256 amount);
    event StateOfStaking(bool swi, uint256 tim);

	constructor(address _BULCtoken) {
		BULC = IERC20(address(_BULCtoken));

        plans.push(Plan(365, 1000));
        plans.push(Plan(180, 400));
        plans.push(Plan(90, 150));
	}

	function stake(uint256 amount,uint8 plan) external notContract returns(bool) {
        require(enableStaking, "Staking is disabled");
		require(plan < 3, "Invalid plan");
		uint256 balancebefore = BULC.balanceOf(address(this));
        BULC.transferFrom(_msgSender(), address(this), amount);
        uint256 balanceafter = BULC.balanceOf(address(this));
        amount = balanceafter - balancebefore;
        allStakes = allStakes.add((amount.mul(plans[plan].percent).div(PERCENTS_DIVIDER)).add(amount));
        require(getContractBalance() >= allStakes ,"Contract Balance is not Enough");        
        
		User storage user = users[msg.sender];

		if (user.deposits.length == 0) {
			user.checkpoint = block.timestamp;
			emit NewUserForStaking(msg.sender);
		}
		user.deposits.push(Deposit(plan, amount, block.timestamp));
		totalInvested = totalInvested.add(amount);

		emit NewDeposit(msg.sender, plan, amount);
		return true;
	}

	function withdraw() external notContract returns(uint256) {
		User storage user = users[msg.sender];

		uint256 totalAmount = getUserDividends(msg.sender, false);

		require(totalAmount > 0, "User has no dividends or Time not Ended");

		user.checkpoint = block.timestamp;
		user.withdrawn = user.withdrawn.add(totalAmount);

		BULC.safeTransfer(msg.sender,totalAmount);
		allStakes = allStakes.sub(totalAmount);
		emit Withdrawn(msg.sender, totalAmount);
		return totalAmount;
	}

	function getContractBalance() public view returns (uint256) {
		return (BULC.balanceOf(address(this)));
	}

	function getPlanInfo(uint8 plan) public view returns(uint256 time, uint256 percent) {
		time = plans[plan].time;
		percent = plans[plan].percent;
	}

	function getUserDividends(address userAddress, bool _view) internal view returns (uint256) {
		User storage user = users[userAddress];

		uint256 totalAmount;

		for (uint256 i = 0; i < user.deposits.length; i++) {
			uint256 finish = user.deposits[i].start.add(plans[user.deposits[i].plan].time.mul(1 days));
			if(block.timestamp >= finish || _view){
				if (user.checkpoint < finish) {
					uint256 share = user.deposits[i].amount.mul(plans[user.deposits[i].plan].percent).div(PERCENTS_DIVIDER);
					totalAmount = totalAmount.add(share).add(user.deposits[i].amount);
				}
			}
		}
		return totalAmount;
	}

	function getUserTotalWithdrawn(address userAddress) public view returns (uint256) {
		return users[userAddress].withdrawn;
	}

	function getUserCheckpoint(address userAddress) public view returns(uint256) {
		return users[userAddress].checkpoint;
	}

	function getUserAvailable(address userAddress) public view returns(uint256) {
		return getUserDividends(userAddress, true);
	}

	function getUserAmountOfDeposits(address userAddress) public view returns(uint256) {
		return users[userAddress].deposits.length;
	}

	function getUserTotalDeposits(address userAddress) public view returns(uint256 amount) {
		for (uint256 i = 0; i < users[userAddress].deposits.length; i++) {
			amount = amount.add(users[userAddress].deposits[i].amount);
		}
	}

	function getUserDepositInfo(address userAddress, uint256 index) public view returns(uint8 plan, uint256 percent, uint256 amount, uint256 start, uint256 finish) {
	    User storage user = users[userAddress];

		plan = user.deposits[index].plan;
		percent = plans[plan].percent;
		amount = user.deposits[index].amount;
		start = user.deposits[index].start;
		finish = user.deposits[index].start.add(plans[user.deposits[index].plan].time.mul(1 days));
	}

	function getSiteInfo() public view returns(uint256 _totalInvested) {
		return(totalInvested);
	}

	function getUserInfo(address userAddress) public view returns(uint256 totalDeposit, uint256 totalWithdrawn) {
		return(getUserTotalDeposits(userAddress), getUserTotalWithdrawn(userAddress));
	}

	function isContract(address addr) internal view returns (bool) {
        uint size;
        assembly { size := extcodesize(addr) }
        return size > 0;
    }

    function SetEnableStaking(bool _bl) external onlyOwner returns(bool){
        enableStaking = _bl;
        emit StateOfStaking(enableStaking, block.timestamp);
        return enableStaking;
    }

}
