        // 0x9Ac64Cc6e4415144C455BD8E4837Fea55603e5c3 TESTNET
        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(_router); // router
        uniswapV2Router = _uniswapV2Router;

        // Create a uniswap pair for this new token
        address _uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
            .createPair(address(this), _uniswapV2Router.WETH());

        uniswapV2Router = _uniswapV2Router;
        uniswapV2Pair = _uniswapV2Pair;

        liquidityPool[_uniswapV2Pair] = true;
        _isMaxWalletExempt[_uniswapV2Pair] = true;
    }

    function setTaxes(
        uint8 _sellTax,
        uint8 _buyTax,
        uint8 _transferTax
    ) external onlyOwner {
        require(_sellTax <= 30, "Must keep taxes below 30%");
        require(_buyTax <= 30, "Must keep taxes below 30%");
        require(_transferTax <= 30, "Must keep taxes below 30%");
        require(_sellTax + _buyTax <= 50, "Must keep combined taxes below 50%");
        sellTax = _sellTax;
        buyTax = _buyTax;
        transferTax = _transferTax;
        emit changeTax(_sellTax, _buyTax, _transferTax);
    }

    function getTaxes()
        external
        view
        returns (
            uint8 _sellTax,
            uint8 _buyTax,
            uint8 _transferTax
