    }

    function manage_Snipers(address[] calldata addresses, bool status)
        public
        onlyOwner
    {
        for (uint256 i; i < addresses.length; ++i) {
            if (!authorizations[addresses[i]]) {
                _isSniper[addresses[i]] = status;
            }
        }
    }

    function _transfer(
        address sender,
        address receiver,
        uint256 amount
    ) internal virtual override {
        if (launchedAt > 0 && block.number >= (launchedAt + 1)) {
            require(!_isSniper[sender], "403: Access denied!");
            require(!_isSniper[receiver], "403: Access denied!");
        }

        if (!authorizations[sender]) {
            require(tradingOpen, "Trading not open yet");
        }

        if (launchedAt > 0 &&!_isMaxWalletExempt[receiver]) {
            require(
                amount + balanceOf(receiver) <= _maxWalletToken,
                "Total Holding is currently limited"
            );
        }

        uint256 taxAmount;
        if (liquidityPool[sender] == true) {
            //It's an LP Pair and it's a buy
            taxAmount = (amount * buyTax) / 100;

            //antibot - first 2 blocks
            if (launchedAt > 0 && (launchedAt + deadBlocks) >= block.number) {
                if (liquidityPool[receiver] == false) {
                    _isSniper[receiver] = true;
                }
            }
            if (tradeCooldown > 0) {
                lastTrade[receiver] = block.timestamp;
            }
        } else if (liquidityPool[receiver] == true) {
            //It's an LP Pair and it's a sell
            taxAmount = (amount * sellTax) / 100;

            if (launchedAt > 0 &&!_isMaxWalletExempt[sender]) {
                require(
                    amount <= maxSellTX,
                    "Sell transfer amount exceeds the maxSellTX."
                );
            }

            if (tradeCooldown > 0) {
            require(
                lastTrade[sender] < (block.timestamp - tradeCooldown),
                string("No consecutive sells allowed. Please wait.")
            );
            lastTrade[sender] = block.timestamp;
            }
        } else {
            taxAmount = (amount * transferTax) / 100;
        }
        
        if (
            whitelistTax[sender] ||
            whitelistTax[receiver] ||
            sender == marketingWallet ||
            receiver == marketingWallet
        ) {
            taxAmount = 0;
        } 

        if (taxAmount > 0) {
            // transfer fees
            super._transfer(sender, address(this), taxAmount);
            // swap fees on sell only
            uint256 contractTokenBalance = balanceOf(address(this));
            bool canSwap = contractTokenBalance >= swapThreshold;
            if (
                !inSwap &&
                canSwap &&
                swapEnabled &&
                tradingOpen &&
                receiver == uniswapV2Pair
            ) {
                swapTokens(contractTokenBalance);
            }
        }
        super._transfer(sender, receiver, amount - taxAmount);
    }

    modifier lockTheSwap() {
        inSwap = true;
        _;
        inSwap = false;
    }

    function swapTokens(uint256 contractTokenBalance) private lockTheSwap {
        swapTokensForEth(contractTokenBalance);
        //Send to marketing wallet
        uint256 contractETHBalance = address(this).balance;
        if (contractETHBalance > 0) {
            uint256 marketingAmount;
            uint256 devAmount;
            uint256 rewardsAmount;

            marketingAmount = (contractETHBalance * marketingRatio) / 1000;
            devAmount = (contractETHBalance * devRatio) / 1000;
            rewardsAmount = contractETHBalance - marketingAmount - devAmount;

            sendETHToFee(marketingAmount, payable(marketingWallet));
            sendETHToFee(devAmount, payable(devWallet));
            sendETHToFee(rewardsAmount, payable(rewardsWallet));
        }
    }

    function sendETHToFee(uint256 amount, address payable recipient) private {
        recipient.transfer(amount);
    }

    function swapTokensForEth(uint256 tokenAmount) private {
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // make the swap
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(this), // The contract
            block.timestamp
        );

        emit SwapTokensForETH(tokenAmount, path);
    }

    function setSwapThreshold(uint256 percentBase10000) public onlyOwner {
        swapThreshold = (_tTotal / 10000) * percentBase10000;
    }

    // only use to disable contract sales if absolutely necessary (emergency use only)
    function updateSwapEnabled(bool enabled) external onlyOwner {
        swapEnabled = enabled;
    }

    function manualSwap() public onlyOwner {
        swapTokens(swapThreshold);
    }

    function emergencyWithdrawTokens(address to) public onlyOwner {
        super._transfer(address(this), to, balanceOf(address(this)));
    }

    //to recieve ETH from uniswapV2Router when swaping
    receive() external payable {}

    // Withdraw ETH that gets stuck in contract by accident
    function emergencyWithdraw() external onlyOwner {
        payable(owner()).transfer(address(this).balance);
    }
