    "contracts/ApeHeroCollabItem.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.9;

import "@openzeppelin/contracts/access/AccessControlEnumerable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

import "./ApeHeroCollabTypes.sol";

contract ApeHeroCollabItem is Ownable, AccessControlEnumerable {
    using Strings for uint;
    using EnumerableSet for EnumerableSet.AddressSet;
    using EnumerableSet for EnumerableSet.UintSet;
    using Address for address;

    struct ContractData {
        Item[] items;
        ItemsTotal total;
    }

    uint CURRENT_ID;
    mapping(uint => Item) private ITEMS;
    EnumerableSet.UintSet private ACTIVE_ITEMS;
    EnumerableSet.UintSet private INACTIVE_ITEMS;
    EnumerableSet.UintSet private FINALIZED_ITEMS;
    EnumerableSet.UintSet private REMOVED_ITEMS;
    mapping(uint => EnumerableSet.AddressSet) private UNIQUE_BUYERS; // {[item.id]: [address...]}

    event ItemAdded(uint itemId, uint timestamp);
    event ItemPaymentChanged(uint itemId, uint price, address paymentToken, uint paymentDecimals, bool nativePayment, uint timestamp);
    event ItemPayoutChanged(uint itemId, address payoutWallet, uint paymentBurn, uint timestamp);
    event ItemExpirationChanged(uint itemId, uint oldExpiration, uint newExpiration, uint timestamp);
    event ItemStatusChanged(uint itemId, ItemStatus oldStatus, ItemStatus newStatus, uint timestamp);
    event ItemMaxPerAccountChanged(uint itemId, uint oldMaxPerAccount, uint newMaxPerAccount, uint timestamp);
    event ItemMaxSupplyChanged(uint itemId, uint oldSupply, uint newSupply, uint timestamp);
    event ItemSupplyIncreased(uint itemId, uint currentSupply, uint currentBalance, uint timestamp);
    event ItemTimeChanged(uint itemId, uint startedOn, uint expiresOn, uint timestamp);
    event ItemAuctionChanged(uint itemId, uint minIncrement, uint timestamp);
    event ItemRaffleChanged(uint itemId, uint winnersTotal, uint timestamp);
    event FundsReceived(address account, uint amount, uint timestamp);
    event FundsDirectlyDeposited(address account, uint amount, uint timestamp);

    constructor() Ownable() {
        _grantRole(DEFAULT_ADMIN_ROLE, owner());
        _grantRole(ADMIN, owner());
        _grantRole(OPERATOR, owner());
    }

    // --- modifiers --- //
    modifier onlyAdminAndOperator() {
        require(
            hasRole(ADMIN, _msgSender()) || hasRole(OPERATOR, _msgSender()),
            "ADMIN or OPERATOR role required"
        );
        _;
    }

    modifier onlyWithUpgradedRoles() {
        address _sender = _msgSender();
        require(
            hasRole(ADMIN, _sender) || hasRole(OPERATOR, _sender) || hasRole(CONTROLLER, _sender),
            "ADMIN, OPERATOR or CONTROLLER role required"
        );
        _;
    }

    modifier itemExists(uint itemId) {
        require(
            itemId <= CURRENT_ID,
            string(abi.encodePacked("Item with id '", itemId.toString(), "' does not exist"))
        );
        _;
    }

    // --- fallback/received --- //
    receive() external payable {
        emit FundsReceived(_msgSender(), msg.value, block.timestamp);
    }

    fallback() external payable {
        emit FundsDirectlyDeposited(_msgSender(), msg.value, block.timestamp);
    }

    // --- utils --- //
    function _setStatus(uint itemId, ItemStatus status) internal {
        ItemStatus _currentStatus = ITEMS[itemId].status;
        require(
            status != ItemStatus.REMOVED ||
            (status == ItemStatus.REMOVED && _currentStatus == ItemStatus.INACTIVE),
            "Only 'INACTIVE' items can be removed"
        );

        uint _now = block.timestamp;
        ITEMS[itemId].status = status;
        ITEMS[itemId].updatedOn = _now;

        if (_currentStatus == ItemStatus.ACTIVE) {
            ACTIVE_ITEMS.remove(itemId);
        } else if (_currentStatus == ItemStatus.INACTIVE) {
            INACTIVE_ITEMS.remove(itemId);
        } else if (_currentStatus == ItemStatus.FINALIZED) {
            FINALIZED_ITEMS.remove(itemId);
        }

        if (status == ItemStatus.FINALIZED) {
            FINALIZED_ITEMS.add(itemId);
        } else if (status == ItemStatus.ACTIVE) {
            ACTIVE_ITEMS.add(itemId);
        } else if (status == ItemStatus.INACTIVE) {
            INACTIVE_ITEMS.add(itemId);
        } else if (status == ItemStatus.REMOVED) {
            REMOVED_ITEMS.add(itemId);
        }

        emit ItemStatusChanged(itemId, _currentStatus, status, _now);
    }

    // --- ADMIN --- //
    function emergencyWithdraw(uint amount) external onlyRole(ADMIN) {
        payable(owner()).transfer(amount > 0 ? amount : address(this).balance);
    }

    function emergencyWithdrawToken(address token, uint amount) external onlyRole(ADMIN) {
        IERC20(token).transfer(owner(), amount > 0 ? amount : IERC20(token).balanceOf(address(this)));
    }

    // --- ADMIN & OPERATOR --- //
    function addItem(ItemMode mode, uint price, uint maxSupply, uint expiresInSeconds) external onlyAdminAndOperator {
        uint _now = block.timestamp;
        uint _newItemId = CURRENT_ID + 1;

        ITEMS[_newItemId] = Item({
            itemId: _newItemId,
            mode: mode,
            status: ItemStatus.INACTIVE,
            uniqueBuyers: 0,
            balance: 0,
            maxPerAccount: 1,
            currentSupply: 0,
            maxSupply: maxSupply,

            price: price,
            paymentToken: address(0),
            paymentTokenDecimals: 0,
            nativePayment: false,
            paymentBurn: 1e4,
            payoutWallet: address(0),

            winnersTotal: 0,

            minIncrement: 0,
            bidsTotal: 0,
            lastBidPrice: 0,
            lastBidOn: 0,
            extensions: 0,
            extendedTime: 0,

            expired: false,
            startedOn: 0,
            expiresOn: expiresInSeconds > 0 ? _now + expiresInSeconds : 0,
            createdOn: _now,
            updatedOn: _now
        });
        INACTIVE_ITEMS.add(_newItemId);
        CURRENT_ID = _newItemId;

        emit ItemAdded(_newItemId, _now);
    }

    function updateItemTime(uint itemId, uint startedOn, uint expiresOn) external onlyAdminAndOperator itemExists(itemId) {
        uint _now = block.timestamp;

        ITEMS[itemId].startedOn = startedOn > 0 ? _now + startedOn : 0;
        ITEMS[itemId].expiresOn = expiresOn > 0 ? _now + expiresOn : 0;
        ITEMS[itemId].updatedOn = _now;

        emit ItemTimeChanged(itemId, ITEMS[itemId].startedOn, ITEMS[itemId].expiresOn, _now);
    }

    function updateItemMaxPerAccount(uint itemId, uint maxPerAccount) external onlyAdminAndOperator itemExists(itemId) {
        Item memory _item = ITEMS[itemId];
        uint _now = block.timestamp;
        uint _oldMaxPerAccount = _item.maxPerAccount;
        ITEMS[itemId].maxPerAccount = maxPerAccount;

        emit ItemMaxPerAccountChanged(itemId, _oldMaxPerAccount, maxPerAccount, _now);
    }

    function updateItemMaxSupply(uint itemId, uint maxSupply) external onlyAdminAndOperator itemExists(itemId) {
        Item memory _item = ITEMS[itemId];
        require(maxSupply >= _item.currentSupply, "Max supply cannot be smaller than current supply");

        uint _now = block.timestamp;
        uint _oldSupply = _item.maxSupply;
        ITEMS[itemId].maxSupply = maxSupply;
        ITEMS[itemId].updatedOn = _now;

        emit ItemMaxSupplyChanged(itemId, _oldSupply, maxSupply, _now);
    }

    function updateAuction(uint itemId, uint minIncrement) external onlyAdminAndOperator itemExists(itemId) {
        uint _now = block.timestamp;
        ITEMS[itemId].minIncrement = minIncrement;
        ITEMS[itemId].updatedOn = _now;

        emit ItemAuctionChanged(itemId, minIncrement, _now);
    }

    function updateRaffle(uint itemId, uint winnersTotal) external onlyAdminAndOperator itemExists(itemId) {
        uint _now = block.timestamp;
        ITEMS[itemId].winnersTotal = winnersTotal;
        ITEMS[itemId].updatedOn = _now;

        emit ItemRaffleChanged(itemId, winnersTotal, _now);
    }

    function updateItemPayment(
        uint itemId, uint price, address paymentToken, uint paymentDecimals, bool nativePayment
    ) external onlyAdminAndOperator itemExists(itemId) {
        uint _now = block.timestamp;
        if (nativePayment) {
            ITEMS[itemId].paymentBurn = 0;
        }
        ITEMS[itemId].price = price;
        ITEMS[itemId].paymentToken = paymentToken;
        ITEMS[itemId].paymentTokenDecimals = paymentDecimals;
        ITEMS[itemId].nativePayment = nativePayment;
        ITEMS[itemId].updatedOn = _now;

        emit ItemPaymentChanged(itemId, price, paymentToken, paymentDecimals, nativePayment, _now);
    }

    function updateItemPayout(
        uint itemId, address payoutWallet, uint paymentBurn
    ) external onlyAdminAndOperator itemExists(itemId) {
        uint _now = block.timestamp;

        ITEMS[itemId].payoutWallet = payoutWallet;
        if (!ITEMS[itemId].nativePayment && paymentBurn > 0) {
            ITEMS[itemId].paymentBurn = paymentBurn;
        }
        ITEMS[itemId].updatedOn = _now;

        emit ItemPayoutChanged(itemId, payoutWallet, paymentBurn, _now);
    }

    // --- UPGRADED --- //
    function setItemStatus(uint itemId, ItemStatus status) external onlyWithUpgradedRoles itemExists(itemId) {
        require(status != ITEMS[itemId].status, "Item already has requested status");
        _setStatus(itemId, status);
    }

    function extendItemExpiration(uint itemId, uint extraSeconds) external onlyWithUpgradedRoles itemExists(itemId) {
        uint _now = block.timestamp;
        uint _oldExpiration = ITEMS[itemId].expiresOn;
        ITEMS[itemId].expiresOn = (ITEMS[itemId].expiresOn > 0 ? ITEMS[itemId].expiresOn : _now) + extraSeconds;
        ITEMS[itemId].extensions += 1;
        ITEMS[itemId].extendedTime += extraSeconds;

        emit ItemExpirationChanged(itemId, _oldExpiration, ITEMS[itemId].expiresOn, _now);
    }

    function increaseCurrentSupply(uint itemId, uint amount, uint payment, uint refunded) external onlyWithUpgradedRoles itemExists(itemId) {
        Item memory _item = ITEMS[itemId];
        uint _oldSupply = _item.currentSupply;
        uint _now = block.timestamp;

        if (amount > 0) {
            require(
                (_item.maxSupply == 0) || (_item.mode == ItemMode.AUCTION) || (_oldSupply + amount <= _item.maxSupply),
                "Current supply cannot exceeds item's max supply"
            );
            ITEMS[itemId].currentSupply += amount;
            ITEMS[itemId].balance += (payment - refunded);

            if (_item.mode == ItemMode.AUCTION) {
                ITEMS[itemId].lastBidPrice = payment;
                ITEMS[itemId].lastBidOn = _now;
            }

            emit ItemSupplyIncreased(itemId, ITEMS[itemId].currentSupply, ITEMS[itemId].balance, _now);
        }
    }

    function addUniqueBuyer(uint itemId, address account) external onlyWithUpgradedRoles itemExists(itemId) {
        UNIQUE_BUYERS[itemId].add(account);
    }

    function removeUniqueBuyer(uint itemId, address account) external onlyWithUpgradedRoles itemExists(itemId) {
        UNIQUE_BUYERS[itemId].remove(account);
    }

    // --- public --- //
    function getItem(uint itemId) external view itemExists(itemId) returns(Item memory) {
        Item memory _item = ITEMS[itemId];
        _item.uniqueBuyers = UNIQUE_BUYERS[itemId].length();
        if (_item.expiresOn > 0 && _item.expiresOn < block.timestamp) {
            _item.expired = true;
        }

        return _item;
    }

    function getItemsByIds(uint[] calldata itemIds) external view returns(Item[] memory) {
        uint _itemsCount = itemIds.length;
        Item[] memory _allItems = new Item[](_itemsCount);

        uint _itemId;
        for (uint _idx = 0; _idx < _itemsCount; _idx++) {
            _itemId = itemIds[_idx];
            require(_itemId <= CURRENT_ID, string(abi.encodePacked("Item with id '", _itemId.toString(), "' does not exist")));
            _allItems[_idx] = ITEMS[_itemId];
            _allItems[_idx].uniqueBuyers = UNIQUE_BUYERS[_itemId].length();

            if (_allItems[_idx].expiresOn > 0 && _allItems[_idx].expiresOn < block.timestamp) {
                _allItems[_idx].expired = true;
            }
        }

        return _allItems;
    }

    function getItemsByStatus(ItemStatus status) public view returns(Item[] memory) {
        uint _allItemsCount;
        if (status == ItemStatus.ACTIVE) {
            _allItemsCount = ACTIVE_ITEMS.length();
        } else if (status == ItemStatus.INACTIVE) {
            _allItemsCount = INACTIVE_ITEMS.length();
        } else if (status == ItemStatus.FINALIZED) {
            _allItemsCount = FINALIZED_ITEMS.length();
        } else if (status == ItemStatus.REMOVED) {
            _allItemsCount = REMOVED_ITEMS.length();
        }

        Item[] memory _allItems;
        if (_allItemsCount == 0) {
            return _allItems;
        }

        _allItems = new Item[](_allItemsCount);
        for (uint _idx = 0; _idx < _allItemsCount; _idx++) {
            if (status == ItemStatus.ACTIVE) {
                _allItems[_idx] = ITEMS[ACTIVE_ITEMS.at(_idx)];
            } else if (status == ItemStatus.INACTIVE) {
                _allItems[_idx] = ITEMS[INACTIVE_ITEMS.at(_idx)];
            } else if (status == ItemStatus.FINALIZED) {
                _allItems[_idx] = ITEMS[FINALIZED_ITEMS.at(_idx)];
            } else if (status == ItemStatus.REMOVED) {
                _allItems[_idx] = ITEMS[REMOVED_ITEMS.at(_idx)];
            }

            _allItems[_idx].uniqueBuyers = UNIQUE_BUYERS[_allItems[_idx].itemId].length();

            if (_allItems[_idx].expiresOn > 0 && _allItems[_idx].expiresOn < block.timestamp) {
                _allItems[_idx].expired = true;
            }
        }

        return _allItems;
    }

    function getItemsTotal() public view returns(ItemsTotal memory) {
        return ItemsTotal({
            active: ACTIVE_ITEMS.length(),
            inactive: INACTIVE_ITEMS.length(),
            finalized: FINALIZED_ITEMS.length(),
            removed: REMOVED_ITEMS.length()
        });
    }

    function cData() external view returns (ContractData memory) {
        return ContractData({
            items: getItemsByStatus(ItemStatus.ACTIVE),
            total: getItemsTotal()
        });
    }
}
"
