// SPDX-License-Identifier: MIT
pragma solidity ^0.8.8;

import {EIP712} from "@openzeppelin/contracts/utils/cryptography/draft-EIP712.sol";
import {ERC20Unimint} from "./ERC20Unimint.sol";

/**
 * @title Who is Satoshi Token (WIST)
 */
contract WISToken is ERC20Unimint {
    // ------------------------------------------------------------------------
    // Max Supply & Premint
    // ------------------------------------------------------------------------
    uint256 internal constant MaxSupplyAmount   = 10_000_000_000 ether;
    uint256 internal constant PremintTeam       = MaxSupplyAmount * 50 / 1000;

    // ------------------------------------------------------------------------
    // Premint Addresses
    // ------------------------------------------------------------------------
    address internal constant AddressTeam       = 0x1e656a65324fDd1167d2A2490D0722a06cB54E33;
    address internal constant AddressBurner     = 0x60875f02a5E919eA3dFdfa0b8476f7053Cb654cF;

    /**
     * @notice Constructor
     */
    constructor()
        ERC20Unimint(
            AddressTeam,
            PremintTeam,
            MaxSupplyAmount,
            "WIS Token",
            "WIST"
        )
        EIP712("WISToken", "1")
    {
        // burner
        _updateBurner(AddressBurner, true);
    }
}
"
