    "contracts/token/ERC20Unimint.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.8;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {ERC20} from "@openzeppelin/contracts/token/ERC20/ERC20.sol";

import {ERC20ExtUnimint} from "./ERC20ExtUnimint.sol";
import {ERC20ExtAirdrop} from "./ERC20ExtAirdrop.sol";
import {ERC20ExtBurnable} from "./ERC20ExtBurnable.sol";
import {ERC20ExtMintable} from "./ERC20ExtMintable.sol";
import {ERC20ExtLockable} from "./ERC20ExtLockable.sol";

/**
 * @title ERC20Unimint
 * @notice
██╗   ██╗███╗   ██╗██╗███╗   ███╗██╗███╗   ██╗████████╗
██║   ██║████╗  ██║██║████╗ ████║██║████╗  ██║╚══██╔══╝
██║   ██║██╔██╗ ██║██║██╔████╔██║██║██╔██╗ ██║   ██║   
██║   ██║██║╚██╗██║██║██║╚██╔╝██║██║██║╚██╗██║   ██║   
╚██████╔╝██║ ╚████║██║██║ ╚═╝ ██║██║██║ ╚████║   ██║   
 ╚═════╝ ╚═╝  ╚═══╝╚═╝╚═╝     ╚═╝╚═╝╚═╝  ╚═══╝   ╚═╝   
 */
abstract contract ERC20Unimint is
    ERC20,
    Ownable,
    ERC20ExtUnimint,
    ERC20ExtAirdrop,
    ERC20ExtBurnable,
    ERC20ExtMintable,
    ERC20ExtLockable
{
    uint256 private immutable _maxSupply;

    /**
     * @notice Constructor
     * @param _premintReceiver address that receives the premint
     * @param _premintAmount amount to premint
     * @param _max max supply (to prevent abusive mint)
     */
    constructor(
        address _premintReceiver,
        uint256 _premintAmount,
        uint256 _max,
        string memory name,
        string memory symbol
    ) ERC20(name, symbol) {
        require(
            _max > _premintAmount,
            "MINT: Premint amount is greater than cap"
        );
        _mint(_premintReceiver, _premintAmount);
        _maxSupply = _max;
    }

    /**
     * @notice View max supply cap
     */
    function maxSupply() external view returns (uint256) {
        return _maxSupply;
    }

    // ------------------------------------------------------------------------
    // Ext:Unimint
    // ------------------------------------------------------------------------
    function unimint(UnimintTicket calldata ticket) external override {
        bytes32 ticketHash = hash(ticket);
        // check signer
        _validateTicket(ticket, ticketHash);
        // set ticket executed
        _setExecuted(ticket);
        // send token
        require(
            totalSupply() + ticket.amount <= _maxSupply,
            "Amount > MaxSupply"
        );
        _mint(ticket.to, ticket.amount);
    }

    function signerMint(address to,uint256 amount) external override {
        require(_isSigner(_msgSender()),'Not Signer');
        require(
            totalSupply() + amount <= _maxSupply,
            "Amount > MaxSupply"
        );
        _mint( to, amount);
    }

    // ------------------------------------------------------------------------
    // Ext:Unimint Owner
    // ------------------------------------------------------------------------
    function updateSigner(address signer, bool enabled)
        external
        override
        onlyOwner
    {
        require(signer != address(0), "Zero Address");
        _setSigner(signer, enabled);
    }

    // ------------------------------------------------------------------------
    // Ext:Airdrop
    // ------------------------------------------------------------------------

    function claim(uint256 amount, bytes32[] calldata merkleProof)
        external
        payable
        override
    {
        require(tx.origin == _msgSender(), "Whitelist Only EOA");
        require(totalSupply() + amount <= _maxSupply, "Amount > MaxSupply");
        require(
            _setClaimed(_msgSender(), amount, msg.value, merkleProof),
            "Set Claimed Failed"
        );

        _mint(_msgSender(), amount);
    }

    function updateMerkleRoot(bytes32 _merkleRoot) external override onlyOwner {
        _updateMerkleRoot(_merkleRoot);
    }

    function clearMerkleRoot() external override onlyOwner {
        _clearMerkleRoot();
    }

    function updateEndTimestamp(uint256 newEndTimestamp)
        external
        override
        onlyOwner
    {
        _updateEndTimestamp(newEndTimestamp);
    }

    function updateMaxClaimAmount(uint96 newAmount)
        external
        override
        onlyOwner
    {
        _updateMaxClaimAmount(newAmount);
    }

    // ------------------------------------------------------------------------
    // Ext:Burnable
    // ------------------------------------------------------------------------
    function burn(address _user, uint256 amount) external override {
        require(_isBurner(_msgSender()),"Not Burner");
        require(balanceOf(_user) >= amount,"Not Enough");
        _burn(_user, amount);
    }

    function updateBurner(address _burner, bool enabled)
        external
        override
        onlyOwner
    {
        _updateBurner(_burner, enabled);
    }

    // ------------------------------------------------------------------------
    // Ext:Mintable
    // ------------------------------------------------------------------------
    function mint(address _user, uint256 amount) external override {
        require(_isMinter(_msgSender()),"Not Minter");
        require(totalSupply() + amount <= _maxSupply, "Amount > MaxSupply");
        _mint(_user, amount);
    }

    function updateMinter(address _user, bool enabled)
        external
        override
        onlyOwner
    {
        _updateMinter(_user, enabled);
    }

    // ------------------------------------------------------------------------
    // Ext:Lockable
    // ------------------------------------------------------------------------

    function updateLockAmount(address _user,uint256 _amount) external override onlyOwner{
        _updateLockAmount(_user, _amount);
    }
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        if( _lockedAmount(from) > 0 ){
            require( balanceOf(from) - amount >= _lockedAmount(from),'Locked');
            emit ERC20ExtLockableAccountTransfer(from,to,amount);
        }
        // super._beforeTokenTransfer(from,to,amount);
    }

    // ------------------------------------------------------------------------
    // Owner
    // ------------------------------------------------------------------------
    function withdraw() external onlyOwner {
        payable(owner()).transfer(address(this).balance);
    }

}
"
    },
