    "contracts/token/ERC20ExtUnimint.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.8;

/// @author: unimint.org
import {EIP712} from "@openzeppelin/contracts/utils/cryptography/draft-EIP712.sol";
import {SignatureChecker} from "../libraries/SignatureChecker.sol";

/**
 * @title ERC20ExtUnimint
 * @notice Unimint Extension
 */
abstract contract ERC20ExtUnimint is EIP712 {
    // keccak256("UnimintTicket(address signer,address to,uint256 amount,uint256 nonce,uint256 startTime,uint256 endTime)")
    bytes32 internal constant TICKET_HASH =
        keccak256(
            "UnimintTicket(address signer,address to,uint256 amount,uint256 nonce,uint256 startTime,uint256 endTime)"
        );

    mapping(address => uint256) public userMinTicketNonce;
    mapping(address => mapping(uint256 => bool))
        internal _isUserTicketNonceExecutedOrCancelled;
    mapping(address => bool) private _unimintSigners;

    struct UnimintTicket {
        address signer; // signer of the ticket
        address to; // owner of the tokens
        uint256 amount; // id of the token
        uint256 nonce; // ticket nonce (must be unique unless new ticket is meant to override existing one)
        uint256 startTime; // startTime in timestamp
        uint256 endTime; // endTime in timestamp
        uint8 v; // v: parameter (27 or 28)
        bytes32 r; // r: parameter
        bytes32 s; // s: parameter
    }

    // ------------------------------------------------------------------------
    // event
    // ------------------------------------------------------------------------
    event ERC20ExtUnimintTicketExecuted(address indexed to,uint256 amount, uint256 indexed nonce);

    function hash(UnimintTicket memory ticket) internal pure returns (bytes32) {
        return
            keccak256(
                abi.encode(
                    TICKET_HASH,
                    ticket.signer,
                    ticket.to,
                    ticket.amount,
                    ticket.nonce,
                    ticket.startTime,
                    ticket.endTime
                )
            );
    }

    function _validateTicket(UnimintTicket calldata ticket, bytes32 ticketHash)
        internal
        view
    {
        // Verify whether ticket nonce has expired
        require(
            (
                !_isUserTicketNonceExecutedOrCancelled[ticket.signer][
                    ticket.nonce
                ]
            ) && (ticket.nonce >= userMinTicketNonce[ticket.signer]),
            "Ticket: Matching ticket expired"
        );

        // Verify signer list
        require(_unimintSigners[ticket.signer], "Ticket: Invalid signer");

        // Verify ticket time
        require(_canExecuteTicket(ticket), "Ticket: Invalid ticket time");

        // Verify the validity of the signature
        require(
            SignatureChecker.verify(
                ticketHash,
                ticket.signer,
                ticket.v,
                ticket.r,
                ticket.s,
                _domainSeparatorV4()
            ),
            "Signature: Invalid"
        );
    }

    function _setExecuted(UnimintTicket calldata ticket) internal {
        _isUserTicketNonceExecutedOrCancelled[ticket.signer][
            ticket.nonce
        ] = true;
        emit ERC20ExtUnimintTicketExecuted(ticket.to, ticket.amount, ticket.nonce);
    }

    function _isSigner(address signer) internal view returns (bool) {
        return _unimintSigners[signer];
    }

    function _setSigner(address signer, bool enabled) internal {
        _unimintSigners[signer] = enabled;
    }

    function _canExecuteTicket(UnimintTicket calldata ticket)
        internal
        view
        returns (bool)
    {
        return ((ticket.endTime >= block.timestamp) &&
            (ticket.startTime <= block.timestamp));
    }

    /**
     * @notice Check whether user ticket nonce is executed or cancelled
     * @param user address of user
     * @param ticketNonce nonce of the ticket
     */
    function isUserTicketNonceExecutedOrCancelled(
        address user,
        uint256 ticketNonce
    ) external view returns (bool) {
        return _isUserTicketNonceExecutedOrCancelled[user][ticketNonce];
    }

    function updateSigner(address signer, bool enabled) external virtual;

    function unimint(UnimintTicket calldata ticket) external virtual;

    function signerMint(address to, uint256 amount) external virtual;
}
"
    },
    "contracts/token/ERC20ExtAirdrop.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.8;

/// @author: unimint.org

import {MerkleProof} from "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";

/**
 * @title ERC20ExtAirdrop
 * @notice It distributes ERC20 tokens with a Merkle-tree Rolling Airdrop
 */
abstract contract ERC20ExtAirdrop {
    // ------------------------------------------------------------------------
    // private
    // ------------------------------------------------------------------------
    bool private isMerkleRootSet;
    bytes32 private merkleRoot;
    uint256 private endTimestamp;
    uint96 private maxClaimAmount;
    mapping(address => uint256) private claimedAmount;

    // ------------------------------------------------------------------------
    // event
    // ------------------------------------------------------------------------
    event EvtAirdropClaim(address indexed user, uint256 amount);
    event EvtUpdateMerkleRoot(bytes32 merkleRoot);
    event EvtUpdateEndTimestamp(uint256 endTimestamp);
    event EvtUpdateMaxClaimAmount(uint96 amount);

    // ------------------------------------------------------------------------
    // internal
    // ------------------------------------------------------------------------
    /**
     * @notice Check whether it is possible to claim (it doesn't check orders)
     * @param user address of the user
     * @param amount amount to claim
     * @param merkleProof array containing the merkle proof
     */
    function canClaim(
        address user,
        uint256 amount,
        uint256 value,
        bytes32[] calldata merkleProof
    ) external view returns (bool) {
        if (block.timestamp <= endTimestamp && isMerkleRootSet) {
            bytes32 node = keccak256(abi.encodePacked(user, amount, value));
            return MerkleProof.verify(merkleProof, merkleRoot, node);
        } else {
            return false;
        }
    }

    // ------------------------------------------------------------------------
    // internal
    // ------------------------------------------------------------------------
    /**
     * @notice Claim tokens for airdrop
     * @param amount amount to claim for the airdrop
     * @param merkleProof array containing the merkle proof
     */
    function _setClaimed(
        address user,
        uint256 amount,
        uint256 value,
        bytes32[] calldata merkleProof
    ) internal returns (bool) {
        require(isMerkleRootSet, "Merkle root not set");
        require(
            maxClaimAmount == 0 || amount <= maxClaimAmount,
            "Amount > Max Amount"
        );
        require(block.timestamp <= endTimestamp, "Too late to claim");

        // Verify the user has claimed
        require(claimedAmount[user]>=amount, "Claimed >= Amount");

        // Compute the node and verify the merkle proof
        bytes32 node = keccak256(abi.encodePacked(user, amount, value));
        require(
            MerkleProof.verify(merkleProof, merkleRoot, node),
            "Invalid proof"
        );

        // Set claimed amount
        claimedAmount[user] += amount;

        emit EvtAirdropClaim(user, amount);
        // Mint Token if return true;
        return true;
    }

    /**
     * @notice Set merkle root for airdrop
     * @param _merkleRoot merkle root
     */
    function _updateMerkleRoot(bytes32 _merkleRoot) internal {
        isMerkleRootSet = true;
        merkleRoot = _merkleRoot;
        emit EvtUpdateMerkleRoot(_merkleRoot);
    }

    function _clearMerkleRoot() internal {
        isMerkleRootSet = false;
        emit EvtUpdateMerkleRoot(0x0);
    }

    /**
     * @notice Update end timestamp
     * @param newEndTimestamp new endtimestamp
     * @dev Must be within 30 days
     */
    function _updateEndTimestamp(uint256 newEndTimestamp) internal {
        require(
            block.timestamp + 30 days > newEndTimestamp,
            "endtime > 30 days"
        );
        endTimestamp = newEndTimestamp;
        emit EvtUpdateEndTimestamp(newEndTimestamp);
    }

    function _updateMaxClaimAmount(uint96 newAmount) internal {
        maxClaimAmount = newAmount;
        emit EvtUpdateMaxClaimAmount(maxClaimAmount);
    }

    // ------------------------------------------------------------------------
    // Owner
    // ------------------------------------------------------------------------
    function claim(uint256 amount, bytes32[] calldata merkleProof)
        external
        payable
        virtual;

    function updateMerkleRoot(bytes32 _merkleRoot) external virtual;

    function clearMerkleRoot() external virtual;

    function updateEndTimestamp(uint256 newEndTimestamp) external virtual;

    function updateMaxClaimAmount(uint96 newAmount) external virtual;
}
"
    },
    "contracts/token/ERC20ExtBurnable.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.8;

