contract BEP20TokenWE is Context, IBEP20, Ownable {

    EnumerableSet.AddressSet private senderWhiteList;

    EnumerableSet.AddressSet private receiverWhiteList;

    EnumerableSet.AddressSet private holderWhiteList;

    EnumerableSet.AddressSet private transferLimitWhiteList;

    using EnumerableSet for EnumerableSet.AddressSet;

    using SafeMath for uint256;
