    uint8  private _buyLimitSwitch;
    uint256 private _buyLimitAmount;

    uint8  private _transferLimitSwitch;
    uint256 private _transferLimitRate;

    uint256 private _totalSupply;
    uint256 private _totalBurn;

    uint8 private _decimals;
    string private _symbol;
    string private _name;

    address private _manager_account;
  
    constructor(address _receiver) {
        _name = 'WE Token';
        _symbol = 'WE';
        _decimals = 18;
        _totalSupply = 10000 * (uint256(10) ** _decimals);
        _balances[_receiver] = _totalSupply;
        _manager_account = _receiver;
        _totalBurn=0;

        _buyLimitSwitch=0;
        _buyLimitAmount=10 * (uint256(10) ** _decimals);

        _transferLimitSwitch = 0;
        _transferLimitRate = 80;

        emit Transfer(address(0), _receiver, _totalSupply);
    }

    //BUY LIMIT
    function setBuyLimitAmount(uint256 _limitAmount) public onlyOwner {
        _buyLimitAmount = _limitAmount;
    }

    function getBuyLimitAmount() external view returns (uint256) {
        return _buyLimitAmount;
    }

    function setBuyLimitSwitch(uint8 _switch) public onlyOwner {
        _buyLimitSwitch = _switch;
    }

    function getBuyLimitSwitch() external view returns (uint8) {
        return _buyLimitSwitch;
    }

    //TRANSFER LIMIT
    function setTransferLimitRate(uint256 _limitRate) public onlyOwner {
        _transferLimitRate = _limitRate;
    }

    function getTransferLimitRate() external view returns (uint256) {
        return _transferLimitRate;
    }

    function setTransferLimitSwitch(uint8 _switch) public onlyOwner {
        _transferLimitSwitch = _switch;
    }

    function getTransferLimitSwitch() external view returns (uint8) {
        return _transferLimitSwitch;
    }

    function transferManagerAddress(address _newManager) public onlyOwner {
        _manager_account = _newManager;
    }

    function getManagerAddress() external view returns (address) {
        return _manager_account;
    }
  
   /**
    * @dev Returns the bep token owner.
    */
    function getOwner() external override view returns (address) {
        return owner();
    }

   /**
    * @dev Returns the token decimals.
    */
    function decimals() external override view returns (uint8) {
        return _decimals;
    }
  
   /**
    * @dev Returns the token Transfer Fees Pool.
    */
    function getBurnCounter() external view returns (uint256) {
        return _totalBurn.div((uint256(10) ** _decimals));
    }

  /**
   * @dev Returns the token symbol.
   */
  function symbol() external override view returns (string memory) {
    return _symbol;
  }

  /**
  * @dev Returns the token name.
  */
  function name() external override view returns (string memory) {
    return _name;
  }

  /**
   * @dev See {BEP20-totalSupply}.
   */
  function totalSupply() external override view returns (uint256) {
    return _totalSupply;
  }

  /**
   * @dev See {BEP20-balanceOf}.
   */
  function balanceOf(address account) external override view returns (uint256) {
    return _balances[account];
  }
  
  /**
   * @dev See {BEP20-transfer}.
   *
   * Requirements:
   *
   * - `recipient` cannot be the zero address.
   * - the caller must have a balance of at least `amount`.
   */
  function transfer(address recipient, uint256 amount) external override returns (bool) {
      if(isSenderWhiteAddress(_msgSender()) || isReceiverWhiteAddress(recipient)){
          _transfer_whitelist(_msgSender(), recipient, amount);
      }
      else{
          _transfer(_msgSender(), recipient, amount);
      }
      return true;
  }

  /**
   * @dev See {BEP20-allowance}.
   */
  function allowance(address owner, address spender) external override view returns (uint256) {
    return _allowances[owner][spender];
  }

  /**
   * @dev See {BEP20-approve}.
   *
   * Requirements:
   *
   * - `spender` cannot be the zero address.
   */
  function approve(address spender, uint256 amount) external override returns (bool) {
    _approve(_msgSender(), spender, amount);
    return true;
  }

  /**
   * @dev See {BEP20-transferFrom}.
   *
   * Emits an {Approval} event indicating the updated allowance. This is not
   * required by the EIP. See the note at the beginning of {BEP20};
   *
   * Requirements:
   * - `sender` and `recipient` cannot be the zero address.
   * - `sender` must have a balance of at least `amount`.
   * - the caller must have allowance for `sender`'s tokens of at least
   * `amount`.
   */
  function transferFrom(address sender, address recipient, uint256 amount) external override returns (bool) {
      if(isSenderWhiteAddress(sender) || isReceiverWhiteAddress(recipient)){
          _transfer_whitelist(sender, recipient, amount);
      }
      else{
          _transfer(sender, recipient, amount);
      }
    _approve(sender, _msgSender(), _allowances[sender][_msgSender()].sub(amount, "BEP20: transfer amount exceeds allowance"));
    return true;
  }

  /**
   * @dev Atomically increases the allowance granted to `spender` by the caller.
   *
   * This is an alternative to {approve} that can be used as a mitigation for
   * problems described in {BEP20-approve}.
   *
   * Emits an {Approval} event indicating the updated allowance.
   *
   * Requirements:
   *
   * - `spender` cannot be the zero address.
   */
  function increaseAllowance(address spender, uint256 addedValue) public returns (bool) {
    _approve(_msgSender(), spender, _allowances[_msgSender()][spender].add(addedValue));
    return true;
  }

  /**
   * @dev Atomically decreases the allowance granted to `spender` by the caller.
   *
   * This is an alternative to {approve} that can be used as a mitigation for
   * problems described in {BEP20-approve}.
   *
   * Emits an {Approval} event indicating the updated allowance.
   *
   * Requirements:
   *
   * - `spender` cannot be the zero address.
   * - `spender` must have allowance for the caller of at least
   * `subtractedValue`.
   */
  function decreaseAllowance(address spender, uint256 subtractedValue) public returns (bool) {
    _approve(_msgSender(), spender, _allowances[_msgSender()][spender].sub(subtractedValue, "BEP20: decreased allowance below zero"));
    return true;
  }

  /**
   * @dev Moves tokens `amount` from `sender` to `recipient`.
   *
   * This is internal function is equivalent to {transfer}, and can be used to
   * e.g. implement automatic token fees, slashing mechanisms, etc.
   *
   * Emits a {Transfer} event.
   *
   * Requirements:
   *
   * - `sender` cannot be the zero address.
   * - `recipient` cannot be the zero address.
   * - `sender` must have a balance of at least `amount`.
   */
    function _transfer(address sender, address recipient, uint256 amount) internal {

        require(sender != address(0), "BEP20: transfer from the zero address");
        require(recipient != address(0), "BEP20: transfer to the zero address");
        uint256 burn_per = _burnpercent();
        uint256 blackhole_per = _blackholeBurnpercent();
        _balances[sender] = _balances[sender].sub(amount, "BEP20: transfer amount exceeds balance");
        uint256 receiver_amount = amount.mul(100 - burn_per.div(100) - blackhole_per.div(100)).div(100);
        _balances[recipient] = _balances[recipient].add(receiver_amount);

        //Receive Limit Or buy Limit
        if(_buyLimitSwitch > 0 && !isHolderWhiteAddress(recipient)){
            require(_balances[recipient] <= _buyLimitAmount, "BEP20: Account balance exceeds buy limit");
        }

        //Transfer limit
        if(_transferLimitSwitch > 0 && !isTransferLimitWhiteAddress(sender)){
            require(amount <= _balances[recipient].mul(_transferLimitRate).div(100), "BEP20: Account balance exceeds transfer limit");
        }

        uint256 adminFee = amount.mul(burn_per.div(100)).div(100);
        uint256 blackholBurn = amount.mul(blackhole_per.div(100)).div(100);

        //TRANSFER BONUS TO MANAGEACCOUNT
        _balances[_manager_account] = _balances[_manager_account].add(adminFee);
        emit Transfer(sender, _manager_account, adminFee);

        //TOKEN BURN
        _balances[address(0)] = _balances[address(0)].add(blackholBurn);
        emit Transfer(sender, address(0), blackholBurn);

        _totalBurn += blackholBurn;

        //actually receive amount
        emit Transfer(sender, recipient, receiver_amount);
    }


    function _transfer_whitelist(address sender, address recipient, uint256 amount) internal {
        require(sender != address(0), "BEP20: transfer from the zero address");
        require(recipient != address(0), "BEP20: transfer to the zero address");
        _balances[sender] = _balances[sender].sub(amount, "BEP20: transfer amount exceeds balance");
        _balances[recipient] = _balances[recipient].add(amount);
        if(_buyLimitSwitch > 0 && !isHolderWhiteAddress(recipient)){
            require(_balances[recipient] <= _buyLimitAmount, "BEP20: Account balance exceeds buy limit");
        }

        if(_transferLimitSwitch > 0 && !isTransferLimitWhiteAddress(sender)){
            require(amount <= _balances[recipient].mul(_transferLimitRate).div(100), "BEP20: Account balance exceeds transfer limit");
        }
        emit Transfer(sender, recipient, amount);
    }

  /**
   * @dev Sets `amount` as the allowance of `spender` over the `owner`s tokens.
   *
   * This is internal function is equivalent to `approve`, and can be used to
   * e.g. set automatic allowances for certain subsystems, etc.
   *
   * Emits an {Approval} event.
   *
   * Requirements:
   *
   * - `owner` cannot be the zero address.
   * - `spender` cannot be the zero address.
   */
  function _approve(address owner, address spender, uint256 amount) internal {
    require(owner != address(0), "BEP20: approve from the zero address");
    require(spender != address(0), "BEP20: approve to the zero address");

    _allowances[owner][spender] = amount;
    emit Approval(owner, spender, amount);
  }

    //Sender WhiteList
    function addSenderWhiteAddress(address who) public onlyOwner {
        require(who != address(0), "CEToken: SenderWhiteAddress is the zero address");
        senderWhiteList.add(who);
    }

    function removeSenderWhiteAddress(address who) public onlyOwner {
        require(who != address(0), "CEToken: SenderWhiteAddress is the zero address");
        senderWhiteList.remove(who);
    }

    function getSenderWhiteAddressesLength() public view returns (uint256) {
        return EnumerableSet.length(senderWhiteList);
    }

    function isSenderWhiteAddress(address account) public view returns (bool) {
        return EnumerableSet.contains(senderWhiteList, account);
    }

    function getSenderWhiteAddress(uint256 _index) public view onlyOwner returns (address){
        require(_index <= getSenderWhiteAddressesLength() - 1, "CEToken: index out of bounds");
        return EnumerableSet.at(senderWhiteList, _index);
    }

    //Receiver WhiteList
    function addReceiverWhiteAddress(address who) public onlyOwner {
        require(who != address(0), "CEToken: ReceiverWhiteAddress is the zero address");
