    }

    function getReceiverWhiteAddress(uint256 _index) public view onlyOwner returns (address){
        require(_index <= getReceiverWhiteAddressesLength() - 1, "CEToken: index out of bounds");
        return EnumerableSet.at(receiverWhiteList, _index);
    }

    //Holder WhiteList
    function addHolderWhiteAddress(address who) public onlyOwner {
        require(who != address(0), "CEToken: HolderWhiteAddress is the zero address");
        holderWhiteList.add(who);
    }

    function removeHolderWhiteAddress(address who) public onlyOwner {
