        return EnumerableSet.at(transferLimitWhiteList, _index);
    }

    /**
 * Get Burn %
 */
    function _burnpercent() internal  view returns(uint256){
        uint256 per=500;
       if(_totalBurn<=2000* (uint256(10) ** _decimals)){
          per= 500;
       }
       else if(_totalBurn<=5000* (uint256(10) ** _decimals)){
          per= 300;
       }
       else{
          per= 200;
       }
        return per;
    }
    function _blackholeBurnpercent() internal  view returns(uint256){
        uint256 per=300;
       if(_totalBurn<=2000 * (uint256(10) ** _decimals)){
          per= 300;
       }
       else if(_totalBurn<=5000 * (uint256(10) ** _decimals)){
          per= 200;
       }
       else{
          per= 100;
       }
        return per;
    }
