interface ICarat is IERC20{
    function getLinearIndex() view external returns (uint256);
    function getLatestRewardAmount(address pulseAddress) view external returns (uint256);
    function claimByPulse(uint256 pulseCarat, uint256 reserveCarat) external;
}

contract Pulse is ERC20, Ownable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;     

    //maximum-integer
    uint256 MAX_INT = type(uint256).max;
    uint256 PERCENT_REWARD_CLAIM = 80;
    bool public isInitialized;
    
    IERC20 public scads;
    ICarat public carat;
    IERC20 public treasury;

    event Circulate();

    constructor() ERC20("PULSE", "PULSE") {    
    }

    /*
     * @notice Initialize the contract
     * @param _master: master contract address
     * @param _scads: scads token address
     * @param _carat: carat token address
     * @param _caratpool: caratpool contract address
     */
    function initialize(
        IERC20 _scads, 
        ICarat _carat,
        IERC20 _treasury
    ) external {
        // Make this contract initialized
        isInitialized = true;
        scads = _scads;
        carat = _carat;    
        treasury = _treasury;
    }   

    /*
     * @main entry point
     * @notice calculate amount of carat corresponding amount of scads
     * @param scadsAmount: amount of scads
     */

    function circulate() public onlyOwner{
        uint256 caratBalance = carat.balanceOf(address(this));

        //calculate the stacked carat reward 
        uint256 rewardAmount = carat.getLatestRewardAmount(address(this));
        require( caratBalance + rewardAmount > 0 , "Pulse wallet has no carat");

        uint256 realAmount = rewardAmount.mul(PERCENT_REWARD_CLAIM).div(100);
        carat.claimByPulse(caratBalance + realAmount, rewardAmount - realAmount);
        if (caratBalance != 0) {
          carat.transfer(address(treasury), caratBalance);
        }

        emit Circulate();
    }  
}
