    "contracts/gateway/VestingGateway.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.23;

import {Initializable} from '@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol';
import {UUPSUpgradeable} from '@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol';
import {PausableUpgradeable} from '@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol';
import {ReentrancyGuardUpgradeable} from '@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol';
import {ERC165Checker} from '@openzeppelin/contracts/utils/introspection/ERC165Checker.sol';
import {IERC20Metadata} from '@openzeppelin/contracts/interfaces/IERC20Metadata.sol';
import {SafeERC20} from '@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol';

import {ProxyAdminManagerUpgradeable} from '../util/ProxyAdminManagerUpgradeable.sol';
import {NonblockingLzAppUpgradeable} from '../util/NonblockingLzAppUpgradeable.sol';
import {IVestingGateway} from '../interface/IVestingGateway.sol';

import {IFactory} from '../interface/IFactory.sol';
import {IFixedVestingV3} from '../interface/IFixedVestingV3.sol';
import {ILinearVestingV3} from '../interface/ILinearVestingV3.sol';

contract VestingGateway is
  Initializable,
  UUPSUpgradeable,
  PausableUpgradeable,
  ProxyAdminManagerUpgradeable,
  ReentrancyGuardUpgradeable,
  NonblockingLzAppUpgradeable,
  IVestingGateway
{
  using ERC165Checker for address;
  using SafeERC20 for IERC20Metadata;
  address public constant override native = 0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE;

  uint256 public override gasForDestinationLzReceive;
  uint256 public override crossFee_d2;
  DstChain[] internal allDstChains; // all supported chains
  mapping(uint240 => uint256) internal dstChainIndex;

  FactoryList public override factory;

  error Misslength(uint256 left, uint256 right);
  error InvalidSender(address who);

  event AssetWithdrawn(address target, address tokenAddress, uint256 amount);

  function init(
    address _lzEndpoint,
    uint256 _gasForDestinationLzReceive,
    uint256 _crossFee_d2
  ) external proxied initializer {
    address deployer = _msgSender();

    __UUPSUpgradeable_init();
    __ReentrancyGuard_init();
    __Pausable_init();
    __ProxyAdminManager_init(deployer);
    __NonblockingLzApp_init(deployer, _lzEndpoint);

    gasForDestinationLzReceive = _gasForDestinationLzReceive;
    crossFee_d2 = _crossFee_d2;
  }

  function _authorizeUpgrade(address newImplementation) internal virtual override proxied {}

  function _convertTo18Decimal(uint256 _amount, uint8 _srcDecimals) internal pure returns (uint256) {
    return (_amount * 1e18) / (10 ** _srcDecimals);
  }

  function _adapterParams() internal view virtual returns (bytes memory) {
    uint16 version = 1;
    return abi.encodePacked(version, gasForDestinationLzReceive);
  }

  function _encodePayload(address _vesting, address _user, uint256 _amount) internal view returns (bytes memory) {
    return abi.encode(uint240(block.chainid), _vesting, _user, _amount);
  }

  function allDstChainsSupportedLength() public view virtual override returns (uint256) {
    return allDstChains.length;
  }

  function isChainIDSupported(uint240 _chainID) public view virtual override returns (bool) {
    if (allDstChainsSupportedLength() == 0) return false;

    return allDstChains[dstChainIndex[_chainID]].chainID == _chainID;
  }

  function crossChainData(uint240 _chainID) external view virtual override returns (DstChain memory data) {
    if (!isChainIDSupported(_chainID)) return data;
    return allDstChains[dstChainIndex[_chainID]];
  }

  function estimateFees(
    uint240 _dstChainID,
    address _vesting,
    address _user,
    address _stable,
    uint256 _amount
  ) public view virtual override returns (uint256 fees, uint256 tax) {
    if (!isChainIDSupported(_dstChainID)) return (0, 0);

    // casting token amount to 18 decimal
    _amount = _convertTo18Decimal(_amount, IERC20Metadata(_stable).decimals());

    (fees, ) = lzEndpoint().estimateFees(
      allDstChains[dstChainIndex[_dstChainID]].lzChainID,
      address(this),
      _encodePayload(_vesting, _user, _amount),
      false,
      _adapterParams()
    );

    tax = (crossFee_d2 * fees) / 1e4;
  }

  function _nonblockingLzReceive(
    uint16, // _srcChainId
    bytes memory, // _srcAddress
    uint64, // _nonce
    bytes memory // _payload
  ) internal virtual override {
    revert();
  }

  function releaseToken(
    uint240 _dstChainID,
    address _user,
    address _stable,
    uint256 _amount
  ) external payable virtual override whenNotPaused nonReentrant {
    address sender = _msgSender();

    IFactory factorySc;
    if (sender.supportsInterface(type(IFixedVestingV3).interfaceId)) {
      require(factory.fixedFactory != address(0), '!fixedFactory');
      factorySc = IFactory(factory.fixedFactory);
    } else if (sender.supportsInterface(type(ILinearVestingV3).interfaceId)) {
      require(factory.linearFactory != address(0), '!linearFactory');
      factorySc = IFactory(factory.linearFactory);
    } else {
      revert InvalidSender(sender);
    }

    if (!factorySc.isKnown(sender)) revert InvalidSender(sender);

    (uint256 feeNeeded, uint256 tax) = estimateFees(_dstChainID, sender, _user, _stable, _amount);
    require(msg.value >= feeNeeded + tax, '!crossFee');

    // casting token amount to 18 decimal
    _amount = _convertTo18Decimal(_amount, IERC20Metadata(_stable).decimals());

    // send LayerZero message
    _lzSend(
      allDstChains[dstChainIndex[_dstChainID]].lzChainID,
      _encodePayload(sender, _user, _amount),
      payable(address(this)),
      address(0x0),
      _adapterParams(),
      feeNeeded
    );
  }

  function assetWithdraw(
    address payable _target,
    address _tokenAddress,
    uint256 _amount
  ) external virtual override nonReentrant onlyOwner {
    uint256 balance = address(this).balance;
    if (_tokenAddress == native) {
      if (balance < _amount) _amount = balance;
      (bool success, ) = _target.call{value: _amount}('');
      require(success, '!eth');
    } else {
      balance = IERC20Metadata(_tokenAddress).balanceOf(address(this));
      if (balance < _amount) _amount = balance;
      IERC20Metadata(_tokenAddress).safeTransfer(_target, _amount);
    }

    emit AssetWithdrawn(_target, _tokenAddress, _amount);
  }

  function setRemote(
    uint240[] calldata _chainIDs,
    uint16[] calldata _lzChainIDs,
    address[] calldata _refundTreasuries
  ) external virtual override onlyOwner {
    uint256 chainIDLength = _chainIDs.length;
    uint256 lzChainIDLength = _lzChainIDs.length;
    uint256 refundTreasuriesLength = _refundTreasuries.length;

    if (chainIDLength != lzChainIDLength) revert Misslength(chainIDLength, lzChainIDLength);
    if (chainIDLength != refundTreasuriesLength) revert Misslength(chainIDLength, refundTreasuriesLength);

    for (uint8 i = 0; i < chainIDLength; ++i) {
      if (_chainIDs[i] == uint240(block.chainid)) continue;

      if (isChainIDSupported(_chainIDs[i])) {
        uint256 chainIndex = dstChainIndex[_chainIDs[i]];
        allDstChains[chainIndex].lzChainID = _lzChainIDs[i];
        allDstChains[chainIndex].refundTreasury = _refundTreasuries[i];
      } else {
        dstChainIndex[_chainIDs[i]] = allDstChains.length;
        allDstChains.push(
          DstChain({chainID: _chainIDs[i], lzChainID: _lzChainIDs[i], refundTreasury: _refundTreasuries[i]})
        );
      }

      this.setTrustedRemoteAddress(_lzChainIDs[i], abi.encodePacked(_refundTreasuries[i]));
    }
  }

  /**
   * @dev Set gas for destination layerZero receive
   * @param _gasForDestinationLzReceive Sale implementation address
   */
  function setGasForDestinationLzReceive(uint256 _gasForDestinationLzReceive) external virtual override onlyOwner {
    require(_gasForDestinationLzReceive > 0 && gasForDestinationLzReceive != _gasForDestinationLzReceive, 'bad');

    gasForDestinationLzReceive = _gasForDestinationLzReceive;
  }

  /**
   * @dev Set cross fee
   * @param _crossFee_d2 Cross fee in 2 decimal
   */
  function setCrossFee_d2(uint256 _crossFee_d2) external virtual override onlyOwner {
    require(crossFee_d2 != _crossFee_d2, 'bad');

    crossFee_d2 = _crossFee_d2;
  }

  function setFixedFactory(address _fixedFactory) external virtual override onlyOwner {
    require(_fixedFactory != address(0), 'bad');

    factory.fixedFactory = _fixedFactory;
  }

  function setLinearFactory(address _linearFactory) external virtual override onlyOwner {
    require(_linearFactory != address(0), 'bad');

    factory.linearFactory = _linearFactory;
  }

  function togglePause() external virtual override onlyOwner {
    if (paused()) {
      _unpause();
    } else {
      _pause();
    }
  }

  receive() external payable virtual {}

  fallback() external payable virtual {}
}
"
