    "contracts/util/LzAppUpgradeable.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import {Initializable} from '@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol';
import {Ownable2StepUpgradeable, OwnableUpgradeable} from '@openzeppelin/contracts-upgradeable/access/Ownable2StepUpgradeable.sol';
import {ILayerZeroReceiver} from '@layerzerolabs/solidity-examples/contracts/lzApp/interfaces/ILayerZeroReceiver.sol';
import {ILayerZeroUserApplicationConfig} from '@layerzerolabs/solidity-examples/contracts/lzApp/interfaces/ILayerZeroUserApplicationConfig.sol';
import {ILayerZeroEndpoint} from '@layerzerolabs/solidity-examples/contracts/lzApp/interfaces/ILayerZeroEndpoint.sol';
import {BytesLib} from '@layerzerolabs/solidity-examples/contracts/libraries/BytesLib.sol';

/*
 * a generic LzReceiver implementation
 */
abstract contract LzAppUpgradeable is
  Initializable,
  Ownable2StepUpgradeable,
  ILayerZeroReceiver,
  ILayerZeroUserApplicationConfig
{
  using BytesLib for bytes;

  modifier onlyPermit() {
    address sender = _msgSender();
    if (owner() != sender && address(this) != sender) {
      revert OwnableUnauthorizedAccount(sender);
    }
    _;
  }

  /// @custom:storage-location erc7201:layerzero.storage.LzApp
  struct LzAppStorage {
    mapping(uint16 => bytes) _trustedRemoteLookup;
    mapping(uint16 => mapping(uint16 => uint)) _minDstGasLookup;
    mapping(uint16 => uint) _payloadSizeLimitLookup;
    ILayerZeroEndpoint _lzEndpoint;
    address _precrime;
  }

  // keccak256(abi.encode(uint256(keccak256("layerzero.storage.LzApp")) - 1)) & ~bytes32(uint256(0xff))
  bytes32 private constant LzAppStorageLocation = 0x111388274dd962a0529050efb131321f60015c2ab1a99387d94540f430037b00;

  function _getLzAppStorage() private pure returns (LzAppStorage storage $) {
    assembly {
      $.slot := LzAppStorageLocation
    }
  }

  // ua can not send payload larger than this by default, but it can be changed by the ua owner
  uint public constant DEFAULT_PAYLOAD_SIZE_LIMIT = 10000;

  event SetPrecrime(address precrime_);
  event SetTrustedRemote(uint16 remoteChainId_, bytes path_);
  event SetTrustedRemoteAddress(uint16 remoteChainId_, bytes remoteAddress_);
  event SetMinDstGas(uint16 dstChainId_, uint16 type_, uint minDstGas_);

  function __LzApp_init(address owner_, address endpoint_) internal onlyInitializing {
    __Ownable_init(owner_);

    __LzApp_init_unchained(endpoint_);
  }

  function __LzApp_init_unchained(address endpoint_) internal onlyInitializing {
    LzAppStorage storage $ = _getLzAppStorage();
    $._lzEndpoint = ILayerZeroEndpoint(endpoint_);
  }

  function lzReceive(
    uint16 srcChainId,
    bytes calldata srcAddress,
    uint64 nonce,
    bytes calldata payload
  ) public virtual override {
    // lzReceive must be called by the endpoint for security
    require(_msgSender() == address(lzEndpoint()), 'LzApp: invalid endpoint caller');

    bytes memory trustedRemote = trustedRemoteLookup(srcChainId);
    // if will still block the message pathway from (srcChainId, srcAddress). should not receive message from untrusted remote.
    require(
      srcAddress.length == trustedRemote.length &&
        trustedRemote.length > 0 &&
        keccak256(srcAddress) == keccak256(trustedRemote),
      'LzApp: invalid source sending contract'
    );

    _blockingLzReceive(srcChainId, srcAddress, nonce, payload);
  }

  // abstract function - the default behaviour of LayerZero is blocking. See: NonblockingLzApp if you dont need to enforce ordered messaging
  function _blockingLzReceive(
    uint16 srcChainId,
    bytes memory srcAddress,
    uint64 nonce,
    bytes memory payload
  ) internal virtual;

  function _lzSend(
    uint16 dstChainId,
    bytes memory payload,
    address payable refundAddress,
    address zroPaymentAddress,
    bytes memory adapterParams,
    uint nativeFee
  ) internal virtual {
    bytes memory trustedRemote = trustedRemoteLookup(dstChainId);
    require(trustedRemote.length != 0, 'LzApp: destination chain is not a trusted source');
    _checkPayloadSize(dstChainId, payload.length);
    lzEndpoint().send{value: nativeFee}(
      dstChainId,
      trustedRemote,
      payload,
      refundAddress,
      zroPaymentAddress,
      adapterParams
    );
  }

  function _checkGasLimit(
    uint16 dstChainId,
    uint16 type_,
    bytes memory adapterParams,
    uint extraGas
  ) internal view virtual {
    uint providedGasLimit = _getGasLimit(adapterParams);
    uint minGasLimit = minDstGasLookup(dstChainId, type_);
    require(minGasLimit > 0, 'LzApp: minGasLimit not set');
    require(providedGasLimit >= minGasLimit + extraGas, 'LzApp: gas limit is too low');
  }

  function _getGasLimit(bytes memory adapterParams) internal pure virtual returns (uint gasLimit) {
    require(adapterParams.length >= 34, 'LzApp: invalid adapterParams');
    assembly {
      gasLimit := mload(add(adapterParams, 34))
    }
  }

  function _checkPayloadSize(uint16 dstChainId, uint payloadSize) internal view virtual {
    uint payloadSizeLimit = payloadSizeLimitLookup(dstChainId);
    if (payloadSizeLimit == 0) {
      // use default if not set
      payloadSizeLimit = DEFAULT_PAYLOAD_SIZE_LIMIT;
    }
    require(payloadSize <= payloadSizeLimit, 'LzApp: payload size is too large');
  }

  //---------------------------UserApplication config----------------------------------------
  function getConfig(uint16 version, uint16 chainId, address, uint configType) external view returns (bytes memory) {
    return lzEndpoint().getConfig(version, chainId, address(this), configType);
  }

  // generic config for LayerZero user Application
  function setConfig(
    uint16 version,
    uint16 chainId,
    uint configType,
    bytes calldata config
  ) external override onlyOwner {
    lzEndpoint().setConfig(version, chainId, configType, config);
  }

  function setSendVersion(uint16 version) external override onlyOwner {
    lzEndpoint().setSendVersion(version);
  }

  function setReceiveVersion(uint16 version) external override onlyOwner {
    lzEndpoint().setReceiveVersion(version);
  }

  function forceResumeReceive(uint16 srcChainId, bytes calldata srcAddress) external override onlyOwner {
    lzEndpoint().forceResumeReceive(srcChainId, srcAddress);
  }

  // _path = abi.encodePacked(remoteAddress, localAddress)
  // this function set the trusted path for the cross-chain communication
  function setTrustedRemote(uint16 remoteChainId, bytes calldata path) external onlyOwner {
    LzAppStorage storage $ = _getLzAppStorage();
    $._trustedRemoteLookup[remoteChainId] = path;
    emit SetTrustedRemote(remoteChainId, path);
  }

  function setTrustedRemoteAddress(uint16 remoteChainId, bytes calldata remoteAddress) external onlyPermit {
    LzAppStorage storage $ = _getLzAppStorage();
    $._trustedRemoteLookup[remoteChainId] = abi.encodePacked(remoteAddress, address(this));
    emit SetTrustedRemoteAddress(remoteChainId, remoteAddress);
  }

  function getTrustedRemoteAddress(uint16 remoteChainId) external view returns (bytes memory) {
    bytes memory path = trustedRemoteLookup(remoteChainId);
    require(path.length != 0, 'LzApp: no trusted path record');
    return path.slice(0, path.length - 20); // the last 20 bytes should be address(this)
  }

  function setPrecrime(address precrime_) external onlyOwner {
    LzAppStorage storage $ = _getLzAppStorage();
    $._precrime = precrime_;
    emit SetPrecrime(precrime_);
  }

  function setMinDstGas(uint16 dstChainId, uint16 packetType, uint minGas) external onlyOwner {
    LzAppStorage storage $ = _getLzAppStorage();
    $._minDstGasLookup[dstChainId][packetType] = minGas;
    emit SetMinDstGas(dstChainId, packetType, minGas);
  }

  // if the size is 0, it means default size limit
  function setPayloadSizeLimit(uint16 dstChainId, uint size) external onlyOwner {
    LzAppStorage storage $ = _getLzAppStorage();
    $._payloadSizeLimitLookup[dstChainId] = size;
  }

  //--------------------------- VIEW FUNCTION ----------------------------------------
  function isTrustedRemote(uint16 srcChainId, bytes calldata srcAddress) external view returns (bool) {
    bytes memory trustedSource = trustedRemoteLookup(srcChainId);
    return keccak256(trustedSource) == keccak256(srcAddress);
  }

  function lzEndpoint() public view virtual returns (ILayerZeroEndpoint) {
    LzAppStorage storage $ = _getLzAppStorage();
    return $._lzEndpoint;
  }

  function trustedRemoteLookup(uint16 chainId) public view virtual returns (bytes memory) {
    LzAppStorage storage $ = _getLzAppStorage();
    return $._trustedRemoteLookup[chainId];
  }

  function minDstGasLookup(uint16 dstChainId, uint16 packetType) public view virtual returns (uint) {
    LzAppStorage storage $ = _getLzAppStorage();
    return $._minDstGasLookup[dstChainId][packetType];
  }

  function payloadSizeLimitLookup(uint16 chainId) public view virtual returns (uint) {
    LzAppStorage storage $ = _getLzAppStorage();
    return $._payloadSizeLimitLookup[chainId];
  }

  function precrime() public view virtual returns (address) {
    LzAppStorage storage $ = _getLzAppStorage();
    return $._precrime;
  }
}
"
    },
    "contracts/util/NonblockingLzAppUpgradeable.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import {Initializable} from '@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol';
import {LzAppUpgradeable} from './LzAppUpgradeable.sol';
import {ExcessivelySafeCall} from '@layerzerolabs/solidity-examples/contracts/libraries/ExcessivelySafeCall.sol';

/*
 * the default LayerZero messaging behaviour is blocking, i.e. any failed message will block the channel
 * this abstract class try-catch all fail messages and store locally for future retry. hence, non-blocking
 * NOTE: if the srcAddress is not configured properly, it will still block the message pathway from (srcChainId, srcAddress)
 */
abstract contract NonblockingLzAppUpgradeable is Initializable, LzAppUpgradeable {
  using ExcessivelySafeCall for address;

  /// @custom:storage-location erc7201:layerzero.storage.NonblockingLzApp
  struct NonblockingLzAppStorage {
    mapping(uint16 => mapping(bytes => mapping(uint64 => bytes32))) _failedMessages;
  }

  // keccak256(abi.encode(uint256(keccak256("layerzero.storage.NonblockingLzApp")) - 1)) & ~bytes32(uint256(0xff))
  bytes32 private constant NonblockingLzAppStorageLocation =
    0xe5a86fa43fa85f564c84895bd4f80ec5c29d03a57a0c1f7cb91d2cc05b4d8600;

  function _getNonblockingLzAppStorage() private pure returns (NonblockingLzAppStorage storage $) {
    assembly {
      $.slot := NonblockingLzAppStorageLocation
    }
  }

  event MessageFailed(uint16 srcChainId_, bytes srcAddress_, uint64 nonce_, bytes payload_, bytes reason_);
  event RetryMessageSuccess(uint16 srcChainId_, bytes srcAddress_, uint64 nonce_, bytes32 payloadHash_);

  function __NonblockingLzApp_init(address owner_, address endpoint_) internal onlyInitializing {
    __LzApp_init(owner_, endpoint_);

    __NonblockingLzApp_init_unchained(endpoint_);
  }

  function __NonblockingLzApp_init_unchained(address endpoint_) internal onlyInitializing {}

  // overriding the virtual function in LzReceiver
  function _blockingLzReceive(
    uint16 srcChainId,
    bytes memory srcAddress,
    uint64 nonce,
    bytes memory payload
  ) internal virtual override {
    (bool success, bytes memory reason) = address(this).excessivelySafeCall(
      gasleft(),
      150,
      abi.encodeWithSelector(this.nonblockingLzReceive.selector, srcChainId, srcAddress, nonce, payload)
    );
    // try-catch all errors/exceptions
    if (!success) {
      _storeFailedMessage(srcChainId, srcAddress, nonce, payload, reason);
    }
  }

  function _storeFailedMessage(
    uint16 srcChainId,
    bytes memory srcAddress,
    uint64 nonce,
    bytes memory payload,
    bytes memory reason
  ) internal virtual {
    NonblockingLzAppStorage storage $ = _getNonblockingLzAppStorage();
    $._failedMessages[srcChainId][srcAddress][nonce] = keccak256(payload);
    emit MessageFailed(srcChainId, srcAddress, nonce, payload, reason);
  }

  function nonblockingLzReceive(
    uint16 srcChainId,
    bytes calldata srcAddress,
    uint64 nonce,
    bytes calldata payload
  ) public virtual {
    // only internal transaction
    require(_msgSender() == address(this), 'NonblockingLzApp: caller must be LzApp');
    _nonblockingLzReceive(srcChainId, srcAddress, nonce, payload);
  }

  //@notice override this function
  function _nonblockingLzReceive(
    uint16 srcChainId,
    bytes memory srcAddress,
    uint64 nonce,
    bytes memory payload
  ) internal virtual;

  function retryMessage(
    uint16 srcChainId,
    bytes calldata srcAddress,
    uint64 nonce,
    bytes calldata payload
  ) public payable virtual {
    // assert there is message to retry
    bytes32 payloadHash = failedMessages(srcChainId, srcAddress, nonce);
    require(payloadHash != bytes32(0), 'NonblockingLzApp: no stored message');
    require(keccak256(payload) == payloadHash, 'NonblockingLzApp: invalid payload');
    // clear the stored message
    NonblockingLzAppStorage storage $ = _getNonblockingLzAppStorage();
    $._failedMessages[srcChainId][srcAddress][nonce] = bytes32(0);
    // execute the message. revert if it fails again
    _nonblockingLzReceive(srcChainId, srcAddress, nonce, payload);
    emit RetryMessageSuccess(srcChainId, srcAddress, nonce, payloadHash);
  }

  function failedMessages(
    uint16 chainId,
    bytes calldata srcAddress,
    uint64 nonce
  ) public view virtual returns (bytes32) {
    NonblockingLzAppStorage storage $ = _getNonblockingLzAppStorage();
    return $._failedMessages[chainId][srcAddress][nonce];
  }
}
"
    },
    "contracts/util/ProxyAdminManagerUpgradeable.sol": {
      "content": "//SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import {ContextUpgradeable} from '@openzeppelin/contracts-upgradeable/utils/ContextUpgradeable.sol';
import {Initializable} from '@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol';
import {Proxied} from 'hardhat-deploy/solc_0.8/proxy/Proxied.sol';

contract ProxyAdminManagerUpgradeable is Initializable, ContextUpgradeable, Proxied {
  /// @custom:storage-location erc7201:kommunitas.storage.ProxyAdminManager
  struct ProxyAdminManagerStorage {
    address _pendingProxyAdmin;
  }

  // keccak256(abi.encode(uint256(keccak256("kommunitas.storage.ProxyAdminManager")) - 1)) & ~bytes32(uint256(0xff))
  bytes32 private constant ProxyAdminManagerStorageLocation =
    0x8a59f7a64c66470bf640c97c76846f17f243529240a7e0230806f27e52406200;

  function _getProxyAdminManagerStorage() private pure returns (ProxyAdminManagerStorage storage $) {
    assembly {
      $.slot := ProxyAdminManagerStorageLocation
    }
  }

  error PendingProxyAdminUnauthorizedAccount(address account);

  error ProxyAdminInvalidAccount(address account);

  event ProxyAdminTransferStarted(address indexed previousProxyAdmin, address indexed newProxyAdmin);

  event ProxyAdminTransferred(address indexed previousProxyAdmin, address indexed newProxyAdmin);

  function __ProxyAdminManager_init(address _initialProxyAdmin) internal onlyInitializing {
    __ProxyAdminManager_init_unchained(_initialProxyAdmin);
  }

  function __ProxyAdminManager_init_unchained(address _initialProxyAdmin) internal onlyInitializing {
    if (_initialProxyAdmin == address(0)) {
      revert ProxyAdminInvalidAccount(address(0));
    }
    assembly {
      sstore(0xb53127684a568b3173ae13b9f8a6016e243e63b6e8ee1178d6a717850b5d6103, _initialProxyAdmin)
    }
  }

  function proxyAdmin() external view virtual returns (address) {
    return _proxyAdmin();
  }

  function pendingProxyAdmin() public view virtual returns (address) {
    ProxyAdminManagerStorage storage $ = _getProxyAdminManagerStorage();
    return $._pendingProxyAdmin;
  }

  function transferProxyAdmin(address _newProxyAdmin) external virtual proxied {
    ProxyAdminManagerStorage storage $ = _getProxyAdminManagerStorage();
    $._pendingProxyAdmin = _newProxyAdmin;
    emit ProxyAdminTransferStarted(_proxyAdmin(), _newProxyAdmin);
  }

  function _transferProxyAdmin(address _newProxyAdmin) internal virtual {
    ProxyAdminManagerStorage storage $ = _getProxyAdminManagerStorage();
    delete $._pendingProxyAdmin;
    address oldProxyAdmin = _proxyAdmin();
    assembly {
