    function _transfer(
        address from,
        address to,
        uint256 amount
    ) private {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        require(amount > 0, "Transfer amount must be greater than zero");

        uint256 senderBalance = _balances[from];
        require(
            senderBalance >= amount,
            "ERC20: transfer amount exceeds balance"
        );
        require(!isTransferBlacklist(from), "don't transfer from this address");
        require(!isTransferBlacklist(to), "don't transfer to this address");
        _balances[from] = senderBalance-amount;

        //buy or sell
        uint256 toAmount = amount;
        
        if (isPair[to] ) {
            bool isAddLiquidity = _isAddLiquidity(amount);
            if(!isAddLiquidity){
                if(!isPairWhitelist(from)&&!isPairWhitelist(to)){
                    require(isOpenSale,"not open sale");
                }
                if (!_isExcludedFromFee[from] && !_isExcludedFromFee[to]) {
                    uint256 saleHolderAmount = 0;
                    if (saleHolderRate > 0) {
                        saleHolderAmount = amount*saleHolderRate/10**4;
                        _balances[saleHolderAddress] = _balances[saleHolderAddress]+saleHolderAmount;
                        emit Transfer(from, saleHolderAddress, saleHolderAmount);
                        toAmount = toAmount-saleHolderAmount;
                        emit transactionFee(from,to,false,saleHolderAmount);
                    }
                    uint256 companyAmount = 0;
                    if (companyRate > 0) {
                        companyAmount = amount*companyRate/10**4;
                        _balances[companyAddress] = _balances[companyAddress]+companyAmount;
                        emit Transfer(from, companyAddress, companyAmount);
                        toAmount = toAmount-companyAmount;
                    }
                }
            }
        }else if(isPair[from]){
            bool isDelLiquidity = _isRemoveLiquidity();
            if(!isDelLiquidity){
                if(!isPairWhitelist(from)&&!isPairWhitelist(to)){
                    require(isOpenBuy,"not open buy");
                }
                if (!_isExcludedFromFee[from] && !_isExcludedFromFee[to]) {
                    uint256 buyHolderAmount = 0;
                    if (buyHolderRate > 0) {
                        buyHolderAmount = amount*buyHolderRate/10**4;
                        _balances[buyHolderAddress] = _balances[buyHolderAddress]+buyHolderAmount;
                        emit Transfer(from, buyHolderAddress, buyHolderAmount);
                        toAmount = toAmount-buyHolderAmount;
                        emit transactionFee(from,to,true,buyHolderAmount);
                    }
                }
            }
        }
        

        _balances[to] = _balances[to]+toAmount;
        emit Transfer(from, to, toAmount);
    }
    function _isAddLiquidity(uint256 amount) internal view returns (bool isAdd){
        (uint256 rOther, uint256 rThis, uint256 balanceOther) = _getReserves();
        uint256 amountOther;
        if (rOther > 0 && rThis > 0) {
            amountOther = amount * rOther / rThis;
        }
        //isAddLP
        if (balanceOther >= rOther + amountOther) {
            isAdd = true;
        }
    }

    
    function _getReserves() public view returns (uint256 rOther, uint256 rThis, uint256 balanceOther){
        IUniswapV2Pair mainPair = IUniswapV2Pair(pairAddress);
        (uint r0, uint256 r1,) = mainPair.getReserves();

        address tokenOther = usdtAddress;
        if (tokenOther < address(this)) {
            rOther = r0;
            rThis = r1;
        } else {
            rOther = r1;
            rThis = r0;
        }

        balanceOther = IERC20(tokenOther).balanceOf(pairAddress);
    }

    function _isRemoveLiquidity() internal view returns (bool isRemove){
        IUniswapV2Pair mainPair = IUniswapV2Pair(pairAddress);
        (uint r0,uint256 r1,) = mainPair.getReserves();

        address tokenOther = usdtAddress;
        uint256 r;
        if (tokenOther < address(this)) {
            r = r0;
        } else {
            r = r1;
        }

        uint bal = IERC20(tokenOther).balanceOf(pairAddress);
        isRemove = r >= bal;
    }
