    "contracts/LpManager.sol": {
      "content": "//SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./utils/IWETH.sol";
import "./utils/IBabyDogePair.sol";
import "./utils/IBabyDogeRouter.sol";

contract LpManager {
    struct LpData {
        address lpTokenAddress;
        address[] unwrapPath0;      // [] for WBNB, [token0, ..., WBNB] for anything else
        address[] unwrapPath1;      // [] for WBNB, [token0, ..., WBNB] for anything else
    }

    IBabyDogeRouter public immutable router;
    address internal immutable WETH;
    uint64 public minWbnbPerLp;         // minimum amount of WBNB should be received from unwrapping LP token
    uint32 public gasRefund = 300_000;  // amount of gas to refund

    event NewMinWbnbPerLp(uint256);
    event NewGasRefund(uint256);
    event ERC20Recover(address account, uint256 amount);
    event BnbRecover(address account, uint256 amount);

    error InvalidPath(uint256 lpTokenIndex);    // one of unwrap paths for LP token is invalid
    error ZeroLiquidity(address lpTokenAddress);

    /*
     * @param _router BabyDoge router address
     * @param _minWbnbPerLp Minimum amount of WBNB should be received from unwrapping LP token
     */
    constructor(
        IBabyDogeRouter _router,
        uint64 _minWbnbPerLp
    ) {
        minWbnbPerLp = _minWbnbPerLp;
        router = _router;
        address _WETH = _router.WETH();
        WETH = _WETH;
        IERC20(_WETH).approve(address(_router), type(uint256).max);
    }

    receive() external payable {}

    /*
     * @notice Swaps LP tokens to WBNB
     * @param lpData LP data: LP token address and paths to unwrap
     * @return gasUsedPerLp Amount of gas user for unwrapping lp token
     * @return amountOut Amount of WBNB contract has after unwrapping all lp tokens and paying for gas
     * @return gasCost Amount of WBNB that were paid for gas
     */
    function _swapToWbnb(
        LpData[] calldata lpData
    ) internal returns(
        uint256[] memory gasUsedPerLp,
        uint256 amountOut,
        uint256 gasCost
    ) {
        uint256 startGas = gasleft();
        uint256 initialWBNB = IERC20(WETH).balanceOf(address(this));
        gasUsedPerLp = new uint256[](lpData.length);

        // remove liquidity
        for (uint i = 0; i < lpData.length; i++) {
            (address tokenA, address tokenB, uint256 gas) = _removeLiquidity(lpData[i].lpTokenAddress);
            gasUsedPerLp[i] = gas;
            _checkPaths(lpData[i].unwrapPath0, lpData[i].unwrapPath1, tokenA, tokenB, i);
        }

        // swap tokens
        for (uint i = 0; i < lpData.length; i++) {
            uint256 gasBeforeSwap = gasleft();
            if(lpData[i].unwrapPath0.length > 0) {
                _swap(lpData[i].unwrapPath0);
            }
            if(lpData[i].unwrapPath1.length > 0) {
                _swap(lpData[i].unwrapPath1);
            }
            gasUsedPerLp[i] += (gasBeforeSwap - gasleft());
        }

        // Estimating gas spendings
        // 10_000 gas will be refunded for "freed" storage as each LP token balances is now 0
        // `gasRefund` gas for other contract calculations
        gasCost = (startGas - gasleft() - lpData.length * 10_000 + gasRefund) * tx.gasprice;
        uint256 wbnbBalance = IERC20(WETH).balanceOf(address(this));
        amountOut = wbnbBalance - gasCost;
        require(
            wbnbBalance - initialWBNB > gasCost
            && amountOut - initialWBNB > minWbnbPerLp * lpData.length,
            "Not enough WBNB received"
        );

        // Return gas spendings
        IWETH(WETH).withdraw(gasCost);
        (bool success,) = payable(msg.sender).call{value: gasCost}("");
        require(success, "Couldn't pay for gas");

        return (gasUsedPerLp, amountOut, gasCost);
    }


    /*
     * @notice Sets minimum WBNB to be received per LP token unwrap
     * @param _minWbnbPerLp Minimum WBNB to be received per LP token unwrap
     */
    function _setMinWbnbPerLp(uint64 _minWbnbPerLp) internal {
        require(minWbnbPerLp != _minWbnbPerLp, "Already set");
        minWbnbPerLp = _minWbnbPerLp;
        emit NewMinWbnbPerLp(_minWbnbPerLp);
    }


    /*
     * @notice Sets additional amount of gas to refund per unwrap
     * @param _gasRefund Additional amount of gas to refund per unwrap
     * @dev To use in case of increased gas cost
     */
    function _setGasRefund(uint32 _gasRefund) internal {
        require(_gasRefund != 0);
        require(gasRefund != _gasRefund, "Already set");
        gasRefund = _gasRefund;
        emit NewGasRefund(_gasRefund);
    }


    /*
     * @notice Removes liquidity from the pool
     * @param lpToken LP token address
     * @return tokenA Token0 of lpToken
     * @return tokenB Token1 of lpToken
     * @return gasUsed Amount of gas user for removing liquidity
     */
    function _removeLiquidity(address lpToken) private returns(
        address tokenA,
        address tokenB,
        uint256 gasUsed
    ) {
        gasUsed = gasleft();

        uint256 liquidity = IBabyDogePair(lpToken).balanceOf(address(this));
        if(liquidity == 0) {
            revert ZeroLiquidity(lpToken);
        }

        if(IERC20(lpToken).allowance(address(this), address(router)) < liquidity) {
            IERC20(lpToken).approve(address(router), type(uint256).max);
        }
        tokenA = IBabyDogePair(lpToken).token0();
        tokenB = IBabyDogePair(lpToken).token1();

        router.removeLiquidity(
            tokenA,
            tokenB,
            liquidity,
            0,
            0,
            address(this),
            block.timestamp + 1200
        );

        gasUsed -= gasleft();
    }


    /*
     * @notice Checks unwrap paths
     * @param unwrapPath0 Unwrap path for token0
     * @param unwrapPath1 Unwrap path for token1
     * @param tokenA Real token0 of the pair
     * @param tokenB Real token1 of the pair
     * @param index Index for reverts information
     */
    function _checkPaths(
        address[] calldata unwrapPath0,
        address[] calldata unwrapPath1,
        address tokenA,
        address tokenB,
        uint256 index
    ) private view {
        if (unwrapPath0.length == 0 && unwrapPath1.length == 0) {
            revert InvalidPath(index);
        }

        address token0 = unwrapPath0.length == 0 ? WETH : unwrapPath0[0];
        address token1 = unwrapPath1.length == 0 ? WETH : unwrapPath1[0];

        // check for incorrect first token in unwrap path
        if (!((token0 == tokenA && token1 == tokenB) || (token1 == tokenA && token0 == tokenB))) {
            revert InvalidPath(index);
        }

        // check for incorrect last token in unwrap path
        // last token must be WBNB
        if (
            (unwrapPath0.length > 0
            && unwrapPath0[unwrapPath0.length - 1] != WETH)
            ||
            (unwrapPath1.length > 0
            && unwrapPath1[unwrapPath1.length - 1] != WETH)
        ) {
            revert InvalidPath(index);
        }
    }


    /*
     * @notice Swaps tokens
     * @param path Swap path
     */
    function _swap(address[] calldata path) private {
        uint256 amountIn = IERC20(path[0]).balanceOf(address(this));

        if(amountIn == 0) {
            return;
        }

        if(IERC20(path[0]).allowance(address(this), address(router)) < amountIn) {
            IERC20(path[0]).approve(address(router), type(uint256).max);
        }

        router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            amountIn,
            0,
            path,
            address(this),
            block.timestamp + 1200
        );
    }

    /*
     * @notice Recovers ERC20 tokens
     * @param token ERC20 token address
     * @param account Receiver of tokens
     * @param amount Amount of tokens to recover
     */
    function _recoverERC20(address token, address account, uint256 amount) internal {
        IERC20(token).transfer(account, amount);

        emit ERC20Recover(account, amount);
    }

    /*
     * @notice Recovers BNB
     * @param account Receiver of BNB
     * @param amount Amount of BNB to recover
     */
    function _recoverBNB(address account, uint256 amount) internal {
        (bool success,) = payable(account).call{value: amount}("");
        require(success, "BNB recover failed");

        emit BnbRecover(account, amount);
    }
}
"
    },
    "contracts/TreasuryFeeManager.sol": {
      "content": "//SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/AccessControlEnumerable.sol";
import "./LpManager.sol";
import "./utils/IBabyDogeRouter.sol";

contract TreasuryFeeManager is AccessControlEnumerable, LpManager{
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant MANAGER_ROLE = keccak256("MANAGER_ROLE");
    
    address public stablecoin;
    address public treasuryAddress;

    event Unwrapped(
        address unwrapper,
        uint256 transferredToTreasury,
        uint256 gasCost
    );

    event NewStablecoin(address);
    event NewTreasury(address);

    /*
     * @param _router BabyDoge router address
     * @param _minWbnbPerLp Minimum amount of WBNB should be received from unwrapping LP token
     * @param _stablecoin Address of stablecoin that will be bought after unwrapping LP tokens
     * @param _treasuryAddress Address of treasury that will receive stablecoins
     */
    constructor(
        IBabyDogeRouter _router,
        uint64 _minWbnbPerLp,
        address _stablecoin,
        address _treasuryAddress
    ) LpManager(_router, _minWbnbPerLp){
        _setupRole(ADMIN_ROLE, msg.sender);
        _setRoleAdmin(MANAGER_ROLE, ADMIN_ROLE);
        _setRoleAdmin(ADMIN_ROLE, ADMIN_ROLE);

        stablecoin = _stablecoin;
        treasuryAddress = _treasuryAddress;
    }


    /*
     * @notice Swaps LP tokens to WBNB and then to stablecoin which will go to treasury
     * @param lpData LP data: LP token address and paths to unwrap
     * @return amountBnbMin Minimum amount of BNB to receive for unwrapping LP tokens
     * @return amountStablesMin Minimum amount of stablecoins to get for WBNB
     * @return gasUsedPerLp Amount of gas user for unwrapping lp token
     * @return amountOut Amount of stablecoins received after unwrapping all lp tokens
     * @return gasCost Amount of WBNB that were paid for gas
     */
    function unwrapTokens(
        LpData[] calldata lpData,
        uint256 amountStablesMin
    )
        external
        onlyRole(MANAGER_ROLE)
        returns(
            uint256[] memory gasUsedPerLp,
            uint256 amountOut,
            uint256 gasCost
        )
    {
        uint256 amountWbnbReceived = 0;
        (gasUsedPerLp, amountWbnbReceived, gasCost) = _swapToWbnb(lpData);

        // swap to stablecoins
        address[] memory path = new address[](2);
        path[0] = WETH;
        path[1] = stablecoin;
        uint256[] memory amounts = router.swapExactTokensForTokens(
            amountWbnbReceived,
            amountStablesMin,
            path,
            treasuryAddress,
            block.timestamp + 1200
        );
        amountOut = amounts[1];

        emit Unwrapped(msg.sender, amountOut, gasCost);
    }


    /*
     * @notice Sets different stablecoin address
     * @param _stablecoin New stablecoin address
     */
    function setStableCoin(address _stablecoin)
        external
        onlyRole(ADMIN_ROLE)
    {
        require(stablecoin != _stablecoin, "Already set");
        require(_stablecoin != address(0));
        stablecoin = _stablecoin;
        emit NewStablecoin(_stablecoin);
    }


    /*
     * @notice Sets different treasury address
     * @param _treasuryAddress New treasury address
     */
    function setTreasuryAddress(address _treasuryAddress)
        external
        onlyRole(ADMIN_ROLE)
    {
        require(treasuryAddress != _treasuryAddress, "Already set");
        require(_treasuryAddress != address(0));
        treasuryAddress = _treasuryAddress;
        emit NewTreasury(_treasuryAddress);
    }


    /*
     * @notice Sets minimum WBNB to be received per LP token unwrap
     * @param _minWbnbPerLp Minimum WBNB to be received per LP token unwrap
     */
    function setMinWbnbPerLp(uint64 _minWbnbPerLp)
        external
        onlyRole(ADMIN_ROLE)
    {
        _setMinWbnbPerLp(_minWbnbPerLp);
    }


    /*
     * @notice Sets additional amount of gas to refund per unwrap
     * @param _gasRefund Additional amount of gas to refund per unwrap
     * @dev To use in case of increased gas cost
     */
    function setGasRefund(uint32 _gasRefund)
        external
        onlyRole(ADMIN_ROLE)
    {
        _setGasRefund(_gasRefund);
    }


    /*
     * @notice Recovers ERC20 tokens
     * @param token ERC20 token address
     * @param account Receiver of tokens
     * @param amount Amount of tokens to recover
     */
    function recoverERC20(
        address token,
        address account,
        uint256 amount
    ) external onlyRole(ADMIN_ROLE) {
        _recoverERC20(token, account, amount);
    }


    /*
     * @notice Recovers BNB
     * @param account Receiver of BNB
     * @param amount Amount of BNB to recover
     */
    function recoverBNB(
        address account,
        uint256 amount
    ) external onlyRole(ADMIN_ROLE) {
        _recoverBNB(account, amount);
    }


    /*
     * @notice Returns all members of specific role
     * @param role Role hash
     * @return List of role members
     */
    function getRoleMembers(bytes32 role) external view returns (address[] memory) {
        uint256 membersCount = getRoleMemberCount(role);
        address[] memory members = new address[](membersCount);

        for (uint i = 0; i < membersCount; i++) {
            members[i] = getRoleMember(role, i);
        }

        return members;
    }
}
"
    },
    "contracts/utils/IBabyDogePair.sol": {
      "content": "//SPDX-License-Identifier: GPL-3.0-or-later

pragma solidity ^0.8.0;

interface IBabyDogePair {
    event Approval(
        address indexed owner,
        address indexed spender,
        uint256 value
    );
    event Transfer(address indexed from, address indexed to, uint256 value);

    function name() external pure returns (string memory);

    function symbol() external pure returns (string memory);

    function decimals() external pure returns (uint8);

    function totalSupply() external view returns (uint256);

    function balanceOf(address owner) external view returns (uint256);

    function allowance(address owner, address spender)
        external
        view
        returns (uint256);

    function approve(address spender, uint256 value) external returns (bool);

    function transfer(address to, uint256 value) external returns (bool);

    function transferFrom(
        address from,
        address to,
        uint256 value
    ) external returns (bool);

    function DOMAIN_SEPARATOR() external view returns (bytes32);

    function PERMIT_TYPEHASH() external pure returns (bytes32);

    function nonces(address owner) external view returns (uint256);

    function permit(
        address owner,
        address spender,
        uint256 value,
        uint256 deadline,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external;

    event Mint(address indexed sender, uint256 amount0, uint256 amount1);
    event Burn(
        address indexed sender,
        uint256 amount0,
        uint256 amount1,
        address indexed to
    );
    event Swap(
        address indexed sender,
        uint256 amount0In,
        uint256 amount1In,
        uint256 amount0Out,
        uint256 amount1Out,
        address indexed to
    );
    event Sync(uint112 reserve0, uint112 reserve1);

    function MINIMUM_LIQUIDITY() external pure returns (uint256);

    function factory() external view returns (address);

    function token0() external view returns (address);

    function token1() external view returns (address);

    function getReserves()
        external
        view
        returns (
            uint112 reserve0,
            uint112 reserve1,
            uint32 blockTimestampLast
        );

    function price0CumulativeLast() external view returns (uint256);

    function price1CumulativeLast() external view returns (uint256);

    function kLast() external view returns (uint256);

    function mint(address to) external returns (uint256 liquidity);

    function burn(address to)
        external
        returns (uint256 amount0, uint256 amount1);

    function swap(
        uint256 amount0Out,
        uint256 amount1Out,
        address to,
        bytes calldata data
    ) external;

    function skim(address to) external;

    function sync() external;

    function initialize(address, address) external;
}
"
    },
    "contracts/utils/IBabyDogeRouter.sol": {
      "content": "//SPDX-License-Identifier: MIT
pragma solidity >=0.6.2;

interface IBabyDogeRouter {
  function removeLiquidityETHSupportingFeeOnTransferTokens(
    address token,
    uint256 liquidity,
    uint256 amountTokenMin,
    uint256 amountETHMin,
    address to,
    uint256 deadline
  ) external returns (uint256 amountETH);

  function removeLiquidityETHWithPermitSupportingFeeOnTransferTokens(
    address token,
    uint256 liquidity,
    uint256 amountTokenMin,
    uint256 amountETHMin,
    address to,
    uint256 deadline,
    bool approveMax,
    uint8 v,
    bytes32 r,
    bytes32 s
  ) external returns (uint256 amountETH);

  function swapExactTokensForTokensSupportingFeeOnTransferTokens(
    uint256 amountIn,
    uint256 amountOutMin,
    address[] calldata path,
    address to,
    uint256 deadline
  ) external;

  function swapExactETHForTokensSupportingFeeOnTransferTokens(
    uint256 amountOutMin,
    address[] calldata path,
    address to,
    uint256 deadline
  ) external payable;

  function swapExactTokensForETHSupportingFeeOnTransferTokens(
    uint256 amountIn,
    uint256 amountOutMin,
    address[] calldata path,
    address to,
    uint256 deadline
  ) external;

  function transactionFee(address _tokenIn, address _tokenOut, address _msgSender)
    external
    view
    returns (uint256);
  function factory() external pure returns (address);

  function WETH() external pure returns (address);

  function addLiquidity(
    address tokenA,
    address tokenB,
    uint256 amountADesired,
    uint256 amountBDesired,
    uint256 amountAMin,
    uint256 amountBMin,
    address to,
    uint256 deadline
  )
  external
  returns (
    uint256 amountA,
    uint256 amountB,
    uint256 liquidity
  );

  function addLiquidityETH(
    address token,
    uint256 amountTokenDesired,
    uint256 amountTokenMin,
    uint256 amountETHMin,
    address to,
    uint256 deadline
  )
  external
  payable
  returns (
    uint256 amountToken,
    uint256 amountETH,
    uint256 liquidity
  );

  function removeLiquidity(
    address tokenA,
    address tokenB,
    uint256 liquidity,
    uint256 amountAMin,
    uint256 amountBMin,
    address to,
    uint256 deadline
  ) external returns (uint256 amountA, uint256 amountB);

  function removeLiquidityETH(
    address token,
    uint256 liquidity,
    uint256 amountTokenMin,
    uint256 amountETHMin,
    address to,
    uint256 deadline
  ) external returns (uint256 amountToken, uint256 amountETH);

  function removeLiquidityWithPermit(
    address tokenA,
    address tokenB,
    uint256 liquidity,
    uint256 amountAMin,
    uint256 amountBMin,
    address to,
    uint256 deadline,
    bool approveMax,
    uint8 v,
    bytes32 r,
    bytes32 s
  ) external returns (uint256 amountA, uint256 amountB);

  function removeLiquidityETHWithPermit(
    address token,
    uint256 liquidity,
    uint256 amountTokenMin,
    uint256 amountETHMin,
    address to,
    uint256 deadline,
    bool approveMax,
    uint8 v,
    bytes32 r,
    bytes32 s
  ) external returns (uint256 amountToken, uint256 amountETH);

  function swapExactTokensForTokens(
    uint256 amountIn,
    uint256 amountOutMin,
    address[] calldata path,
    address to,
    uint256 deadline
  ) external returns (uint256[] memory amounts);

  function swapTokensForExactTokens(
    uint256 amountOut,
    uint256 amountInMax,
    address[] calldata path,
    address to,
    uint256 deadline
  ) external returns (uint256[] memory amounts);

  function swapExactETHForTokens(
    uint256 amountOutMin,
    address[] calldata path,
    address to,
    uint256 deadline
  ) external payable returns (uint256[] memory amounts);

  function swapTokensForExactETH(
    uint256 amountOut,
    uint256 amountInMax,
    address[] calldata path,
    address to,
    uint256 deadline
  ) external returns (uint256[] memory amounts);

  function swapExactTokensForETH(
    uint256 amountIn,
    uint256 amountOutMin,
    address[] calldata path,
    address to,
    uint256 deadline
  ) external returns (uint256[] memory amounts);

  function swapETHForExactTokens(
    uint256 amountOut,
    address[] calldata path,
    address to,
    uint256 deadline
  ) external payable returns (uint256[] memory amounts);

  function quote(
    uint256 amountA,
    uint256 reserveA,
    uint256 reserveB
  ) external pure returns (uint256 amountB);

  function getAmountsOut(uint256 amountIn, address[] calldata path)
  external
  view
  returns (uint256[] memory amounts);

  function getAmountsIn(uint256 amountOut, address[] calldata path)
  external
  view
  returns (uint256[] memory amounts);
}
"
