// pragma solidity >=0.5.0;

// helper methods for interacting with BEP20 tokens and sending ETH that do not consistently return true/false
library TransferHelper {
    function safeApprove(
        address token,
        address to,
        uint256 value
    ) internal {
        // bytes4(keccak256(bytes('approve(address,uint256)')));
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(0x095ea7b3, to, value));
        require(success && (data.length == 0 || abi.decode(data, (bool))), 'TransferHelper: APPROVE_FAILED');
    }

    function safeTransfer(
        address token,
        address to,
        uint256 value
    ) internal {
        // bytes4(keccak256(bytes('transfer(address,uint256)')));
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(0xa9059cbb, to, value));
        require(success && (data.length == 0 || abi.decode(data, (bool))), 'TransferHelper: TRANSFER_FAILED');
    }

    function safeTransferFrom(
        address token,
        address from,
        address to,
        uint256 value
    ) internal {
        // bytes4(keccak256(bytes('transferFrom(address,address,uint256)')));
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(0x23b872dd, from, to, value));
        require(success && (data.length == 0 || abi.decode(data, (bool))), 'TransferHelper: TRANSFER_FROM_FAILED');
    }
}

contract PTSMinePool is ReentrancyGuard {
    using TransferHelper for address;
    using SafeMath for uint256;

    address _owner;

    string constant _name = 'PTSMinePool';
    string constant _symbol = 'PTSMinePool';
    address private immutable _minePool = 0x8f4B62029BBDE8C15eC816251585284E14751Be4;
    address private immutable _stackPool = 0x381C3871187dC3e74f5658295eeA0E40D767168d;

    address private _token;

    constructor(address token) {
        _owner = msg.sender;
        _token = token;
    }

    modifier onlyOwner() {
        require(_owner == msg.sender, 'Ownable: caller is not the owner');
        _;
    }

    function name() public pure returns (string memory) {
        return _name;
    }

    function symbol() public pure returns (string memory) {
        return _symbol;
    }

    function setToken(address token) public onlyOwner {
        require(msg.sender == _owner);
        _token = token;
    }

    function getToken() public view onlyOwner returns (address) {
        require(msg.sender == _owner);
        return _token;
    }

    function stack(
        address tokena,
        uint256 amounta,
        address tokenb,
        uint256 amountb
    ) public payable nonReentrant returns (bool) {
        address user = msg.sender;
        require(tokena != address(0), 'ERC20: deposit from the zero address');
        require(tokena != _minePool && tokena != _stackPool, 'ERROR: address A');
        require(tokenb != _minePool && tokenb != _stackPool, 'ERROR: address B');
        require(amounta > 0, 'ERC20: Transfer amount must be greater than zero');

        address recipient1 = _minePool;
        if (tokena != _token) {
            recipient1 = _stackPool;
        }

        tokena.safeTransferFrom(user, recipient1, amounta);
        if (amountb > 0) {
            require(tokenb != address(0), 'ERC20: deposit from the zero address');
            require(amountb > 0, 'ERC20: Transfer amount must be greater than zero');

            address recipient2 = _minePool;
            if (tokenb != _token) {
                recipient2 = _stackPool;
            }

            tokenb.safeTransferFrom(user, recipient2, amountb);
        }
        return true;
    }

    function takeback(
        address tokena,
        uint256 amounta,
        address tokenb,
        uint256 amountb
    ) public nonReentrant returns (bool) {
        require(tokena != address(0), 'ERC20: deposit from the zero address');
        require(tokena != _minePool, 'ERROR: address A');
        require(tokenb != _minePool, 'ERROR: address B');
        require(amounta > 0, 'ERC20: Transfer amount must be greater than zero');

        if (amountb > 0) {
            require(tokenb != address(0), 'ERC20: deposit from the zero address');
            require(amountb > 0, 'ERC20: Transfer amount must be greater than zero');
        }
        return true;
    }
}
